// frontend/src/components/equipment/Equipment.js
import React, { useState, useEffect } from 'react';
import api from '../../utils/api';

const Equipment = () => {
  const [equipment, setEquipment] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchEquipment = async () => {
      try {
        setLoading(true);
        const res = await api.get('/equipment');
        setEquipment(res.data.equipment || []);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch equipment');
      } finally {
        setLoading(false);
      }
    };

    fetchEquipment();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-teal-600 border-r-transparent"></div>
          <p className="mt-4 text-gray-600">Loading equipment...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="bg-red-100 border border-red-400 text-red-800 px-4 py-3 rounded max-w-md text-center">
          <p className="font-bold">Error</p>
          <p>{error}</p>
        </div>
      </div>
    );
  }

  if (!equipment || equipment.length === 0) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="bg-yellow-100 border border-yellow-400 text-yellow-800 px-4 py-3 rounded max-w-md text-center">
          <p>No equipment found</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto px-6 py-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">Equipment Inventory</h1>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {equipment.map(equip => (
            <div key={equip._id} className="bg-white shadow p-6 rounded-lg">
              <h2 className="text-lg font-semibold">{equip.name}</h2>
              <p className="text-sm text-gray-600">{equip.serialNumber}</p>
              <p className="text-xs text-gray-500">
                Model: {equip.model} | Department: {equip.department}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Equipment;
