// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';

// Import components
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import Dashboard from './components/dashboard/Dashboard';
import Equipment from './components/equipment/Equipment';
import Maintenance from './components/maintenance/Maintenance';
import Tasks from './components/tasks/Tasks';
import Users from './components/users/Users';
import Navbar from './components/layout/Navbar';
import Sidebar from './components/layout/Sidebar';
import PrivateRoute from './components/routing/PrivateRoute';

// Set base URL for axios
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
axios.defaults.withCredentials = true;

// Add request interceptor for debugging
axios.interceptors.request.use(request => {
  console.log('Request URL:', request.url);
  
  // Ensure Authorization header is set with token from localStorage
  const token = localStorage.getItem('token');
  if (token) {
    request.headers['Authorization'] = `Bearer ${token}`;
  }
  
  return request;
});

// Add response interceptor for debugging
axios.interceptors.response.use(response => {
  console.log('Response:', response);
  return response;
}, error => {
  console.error('Axios Error:', error);
  return Promise.reject(error);
});

function App() {
  const [user, setUser] = useState(null);
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [loading, setLoading] = useState(true);

  // Check for token on load
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // Verify token and get user data
      verifyToken(token);
    } else {
      setLoading(false);
    }
  }, []);

  const verifyToken = async (token) => {
    try {
      const res = await axios.get('/auth/me', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setUser(res.data.user);
    } catch (err) {
      localStorage.removeItem('token');
      setUser(null);
    } finally {
      setLoading(false);
    }
  };

  const login = (userData) => {
    console.log('Login function called with:', userData);
    setUser(userData.user);
    localStorage.setItem('token', userData.token);
    
    // Set Authorization header for future requests
    axios.defaults.headers.common['Authorization'] = `Bearer ${userData.token}`;
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="text-center">
          <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-teal-600 border-r-transparent"></div>
          <p className="mt-4 text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="flex h-screen bg-gray-50">
        {user && <Sidebar currentPage={currentPage} />}
        
        <div className={user ? "flex-1 flex flex-col overflow-hidden" : "w-full"}>
          {user && <Navbar user={user} logout={logout} />}
          
          <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50">
            <div className="container mx-auto px-6 py-8">
              <Routes>
                {/* Public routes */}
                <Route 
                  path="/login" 
                  element={
                    !user ? <Login login={login} /> : <Navigate to="/dashboard" replace />
                  } 
                />
                <Route 
                  path="/register" 
                  element={
                    !user ? <Register /> : <Navigate to="/dashboard" replace />
                  } 
                />
                
                {/* Default route */}
                <Route 
                  path="/" 
                  element={
                    user ? <Navigate to="/dashboard" replace /> : <Navigate to="/login" replace />
                  } 
                />
                
                {/* Protected routes */}
                <Route 
                  path="/dashboard" 
                  element={
                    <PrivateRoute user={user}>
                      <Dashboard user={user} setCurrentPage={setCurrentPage} />
                    </PrivateRoute>
                  } 
                />
                <Route 
                  path="/equipment" 
                  element={
                    <PrivateRoute user={user}>
                      <Equipment user={user} setCurrentPage={setCurrentPage} />
                    </PrivateRoute>
                  } 
                />
                <Route 
                  path="/maintenance" 
                  element={
                    <PrivateRoute user={user}>
                      <Maintenance user={user} setCurrentPage={setCurrentPage} />
                    </PrivateRoute>
                  } 
                />
                <Route 
                  path="/tasks" 
                  element={
                    <PrivateRoute user={user}>
                      <Tasks user={user} setCurrentPage={setCurrentPage} />
                    </PrivateRoute>
                  } 
                />
                <Route 
                  path="/users" 
                  element={
                    <PrivateRoute user={user} role="Engineer">
                      <Users user={user} setCurrentPage={setCurrentPage} />
                    </PrivateRoute>
                  } 
                />
                
                {/* Catch all unmatched routes */}
                <Route 
                  path="*" 
                  element={<Navigate to={user ? "/dashboard" : "/login"} replace />} 
                />
              </Routes>
            </div>
          </main>
        </div>
      </div>
    </Router>
  );
}

export default App;
