// frontend/src/components/equipment/AddEquipment.js
import React, { useState } from 'react';
import axios from 'axios';

const AddEquipment = ({ user }) => {
  const [formData, setFormData] = useState({
    name: '',
    brand: '',
    model: '',
    serialNumber: '',
    department: 'Engineering',
    status: 'Active'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const { name, brand, model, serialNumber, department, status } = formData;

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const res = await axios.post('/equipment', formData);
      console.log('Equipment added:', res.data);
      alert('Equipment added successfully!');
      window.location.href = '/equipment';
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to add equipment');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Add Equipment</h1>
      <form onSubmit={onSubmit}>
        <input
          type="text"
          name="name"
          value={name}
          onChange={onChange}
          placeholder="Name"
          required
        />
        <input
          type="text"
          name="brand"
          value={brand}
          onChange={onChange}
          placeholder="Brand"
          required
        />
        <input
          type="text"
          name="model"
          value={model}
          onChange={onChange}
          placeholder="Model"
          required
        />
        <input
          type="text"
          name="serialNumber"
          value={serialNumber}
          onChange={onChange}
          placeholder="Serial Number"
          required
        />
        <select name="department" value={department} onChange={onChange}>
          <option value="Engineering">Engineering</option>
          <option value="Maintenance">Maintenance</option>
          <option value="Facilities">Facilities</option>
        </select>
        <select name="status" value={status} onChange={onChange}>
          <option value="Active">Active</option>
          <option value="Inactive">Inactive</option>
        </select>
        <button type="submit">{loading ? 'Adding...' : 'Add Equipment'}</button>
      </form>
      {error && <p>{error}</p>}
    </div>
  );
};

export default AddEquipment;
