// frontend/src/components/equipment/AddEquipment.js
import React, { useState } from 'react';
import api from '../../utils/api';

const AddEquipment = ({ onEquipmentAdded }) => {
  const [formData, setFormData] = useState({
    name: '',
    status: 'Serviceable',
    manufacturer: '',
    model: '',
    serialNumber: '',
    department: '',
    location: '',
    installationDate: '',
    warrantyExpiry: '',
    category: ''
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();

    // Check required fields
    const requiredFields = [
      'name',
      'status',
      'manufacturer',
      'model',
      'serialNumber',
      'department',
      'location',
      'installationDate',
      'category'
    ];
    const missing = requiredFields.filter((f) => !formData[f]);
    if (missing.length > 0) {
      setError('Please fill in all required fields');
      setSuccess('');
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const response = await api.post('/equipment', formData);

      if (response.status === 201 || response.status === 200) {
        setSuccess(response.data?.message || 'Equipment added successfully');
        onEquipmentAdded?.(response.data);
        setFormData({
          name: '',
          status: 'Serviceable',
          manufacturer: '',
          model: '',
          serialNumber: '',
          department: '',
          location: '',
          installationDate: '',
          warrantyExpiry: '',
          category: ''
        });
      } else {
        setError('Unexpected response from server.');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to add equipment');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white rounded-2xl shadow-lg border border-slate-200 p-6">
      <h2 className="text-2xl font-bold text-slate-800 mb-6">
        Add New Equipment
      </h2>

      {error && (
        <div className="mb-4 p-3 bg-red-100 border border-red-200 text-red-700 rounded-lg">
          {error}
        </div>
      )}

      {success && (
        <div className="mb-4 p-3 bg-green-100 border border-green-200 text-green-700 rounded-lg">
          {success}
        </div>
      )}

      <form onSubmit={onSubmit}>
        {/* form fields remain the same as yours */}
        {/* ... */}

        <div className="mt-8 flex justify-end">
          <button
            type="submit"
            disabled={loading}
            className="px-8 py-3 bg-gradient-to-r from-blue-600 to-green-500 text-white font-medium rounded-xl hover:from-blue-700 hover:to-green-600 transition-all duration-200 transform hover:scale-105 disabled:opacity-70"
          >
            {loading ? (
              <div className="flex items-center">
                <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                Adding Equipment...
              </div>
            ) : (
              'Add Equipment'
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddEquipment;
