// frontend/src/components/maintenance/Maintenance.js
import React, { useEffect, useMemo, useState } from 'react';
import io from 'socket.io-client';
import api from '../../utils/api';

const socket = io('http://localhost:5000', { transports: ['websocket'] });

const Maintenance = ({ user }) => {
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState('');

  const loadHistory = async () => {
    try {
      setErr('');
      setLoading(true);
      // Get only completed tasks to display as maintenance history
      const res = await api.get('/tasks?includePreventive=true');
      const all = Array.isArray(res.data.tasks) ? res.data.tasks : [];
      const completed = all.filter(t => t.status === 'Completed'); // engineer certification logic can further filter if you stored flags
      setHistory(completed);
    } catch (e) {
      setErr(e.response?.data?.message || 'Failed to load maintenance history');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { loadHistory(); }, []);

  useEffect(() => {
    const refetch = () => loadHistory();
    socket.on('task:updated', refetch);
    socket.on('task:workUpdated', refetch);
    socket.on('task:created', refetch);
    socket.on('task:deleted', refetch);
    return () => {
      socket.off('task:updated', refetch);
      socket.off('task:workUpdated', refetch);
      socket.off('task:created', refetch);
      socket.off('task:deleted', refetch);
    };
  }, []);

  if (loading) {
    return (
      <div className="min-h-[40vh] bg-white rounded-2xl border border-slate-200 shadow p-8">
        <div className="flex items-center justify-center">
          <div className="inline-block h-10 w-10 animate-spin rounded-full border-4 border-solid border-blue-600 border-r-transparent"></div>
        </div>
      </div>
    );
  }

  if (err) {
    return (
      <div className="min-h-[40vh] bg-white rounded-2xl border border-slate-200 shadow p-8">
        <div className="text-red-600">{err}</div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-2xl border border-slate-200 shadow p-6">
      <h2 className="text-xl font-semibold text-slate-800 mb-4">Maintenance History</h2>

      {history.length === 0 ? (
        <div className="text-slate-500">No maintenance history yet.</div>
      ) : (
        <div className="space-y-4">
          {history.map(t => (
            <div key={t._id} className="border border-slate-200 rounded-xl p-4">
              <div className="flex justify-between items-start">
                <div>
                  <div className="text-slate-800 font-semibold">{t.title}</div>
                  <div className="text-slate-500 text-sm mt-0.5">
                    Equipment: <span className="font-medium">{t.equipment?.name || 'N/A'}</span>
                    {t.assignedTo?.name && <> • Technician: <span className="font-medium">{t.assignedTo.name}</span></>}
                  </div>
                </div>
                <div className="text-xs px-2 py-1 rounded bg-green-100 border border-green-200 text-green-800">Completed</div>
              </div>

              {/* Details from Add Work */}
              {(t.faultDescription || t.comments || (t.spareParts && t.spareParts.length)) && (
                <div className="mt-3 grid gap-3 md:grid-cols-3">
                  {t.faultDescription && (
                    <div className="bg-slate-50 rounded-lg p-3 border border-slate-200">
                      <div className="text-xs uppercase tracking-wide text-slate-500">Fault Description</div>
                      <div className="text-sm text-slate-800 mt-1">{t.faultDescription}</div>
                    </div>
                  )}
                  {t.comments && (
                    <div className="bg-slate-50 rounded-lg p-3 border border-slate-200">
                      <div className="text-xs uppercase tracking-wide text-slate-500">Repair Details</div>
                      <div className="text-sm text-slate-800 mt-1 whitespace-pre-wrap">{t.comments}</div>
                    </div>
                  )}
                  {t.spareParts && t.spareParts.length > 0 && (
                    <div className="bg-slate-50 rounded-lg p-3 border border-slate-200">
                      <div className="text-xs uppercase tracking-wide text-slate-500">Spare Parts</div>
                      <ul className="text-sm text-slate-800 mt-1 list-disc list-inside space-y-1">
                        {t.spareParts.map((p, i) => (
                          <li key={i}>
                            <span className="font-medium">{p.name}</span>
                            {typeof p.quantity === 'number' && <> × {p.quantity}</>}
                            {p.description && <> — <span className="text-slate-600">{p.description}</span></>}
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              )}

              <div className="mt-3 text-xs text-slate-500">
                {t.completedDate ? new Date(t.completedDate).toLocaleString() : new Date(t.updatedAt || t.createdAt).toLocaleString()}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Maintenance;
