// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import { AuthProvider } from './context/AuthContext';

// Import components
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import Dashboard from './components/dashboard/Dashboard';
import Equipment from './components/equipment/Equipment';
import Maintenance from './components/maintenance/Maintenance';
import SchedulePreventive from './components/maintenance/SchedulePreventive';
import ReportIssue from './components/maintenance/ReportIssue';
import Tasks from './components/tasks/Tasks';
import Users from './components/users/Users';
import AddEquipment from './components/equipment/AddEquipment';
import AddTask from './components/tasks/AddTask';
import AddUser from './components/users/AddUser';
import Navbar from './components/layout/Navbar';
import Sidebar from './components/layout/Sidebar';
import PrivateRoute from './components/routing/PrivateRoute';

// NEW: Reports page
import ReportsPage from './components/reports/ReportsPage';

// Set base URL for axios
axios.defaults.baseURL = 'http://localhost:5000/api';
axios.defaults.withCredentials = true;

// Add request interceptor for debugging
axios.interceptors.request.use(
  request => {
    console.log('Request URL:', request.url);
    const token = localStorage.getItem('token');
    if (token) {
      request.headers['Authorization'] = `Bearer ${token}`;
    }
    return request;
  },
  error => Promise.reject(error)
);

// Add response interceptor for debugging
axios.interceptors.response.use(
  response => {
    console.log('Response:', response);
    return response;
  },
  error => {
    console.error('Axios Error:', error);
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

function App() {
  const [user, setUser] = useState(null);
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [loading, setLoading] = useState(true);

  // NEW: mobile sidebar state
  const [sidebarOpen, setSidebarOpen] = useState(false);

  useEffect(() => {
    const initializeAuth = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          const res = await axios.get('/auth/me', {
            headers: { 'Authorization': `Bearer ${token}` }
          });
          if (res.data && res.data.user) {
            setUser(res.data.user);
            localStorage.setItem('user', JSON.stringify(res.data.user));
          } else {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
          }
        } catch (err) {
          console.error('Auth verification error:', err);
          localStorage.removeItem('token');
          localStorage.removeItem('user');
        }
      }
      setLoading(false);
    };
    initializeAuth();
  }, []);

  const login = (userData) => {
    if (userData?.token && userData?.user) {
      localStorage.setItem('token', userData.token);
      localStorage.setItem('user', JSON.stringify(userData.user));
      setUser(userData.user);
      axios.defaults.headers.common['Authorization'] = `Bearer ${userData.token}`;
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    delete axios.defaults.headers.common['Authorization'];
    window.location.href = '/login';
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="text-center">
          <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-teal-600 border-r-transparent"></div>
          <p className="mt-4 text-gray-600">Loading application...</p>
        </div>
      </div>
    );
  }

  return (
    <AuthProvider>
      <Router>
        {/* Use min-h-screen so mobile browsers with dynamic address bars don’t clip content */}
        <div className="flex min-h-screen bg-gray-50">
          {/* ===== Responsive Sidebar ===== */}
          {user && (
            <>
              {/* Mobile overlay + slide-in drawer */}
              <div
                className={`fixed inset-0 z-40 bg-black/40 transition-opacity lg:hidden ${sidebarOpen ? 'opacity-100' : 'pointer-events-none opacity-0'}`}
                onClick={() => setSidebarOpen(false)}
                aria-hidden={!sidebarOpen}
              />
              <div
                className={`fixed inset-y-0 left-0 z-50 w-72 transform bg-white shadow-xl transition-transform lg:static lg:z-auto lg:w-72 lg:translate-x-0 ${
                  sidebarOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'
                }`}
                role="dialog"
                aria-modal="true"
              >
                <Sidebar currentPage={currentPage} />
              </div>
            </>
          )}

          {/* ===== Main Column ===== */}
          <div className={user ? "flex-1 flex flex-col min-w-0" : "w-full"}>
            {/* Small-screen top bar with hamburger (shows only when logged in) */}
            {user && (
              <div className="lg:hidden sticky top-0 z-30 flex items-center gap-3 bg-white/90 backdrop-blur border-b border-slate-200 px-4 py-3">
                <button
                  type="button"
                  onClick={() => setSidebarOpen(s => !s)}
                  aria-label="Open sidebar"
                  className="inline-flex items-center justify-center rounded-md p-2 text-slate-700 hover:bg-slate-100 focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  {/* Hamburger icon */}
                  <svg className="h-6 w-6" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
                  </svg>
                </button>
                <div className="text-sm font-semibold text-slate-700">Servix CMMS</div>
              </div>
            )}

            {/* Existing Navbar (desktop + can also show on mobile below the small bar) */}
            {user && <Navbar user={user} logout={logout} />}

            {/* Make the main content scroll independently (good for mobile) */}
            <main className="flex-1 min-h-0 overflow-x-hidden overflow-y-auto bg-gray-50">
              {/* Use responsive padding so it’s comfortable on phones/tablets */}
              <div className="mx-auto w-full max-w-7xl px-4 sm:px-6 lg:px-8 py-6">
                <Routes>
                  {/* Public routes */}
                  <Route path="/login" element={!user ? <Login login={login} /> : <Navigate to="/dashboard" replace />} />
                  <Route path="/register" element={!user ? <Register login={login} /> : <Navigate to="/dashboard" replace />} />

                  {/* Default route */}
                  <Route path="/" element={user ? <Navigate to="/dashboard" replace /> : <Navigate to="/login" replace />} />

                  {/* Protected routes */}
                  <Route
                    path="/dashboard"
                    element={
                      <PrivateRoute user={user}>
                        <Dashboard user={user} setCurrentPage={setCurrentPage} />
                      </PrivateRoute>
                    }
                  />
                  <Route
                    path="/equipment"
                    element={
                      <PrivateRoute user={user}>
                        <Equipment user={user} setCurrentPage={setCurrentPage} />
                      </PrivateRoute>
                    }
                  />
                  <Route
                    path="/add-equipment"
                    element={
                      <PrivateRoute user={user}>
                        <AddEquipment user={user} setCurrentPage={setCurrentPage} />
                      </PrivateRoute>
                    }
                  />
                  <Route
                    path="/maintenance"
                    element={
                      <PrivateRoute user={user}>
                        <Maintenance user={user} setCurrentPage={setCurrentPage} />
                      </PrivateRoute>
                    }
                  />
                  <Route
                    path="/schedule-preventive"
                    element={
                      <PrivateRoute user={user} role="Engineer">
                        <SchedulePreventive user={user} setCurrentPage={setCurrentPage} />
                      </PrivateRoute>
                    }
                  />
                  <Route
                    path="/report-issue"
                    element={
                      <PrivateRoute user={user}>
                        <ReportIssue user={user} setCurrentPage={setCurrentPage} />
                      </PrivateRoute>
                    }
                  />
                  <Route
                    path="/tasks"
                    element={
                      <PrivateRoute user={user}>
                        <Tasks user={user} setCurrentPage={setCurrentPage} />
                      </PrivateRoute>
                    }
                  />
                  <Route
                    path="/add-task"
                    element={
                      <PrivateRoute user={user}>
                        <AddTask user={user} setCurrentPage={setCurrentPage} />
                      </PrivateRoute>
                    }
                  />
                  <Route
                    path="/users"
                    element={
                      <PrivateRoute user={user} role="Engineer">
                        <Users user={user} setCurrentPage={setCurrentPage} />
                      </PrivateRoute>
                    }
                  />
                  <Route
                    path="/add-user"
                    element={
                      <PrivateRoute user={user} role="Engineer">
                        <AddUser user={user} setCurrentPage={setCurrentPage} />
                      </PrivateRoute>
                    }
                  />

                  {/* NEW: Reports route (protected) */}
                  <Route
                    path="/reports"
                    element={
                      <PrivateRoute user={user}>
                        <ReportsPage user={user} setCurrentPage={setCurrentPage} />
                      </PrivateRoute>
                    }
                  />

                  {/* Catch all unmatched routes */}
                  <Route path="*" element={<Navigate to={user ? "/dashboard" : "/login"} replace />} />
                </Routes>
              </div>
            </main>
          </div>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
