import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';

const Tasks = () => {
  const { user } = useAuth();
  const [tasks, setTasks] = useState([]);
  const [equipmentOptions, setEquipmentOptions] = useState([]);
  const [technicianOptions, setTechnicianOptions] = useState([]);
  const [selectedEquipment, setSelectedEquipment] = useState(null);
  const [selectedTechnician, setSelectedTechnician] = useState(null);
  const [taskDescription, setTaskDescription] = useState('');
  const [loading, setLoading] = useState(false);

  // Fetch tasks
  const fetchTasks = async () => {
    try {
      const res = await axios.get('/api/tasks');
      setTasks(res.data.tasks || []);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  // Fetch equipment for dropdown
  const fetchEquipment = async () => {
    try {
      const res = await axios.get('/api/equipment');
      const options = res.data.equipment.map(eq => ({
        value: eq._id,
        label: `${eq.name} (${eq.serialNumber})`
      }));
      setEquipmentOptions(options);
    } catch (err) {
      console.error('Error fetching equipment:', err);
    }
  };

  // Fetch technicians for dropdown
  const fetchTechnicians = async () => {
    try {
      const res = await axios.get('/api/users');
      const techs = res.data.users
        .filter(u => u.role === 'Technician')
        .map(u => ({
          value: u._id,
          label: `${u.name} (${u.serviceNumber})`
        }));
      setTechnicianOptions(techs);
    } catch (err) {
      console.error('Error fetching technicians:', err);
    }
  };

  useEffect(() => {
    fetchTasks();
    if (user?.role === 'Admin' || user?.role === 'Engineer') {
      fetchEquipment();
      fetchTechnicians();
    }
  }, [user]);

  const handleAssignTask = async (e) => {
    e.preventDefault();
    if (!selectedEquipment || !selectedTechnician || !taskDescription.trim()) {
      alert('Please fill in all fields');
      return;
    }
    setLoading(true);
    try {
      await axios.post('/api/tasks', {
        equipment: selectedEquipment.value,
        assignedTo: selectedTechnician.value,
        description: taskDescription
      });
      setSelectedEquipment(null);
      setSelectedTechnician(null);
      setTaskDescription('');
      fetchTasks();
    } catch (err) {
      console.error('Error assigning task:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="tasks-container">
      <h2>Tasks</h2>

      {/* Assign Task Section - Only for Admins & Engineers */}
      {(user?.role === 'Admin' || user?.role === 'Engineer') && (
        <div className="assign-task">
          <h3>Assign New Task</h3>
          <form onSubmit={handleAssignTask} className="assign-task-form">
            <div className="form-group">
              <label>Equipment</label>
              <Select
                options={equipmentOptions}
                value={selectedEquipment}
                onChange={setSelectedEquipment}
                placeholder="Search equipment..."
                isClearable
              />
            </div>
            <div className="form-group">
              <label>Technician</label>
              <Select
                options={technicianOptions}
                value={selectedTechnician}
                onChange={setSelectedTechnician}
                placeholder="Search technician..."
                isClearable
              />
            </div>
            <div className="form-group">
              <label>Task Description</label>
              <textarea
                value={taskDescription}
                onChange={(e) => setTaskDescription(e.target.value)}
                placeholder="Describe the task..."
              />
            </div>
            <button type="submit" disabled={loading}>
              {loading ? 'Assigning...' : 'Assign Task'}
            </button>
          </form>
        </div>
      )}

      {/* Tasks List */}
      <div className="tasks-list">
        <h3>All Tasks</h3>
        <table>
          <thead>
            <tr>
              <th>Equipment</th>
              <th>Description</th>
              <th>Assigned To</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {tasks.length > 0 ? (
              tasks.map(task => (
                <tr key={task._id}>
                  <td>{task.equipment?.name}</td>
                  <td>{task.description}</td>
                  <td>{task.assignedTo?.name}</td>
                  <td>{task.status}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4">No tasks found</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Tasks;
