// frontend/src/components/layout/Sidebar.js
import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = () => {
  const location = useLocation();
  const user = JSON.parse(localStorage.getItem('user'));
  
  const isActive = (path) => {
    return location.pathname === path;
  };

  // Determine which menu items to show based on user role
  const showAllFeatures = user && (user.role === 'Engineer' || user.role === 'Admin');

  return (
    <div className="w-64 bg-gradient-to-b from-blue-900 to-blue-800 text-white shadow-2xl h-screen fixed left-0 top-0 z-40">
      {/* Logo Section */}
      <div className="p-6 border-b border-blue-700">
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-gradient-to-r from-green-400 to-blue-500 rounded-xl flex items-center justify-center">
            <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
            </svg>
          </div>
          <div>
            <h2 className="text-xl font-bold">Servix</h2>
            <p className="text-blue-200 text-sm">CMMS</p>
          </div>
        </div>
      </div>
      
      {/* Navigation */}
      <nav className="mt-6">
        <ul className="space-y-2 px-4">
          <li>
            <Link 
              to="/" 
              className={`flex items-center px-4 py-3 rounded-lg transition-all duration-200 group ${
                isActive('/') 
                  ? 'bg-white/20 border-r-4 border-green-400' 
                  : 'hover:bg-white/10 hover:translate-x-1'
              }`}
            >
              <svg className="w-5 h-5 mr-3 transition-colors duration-200 group-hover:text-green-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
              </svg>
              <span className="font-medium">Dashboard</span>
            </Link>
          </li>
          
          <li>
            <Link 
              to="/equipment" 
              className={`flex items-center px-4 py-3 rounded-lg transition-all duration-200 group ${
                isActive('/equipment') 
                  ? 'bg-white/20 border-r-4 border-green-400' 
                  : 'hover:bg-white/10 hover:translate-x-1'
              }`}
            >
              <svg className="w-5 h-5 mr-3 transition-colors duration-200 group-hover:text-green-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2-2v2m8 0V6a2 2 0 012 2v6a2 2 0 01-2 2H8a2 2 0 01-2-2V8a2 2 0 012-2V6" />
              </svg>
              <span className="font-medium">Equipment</span>
            </Link>
          </li>
          
          {showAllFeatures && (
            <li>
              <Link 
                to="/add-equipment" 
                className="flex items-center px-4 py-3 rounded-lg transition-all duration-200 hover:bg-white/10 hover:translate-x-1 group"
              >
                <svg className="w-5 h-5 mr-3 text-green-400 transition-transform duration-200 group-hover:scale-110" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                </svg>
                <span className="font-medium text-green-400">Add Equipment</span>
              </Link>
            </li>
          )}
        </ul>
      </nav>
      
      {/* User Profile */}
      <div className="absolute bottom-6 left-6 right-6">
        <div className="border-t border-blue-700 pt-6">
          <div className="flex items-center">
            <div className="w-10 h-10 bg-gradient-to-r from-green-400 to-blue-500 rounded-full flex items-center justify-center text-white font-bold">
              {user?.name?.charAt(0) || 'U'}
            </div>
            <div className="ml-3">
              <p className="font-medium">
                {user?.name || 'User'}
              </p>
              <p className="text-blue-200 text-sm">
                {user?.role || 'Staff'}
              </p>
            </div>
          </div>
        </div>
      </div>
      
      {/* Decorative Elements */}
      <div className="absolute top-20 -right-1 w-32 h-32 bg-blue-600/30 rounded-full blur-xl"></div>
      <div className="absolute bottom-20 -left-4 w-24 h-24 bg-green-500/30 rounded-full blur-xl"></div>
    </div>
  );
};

export default Sidebar;
