import React, { useState } from 'react';
import axios from 'axios';

const AddEquipment = () => {
  const [name, setName] = useState('');
  const [serialNumber, setSerialNumber] = useState('');
  const [model, setModel] = useState('');
  const [manufacturer, setManufacturer] = useState('');
  const [department, setDepartment] = useState('');
  const [location, setLocation] = useState('');
  const [category, setCategory] = useState('');
  const [installationDate, setInstallationDate] = useState('');
  const [warrantyExpiry, setWarrantyExpiry] = useState('');
  const [message, setMessage] = useState('');

  // Get logged-in user from localStorage
  const user = JSON.parse(localStorage.getItem('user'));
  const createdBy = user?._id || user?.id;

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/equipment', {
        name,
        serialNumber,
        model,
        manufacturer,
        department,
        location,
        category,
        installationDate,
        warrantyExpiry,
        createdBy,
      });

      setMessage('Equipment added successfully!');
      console.log(response.data);

      // Clear form
      setName('');
      setSerialNumber('');
      setModel('');
      setManufacturer('');
      setDepartment('');
      setLocation('');
      setCategory('');
      setInstallationDate('');
      setWarrantyExpiry('');
    } catch (error) {
      console.error('Error adding equipment:', error.response?.data || error.message);
      setMessage('Error adding equipment. Please check all fields.');
    }
  };

  return (
    <div>
      <h2>Add New Equipment</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Equipment Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Serial Number"
          value={serialNumber}
          onChange={(e) => setSerialNumber(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Model"
          value={model}
          onChange={(e) => setModel(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Manufacturer"
          value={manufacturer}
          onChange={(e) => setManufacturer(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Department"
          value={department}
          onChange={(e) => setDepartment(e.target.value)}
        />
        <input
          type="text"
          placeholder="Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <input
          type="text"
          placeholder="Category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          required
        />
        <label>
          Installation Date:
          <input
            type="date"
            value={installationDate}
            onChange={(e) => setInstallationDate(e.target.value)}
            required
          />
        </label>
        <label>
          Warranty Expiry:
          <input
            type="date"
            value={warrantyExpiry}
            onChange={(e) => setWarrantyExpiry(e.target.value)}
          />
        </label>

        <button type="submit">Add Equipment</button>
      </form>
    </div>
  );
};

export default AddEquipment;
