// frontend/src/components/maintenance/Maintenance.js
import React, { useEffect, useMemo, useState } from 'react';
import api from '../../utils/api';
import { Wrench, Plus, Calendar, ClipboardList, AlertCircle } from 'lucide-react';
import { io } from 'socket.io-client';

const socketURL = 'http://localhost:5000';

const Maintenance = ({ user }) => {
  const [loading, setLoading] = useState(true);
  const [history, setHistory] = useState([]);                 // Completed tasks
  const [pmList, setPmList] = useState([]);                   // Scheduled/Upcoming (Pending/In Progress, taskType != Repair)
  const [error, setError] = useState('');

  // Dropdown data
  const [equipments, setEquipments] = useState([]);
  const [technicians, setTechnicians] = useState([]);

  // Schedule PM modal
  const [showPMModal, setShowPMModal] = useState(false);
  const [pmForm, setPmForm] = useState({
    title: '',
    description: '',
    equipment: '',
    assignedTo: '',
    taskType: 'Inspect',     // default (Install/Inspect/Assess/Calibrate/Repair)
    priority: 'Low',
    dueDate: ''
  });

  // Socket
  const [socket, setSocket] = useState(null);

  // ----- Fetch helpers -----
  const fetchAll = async () => {
    try {
      setLoading(true);
      setError('');

      // Equipment list (for dropdown)
      const eqRes = await api.get('/equipment');
      setEquipments(Array.isArray(eqRes.data.equipment) ? eqRes.data.equipment : []);

      // Technicians (approved=any so you see everyone)
      if (user?.role === 'Engineer' || user?.role === 'Admin') {
        const techRes = await api.get('/users/preapproved/technicians', { params: { approved: 'any' } });
        setTechnicians(techRes?.data?.technicians || []);
      } else {
        setTechnicians([]);
      }

      // Tasks for PM list (Pending/In Progress but not plain Repair installs OK)
      const tasksRes = await api.get('/tasks');
      const allTasks = Array.isArray(tasksRes.data.tasks) ? tasksRes.data.tasks : [];

      // Upcoming / scheduled (exclude Completed/Cancelled)
      const upcoming = allTasks.filter(t => t.status !== 'Completed' && t.status !== 'Cancelled');
      setPmList(upcoming.sort((a,b) => new Date(a.dueDate||a.createdAt) - new Date(b.dueDate||b.createdAt)));

      // Completed history (latest 20)
      const completed = allTasks
        .filter(t => t.status === 'Completed')
        .sort((a, b) => new Date(b.completedDate || b.updatedAt || b.createdAt) - new Date(a.completedDate || a.updatedAt || a.createdAt))
        .slice(0, 20);
      setHistory(completed);

    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Failed to load maintenance data');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchAll(); /* initial */ }, []);

  // ----- Socket live updates -----
  useEffect(() => {
    try {
      const s = io(socketURL, { transports: ['websocket'] });
      setSocket(s);

      // Reuse events you already emit in your backend (examples below)
      const refresh = () => fetchAll();

      s.on('connect', () => {/* connected */});
      s.on('task:created', refresh);
      s.on('task:updated', refresh);
      s.on('task:statusChanged', refresh);
      s.on('equipment:statusChanged', refresh);

      return () => {
        s.off('task:created', refresh);
        s.off('task:updated', refresh);
        s.off('task:statusChanged', refresh);
        s.off('equipment:statusChanged', refresh);
        s.disconnect();
      };
    } catch {
      // If socket server not present, ignore silently
    }
  }, []); // once

  // ----- PM Modal handlers -----
  const openPM = () => setShowPMModal(true);
  const closePM = () => {
    setShowPMModal(false);
    setPmForm({
      title: '',
      description: '',
      equipment: '',
      assignedTo: '',
      taskType: 'Inspect',
      priority: 'Low',
      dueDate: ''
    });
  };
  const onPMChange = (e) => {
    const { name, value } = e.target;
    setPmForm(p => ({ ...p, [name]: value }));
  };

  const submitPM = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const { title, equipment, taskType } = pmForm;
      if (!title || !equipment || !taskType) {
        setError('Please fill the required fields');
        return;
      }

      // Create as a normal task (backend will do equipment status rules you added)
      await api.post('/tasks', {
        ...pmForm,
        assignedTo: pmForm.assignedTo || undefined
      });

      closePM();
      await fetchAll();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to schedule preventive task');
    }
  };

  // ----- UI helpers -----
  const getStatusBadge = (status) => {
    switch (status) {
      case 'Completed': return 'bg-green-100 text-green-700 border border-green-200';
      case 'In Progress': return 'bg-blue-100 text-blue-700 border border-blue-200';
      case 'Pending': return 'bg-yellow-100 text-yellow-700 border border-yellow-200';
      case 'Cancelled': return 'bg-red-100 text-red-700 border border-red-200';
      default: return 'bg-slate-100 text-slate-700 border border-slate-200';
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-12 px-4">
        <div className="max-w-7xl mx-auto">
          <div className="text-center">
            <div className="inline-block h-12 w-12 animate-spin rounded-full border-4 border-solid border-blue-600 border-r-transparent"></div>
            <p className="mt-4 text-xl text-slate-600">Loading maintenance…</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-12 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header + Actions */}
        <div className="mb-8 flex flex-col md:flex-row md:items-center md:justify-between gap-4">
          <div className="flex items-center gap-3">
            <div className="w-12 h-12 rounded-xl bg-gradient-to-r from-blue-600 to-green-500 flex items-center justify-center text-white">
              <Wrench className="w-6 h-6" />
            </div>
            <div>
              <h1 className="text-3xl font-bold text-slate-800">Maintenance Management</h1>
              <p className="text-slate-600">Plan, track and review maintenance work</p>
            </div>
          </div>

          <div className="flex gap-3">
            {(user?.role === 'Engineer' || user?.role === 'Admin') && (
              <button
                onClick={openPM}
                className="inline-flex items-center gap-2 px-5 py-3 bg-amber-600 hover:bg-amber-700 text-white rounded-xl transition"
              >
                <Calendar className="w-5 h-5" />
                Schedule Preventive
              </button>
            )}
            <a
              href="/report-issue"
              className="inline-flex items-center gap-2 px-5 py-3 bg-red-600 hover:bg-red-700 text-white rounded-xl transition"
            >
              <AlertCircle className="w-5 h-5" />
              Report Issue
            </a>
          </div>
        </div>

        {error && (
          <div className="mb-6 px-4 py-3 bg-red-50 text-red-800 border border-red-200 rounded-xl flex items-center gap-2">
            <AlertCircle className="w-5 h-5" />
            {error}
          </div>
        )}

        {/* Two-column layout */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Preventive / Upcoming */}
          <div className="bg-white rounded-2xl shadow-lg border border-slate-200">
            <div className="px-6 py-4 border-b border-slate-200 flex items-center justify-between">
              <h2 className="text-lg font-semibold text-slate-800 flex items-center gap-2">
                <ClipboardList className="w-5 h-5 text-blue-600" />
                Preventive Maintenance
              </h2>
              <span className="text-sm text-slate-500">{pmList.length} task{pmList.length !== 1 ? 's' : ''}</span>
            </div>

            <div className="p-6">
              {pmList.length === 0 ? (
                <div className="text-center py-10">
                  <svg className="mx-auto h-14 w-14 text-slate-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-3-3v6m9 3a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                  <p className="mt-3 text-slate-600">No preventive maintenance tasks</p>
                  {(user?.role === 'Engineer' || user?.role === 'Admin') && (
                    <button onClick={openPM} className="mt-4 inline-flex items-center gap-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg">
                      <Plus className="w-4 h-4" /> Schedule Preventive
                    </button>
                  )}
                </div>
              ) : (
                <div className="space-y-4">
                  {pmList.map(t => (
                    <div key={t._id} className="p-4 rounded-xl border border-slate-200 hover:shadow-sm transition bg-white">
                      <div className="flex items-start justify-between">
                        <div className="min-w-0 pr-3">
                          <div className="font-semibold text-slate-800 truncate">{t.title}</div>
                          <div className="text-sm text-slate-600 mt-1 line-clamp-2">{t.description}</div>
                          <div className="mt-2 text-xs text-slate-500">
                            Equipment: {t.equipment?.name || '—'}{t.equipment?.serialNumber ? ` • S/N ${t.equipment.serialNumber}` : ''}
                            {t.assignedTo?.name ? ` • Technician: ${t.assignedTo.name}` : ''}
                          </div>
                        </div>
                        <div className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusBadge(t.status)}`}>{t.status}</div>
                      </div>
                      <div className="mt-2 text-xs text-slate-500">
                        Type: {t.taskType || 'N/A'} • Priority: {t.priority || 'N/A'} • Due:{' '}
                        {t.dueDate ? new Date(t.dueDate).toLocaleString() : '—'}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Maintenance History (Completed) */}
          <div className="bg-white rounded-2xl shadow-lg border border-slate-200">
            <div className="px-6 py-4 border-b border-slate-200 flex items-center justify-between">
              <h2 className="text-lg font-semibold text-slate-800 flex items-center gap-2">
                <Wrench className="w-5 h-5 text-emerald-600" />
                Maintenance History
              </h2>
              <span className="text-sm text-slate-500">{history.length} record{history.length !== 1 ? 's' : ''}</span>
            </div>

            <div className="p-6">
              {history.length === 0 ? (
                <div className="text-center py-10">
                  <svg className="mx-auto h-14 w-14 text-slate-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-3-3v6m9 3a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                  <p className="mt-3 text-slate-600">No maintenance history</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {history.slice(0, 20).map(t => (
                    <div key={t._id} className="p-4 rounded-xl border border-slate-200 bg-white hover:shadow-sm transition">
                      <div className="flex items-start justify-between">
                        <div className="min-w-0 pr-3">
                          <div className="text-slate-800 font-semibold truncate">{t.title}</div>
                          {t.description && <div className="text-sm text-slate-600 mt-1 line-clamp-2">{t.description}</div>}

                          {/* Work details */}
                          {(t.faultDescription || t.comments || (t.spareParts?.length > 0)) && (
                            <div className="mt-2 p-3 rounded-lg bg-slate-50 border border-slate-200">
                              {t.faultDescription && (
                                <div className="text-sm text-slate-700">
                                  <span className="font-medium">Fault:</span> {t.faultDescription}
                                </div>
                              )}
                              {t.comments && (
                                <div className="text-sm text-slate-700">
                                  <span className="font-medium">Repair Details:</span> {t.comments}
                                </div>
                              )}
                              {t.spareParts?.length > 0 && (
                                <div className="text-sm text-slate-700">
                                  <span className="font-medium">Spare Parts:</span>{' '}
                                  {t.spareParts.map((p, i) => (
                                    <span key={i} className="mr-2">{p.name}({p.quantity || 1})</span>
                                  ))}
                                </div>
                              )}
                            </div>
                          )}

                          <div className="mt-2 text-xs text-slate-500">
                            Equipment: {t.equipment?.name || '—'}
                            {t.assignedTo?.name ? ` • Technician: ${t.assignedTo.name}` : ''}
                          </div>
                        </div>

                        <div className="text-xs text-slate-500 whitespace-nowrap">
                          {t.completedDate
                            ? new Date(t.completedDate).toLocaleString()
                            : new Date(t.updatedAt || t.createdAt).toLocaleString()}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Schedule Preventive Modal */}
      {showPMModal && (
        <div className="fixed inset-0 bg-black/30 z-50 flex items-center justify-center overflow-auto">
          <div className="bg-white rounded-xl shadow-xl max-w-lg w-full p-6 my-10">
            <h3 className="text-xl font-semibold mb-4">Schedule Preventive Task</h3>
            <form onSubmit={submitPM} className="space-y-4 max-h-[80vh] overflow-y-auto pr-2">
              <div>
                <label className="block text-sm font-medium">Title *</label>
                <input
                  name="title" value={pmForm.title} onChange={onPMChange} required
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium">Description</label>
                <textarea
                  name="description" rows={3} value={pmForm.description} onChange={onPMChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium">Task Type *</label>
                <select
                  name="taskType" value={pmForm.taskType} onChange={onPMChange} required
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500"
                >
                  <option value="Inspect">Inspect</option>
                  <option value="Calibrate">Calibrate</option>
                  <option value="Assess">Assess</option>
                  <option value="Install">Install</option>
                  <option value="Repair">Repair</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium">Priority</label>
                <select
                  name="priority" value={pmForm.priority} onChange={onPMChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500"
                >
                  <option>Low</option>
                  <option>Medium</option>
                  <option>High</option>
                  <option>Urgent</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium">Assign To (optional)</label>
                <select
                  name="assignedTo" value={pmForm.assignedTo} onChange={onPMChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">Unassigned</option>
                  {technicians.map(t => (
                    <option key={t._id} value={t._id}>{t.name}</option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium">Equipment *</label>
                <select
                  name="equipment" value={pmForm.equipment} onChange={onPMChange} required
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">Select Equipment</option>
                  {equipments.map(eq => (
                    <option key={eq._id} value={eq._id}>
                      {eq.name} {eq.serialNumber ? `- ${eq.serialNumber}` : ''}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium">Due Date</label>
                <input
                  name="dueDate" type="date" value={pmForm.dueDate} onChange={onPMChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div className="flex justify-end gap-3 pt-2">
                <button type="button" onClick={closePM}
                        className="px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100">Cancel</button>
                <button type="submit"
                        className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg">Save</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Maintenance;
