// frontend/src/components/equipment/AddEquipment.js
import React, { useState } from 'react';
import axios from '../../utils/api';

const AddEquipment = ({ onEquipmentAdded }) => {
  const [formData, setFormData] = useState({
    name: '',
    status: 'Serviceable',
    manufacturer: '',
    model: '',
    serialNumber: '',
    department: '',
    installationDate: '',
    warrantyExpiry: ''
  });

  const { name, status, manufacturer, model, serialNumber, department, installationDate, warrantyExpiry } = formData;

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();

    try {
      const response = await axios.post('/equipment', formData);
      onEquipmentAdded(response.data);
    } catch (error) {
      console.error('Error adding equipment:', error);
    }
  };

  return (
    <form onSubmit={onSubmit}>
      {/* Equipment form fields */}
      <div className="mb-4">
        <label htmlFor="name">Equipment Name *</label>
        <input
          type="text"
          id="name"
          name="name"
          value={name}
          onChange={onChange}
          required
        />
      </div>
      {/* Other form fields... */}

      <button type="submit">Add Equipment</button>
    </form>
  );
};

export default AddEquipment;
