// frontend/src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import EquipmentList from './components/equipment/EquipmentList';
import AddEquipment from './components/equipment/AddEquipment';
import Login from './components/auth/Login';
import Sidebar from './components/layout/Sidebar';

function App() {
  return (
    <Router>
      <div className="flex">
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/" element={
            <>
              <Sidebar />
              <main className="ml-64 mt-16 min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
                <div className="max-w-7xl mx-auto py-12 px-4">
                  <div className="text-center mb-12">
                    <h1 className="text-4xl font-bold text-slate-800 mb-4">
                      Servix CMMS Dashboard
                    </h1>
                    <p className="text-slate-600 text-lg">
                      Comprehensive maintenance management system
                    </p>
                  </div>
                  <EquipmentList />
                </div>
              </main>
            </>
          } />
          <Route path="/equipment" element={
            <>
              <Sidebar />
              <main className="ml-64 mt-16 min-h-screen">
                <div className="max-w-7xl mx-auto py-12 px-4">
                  <EquipmentList />
                </div>
              </main>
            </>
          } />
          <Route path="/add-equipment" element={
            <>
              <Sidebar />
              <main className="ml-64 mt-16 min-h-screen">
                <div className="max-w-4xl mx-auto py-12 px-4">
                  <AddEquipment />
                </div>
              </main>
            </>
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
