// frontend/src/components/equipment/AddEquipment.js
import React, { useState } from 'react';
import api from '../../utils/api';

const AddEquipment = ({ onEquipmentAdded }) => {
  const [formData, setFormData] = useState({
    name: '',
    status: 'Serviceable',
    manufacturer: '',
    model: '',
    serialNumber: '',
    department: '',
    location: '',
    installationDate: '',
    warrantyExpiry: '',
    category: ''
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Destructure form fields for easy access
  const {
    name,
    status,
    manufacturer,
    model,
    serialNumber,
    department,
    location,
    installationDate,
    warrantyExpiry,
    category
  } = formData;

  // Handle input changes
  const onChange = (e) =>
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value
    }));

  // Validate required fields
  const validateForm = () => {
    if (
      !name ||
      !status ||
      !manufacturer ||
      !model ||
      !serialNumber ||
      !department ||
      !location ||
      !installationDate ||
      !category
    ) {
      setError('Please fill in all required fields');
      return false;
    }
    return true;
  };

  // Handle form submission
  const onSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);
    setError('');

    try {
      const response = await api.post('/equipment', formData);
      onEquipmentAdded(response.data);
      // Reset form after successful submission
      setFormData({
        name: '',
        status: 'Serviceable',
        manufacturer: '',
        model: '',
        serialNumber: '',
        department: '',
        location: '',
        installationDate: '',
        warrantyExpiry: '',
        category: ''
      });
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to add equipment');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white rounded-2xl shadow-lg border border-slate-200 p-6">
      <h2 className="text-2xl font-bold text-slate-800 mb-6">Add New Equipment</h2>

      {error && (
        <div className="mb-4 p-3 bg-red-100 border border-red-200 text-red-700 rounded-lg">
          {error}
        </div>
      )}

      <form onSubmit={onSubmit}>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Left Column */}
          <div className="space-y-4">
            {[
              { label: 'Equipment Name *', name: 'name', type: 'text', placeholder: 'Enter equipment name' },
              { label: 'Manufacturer *', name: 'manufacturer', type: 'text', placeholder: 'Enter manufacturer name' },
              { label: 'Model *', name: 'model', type: 'text', placeholder: 'Enter model number' },
              { label: 'Serial Number *', name: 'serialNumber', type: 'text', placeholder: 'Enter serial number' }
            ].map((field) => (
              <div key={field.name}>
                <label className="block text-sm font-semibold text-slate-700 mb-2">{field.label}</label>
                <input
                  type={field.type}
                  name={field.name}
                  value={formData[field.name]}
                  onChange={onChange}
                  className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder={field.placeholder}
                />
              </div>
            ))}
          </div>

          {/* Right Column */}
          <div className="space-y-4">
            {[
              { label: 'Department *', name: 'department', type: 'text', placeholder: 'Enter department' },
              { label: 'Location *', name: 'location', type: 'text', placeholder: 'Enter location' },
              { label: 'Installation Date *', name: 'installationDate', type: 'date' },
              { label: 'Warranty Expiry', name: 'warrantyExpiry', type: 'date' }
            ].map((field) => (
              <div key={field.name}>
                <label className="block text-sm font-semibold text-slate-700 mb-2">{field.label}</label>
                <input
                  type={field.type}
                  name={field.name}
                  value={formData[field.name]}
                  onChange={onChange}
                  className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder={field.placeholder || ''}
                />
              </div>
            ))}

            {/* Status Dropdown */}
            <div>
              <label className="block text-sm font-semibold text-slate-700 mb-2">Status</label>
              <select
                name="status"
                value={status}
                onChange={onChange}
                className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="Serviceable">Serviceable</option>
                <option value="Under Maintenance">Under Maintenance</option>
                <option value="Unserviceable">Unserviceable</option>
                <option value="Decommissioned">Decommissioned</option>
                <option value="Auctioned">Auctioned</option>
              </select>
            </div>

            {/* Category Input */}
            <div>
              <label className="block text-sm font-semibold text-slate-700 mb-2">Category *</label>
              <input
                type="text"
                name="category"
                value={category}
                onChange={onChange}
                className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="Enter category"
              />
            </div>
          </div>
        </div>

        {/* Submit Button */}
        <div className="mt-8 flex justify-end">
          <button
            type="submit"
            disabled={loading}
            className="px-8 py-3 bg-gradient-to-r from-blue-600 to-green-500 text-white font-medium rounded-xl hover:from-blue-700 hover:to-green-600 transition-all duration-200 transform hover:scale-105 disabled:opacity-70"
          >
            {loading ? (
              <div className="flex items-center">
                <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                Adding Equipment...
              </div>
            ) : (
              'Add Equipment'
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddEquipment;
