  const [manufacturer, setManufacturer] = useState('');
  const [department, setDepartment] = useState('');
  const [location, setLocation] = useState('');
  const [category, setCategory] = useState('');
  const [installationDate, setInstallationDate] = useState('');
  const [warrantyExpiry, setWarrantyExpiry] = useState('');
  const [message, setMessage] = useState('');

  // Get logged-in user from localStorage
  const user = JSON.parse(localStorage.getItem('user'));
  const createdBy = user?._id || user?.id;

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/equipment', {
        name,
        serialNumber,
        model,
        manufacturer,
        department,
        location,
        category,
        installationDate,
        warrantyExpiry,
        createdBy,
      });

      setMessage('Equipment added successfully!');
      console.log(response.data);

      // Clear form
      setName('');
      setSerialNumber('');
      setModel('');
      setManufacturer('');
      setDepartment('');
      setLocation('');
      setCategory('');
      setInstallationDate('');
      setWarrantyExpiry('');
    } catch (error) {
      console.error('Error adding equipment:', error.response?.data || error.message);
      setMessage('Error adding equipment. Please check all fields.');
    }
  };

  const handlePrint = () => {
    // Create a printable version of the equipment data
    const printWindow = window.open('', '_blank');
    const currentDate = new Date().toLocaleDateString();
    
    const printContent = `
      <html>
        <head>
          <title>Equipment Details - ${name || 'New Equipment'}</title>
          <style>
            body {
              font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
              line-height: 1.6;
              color: #333;
              max-width: 800px;
              margin: 0 auto;
              padding: 40px;
            }
            .header {
              text-align: center;
              border-bottom: 2px solid #3b82f6;
              padding-bottom: 20px;
              margin-bottom: 30px;
            }
            .logo {
              font-size: 28px;
              font-weight: bold;
              color: #3b82f6;
              margin-bottom: 5px;
            }
            .title {
              font-size: 24px;
              color: #1f2937;
              margin: 10px 0;
            }
            .date {
              color: #6b7280;
              font-size: 14px;
            }
            .equipment-grid {
              display: grid;
              grid-template-columns: 1fr 1fr;
              gap: 20px;
              margin-bottom: 30px;
            }
            .field {
              margin-bottom: 15px;
            }
            .label {
              font-weight: 600;
              color: #4b5563;
              font-size: 14px;
              text-transform: uppercase;
              letter-spacing: 0.5px;
            }
            .value {
              font-size: 16px;
              padding: 10px;
              background-color: #f9fafb;
              border: 1px solid #e5e7eb;
              border-radius: 6px;
              margin-top: 5px;
            }
            .footer {
              text-align: center;
              margin-top: 40px;
              color: #9ca3af;
              font-size: 12px;
              border-top: 1px solid #e5e7eb;
              padding-top: 20px;
            }
            @media print {
              body {
                padding: 20px;
              }
              .no-print {
                display: none;
              }
            }
          </style>
        </head>
        <body>
          <div class="header">
            <div class="logo">SERVIX CMMS</div>
            <div class="title">Equipment Details</div>
            <div class="date">Printed on: ${currentDate}</div>
          </div>
          
          <div class="equipment-grid">
            <div class="field">
              <div class="label">Equipment Name</div>
              <div class="value">${name || 'Not specified'}</div>
            </div>
            <div class="field">
              <div class="label">Serial Number</div>
              <div class="value">${serialNumber || 'Not specified'}</div>
            </div>
            <div class="field">
              <div class="label">Model</div>
              <div class="value">${model || 'Not specified'}</div>
            </div>
            <div class="field">
              <div class="label">Manufacturer</div>
              <div class="value">${manufacturer || 'Not specified'}</div>
            </div>
            <div class="field">
              <div class="label">Department</div>
              <div class="value">${department || 'Not specified'}</div>
            </div>
            <div class="field">
              <div class="label">Location</div>
              <div class="value">${location || 'Not specified'}</div>
            </div>
            <div class="field">
              <div class="label">Category</div>
              <div class="value">${category || 'Not specified'}</div>
            </div>
            <div class="field">
              <div class="label">Installation Date</div>
              <div class="value">${installationDate || 'Not specified'}</div>
            </div>
            <div class="field">
              <div class="label">Warranty Expiry</div>
              <div class="value">${warrantyExpiry || 'Not specified'}</div>
            </div>
            <div class="field">
              <div class="label">Created By</div>
              <div class="value">${user?.name || 'Not specified'}</div>
            </div>
          </div>
          
          <div class="footer">
            Servix CMMS - Computerized Maintenance Management System
          </div>
        </body>
      </html>
    `;
    
    printWindow.document.write(printContent);
    printWindow.document.close();
    printWindow.print();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-12 px-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-slate-800 mb-4">
            Add New Equipment
          </h1>
          <p className="text-slate-600 text-lg">
            Enter the details of your new equipment below
          </p>
        </div>

        {/* Message Display */}
        {message && (
          <div className={`max-w-md mx-auto mb-8 p-4 rounded-xl font-medium text-center transition-all duration-300 ${
            message.includes('successfully') 
              ? 'bg-emerald-50 text-emerald-700 border border-emerald-200' 
              : 'bg-rose-50 text-rose-700 border border-rose-200'
          }`}>
            {message}
          </div>
        )}

        {/* Form Container */}
        <div className="bg-white rounded-2xl shadow-xl p-8 md:p-12 border border-slate-200 relative">
          <form onSubmit={handleSubmit} className="space-y-8">
            {/* Equipment Details Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              {/* Left Column */}
              <div className="space-y-6">
                <div className="space-y-2">
                  <label htmlFor="name" className="block text-sm font-semibold text-slate-700">
                    Equipment Name *
                  </label>
                  <input
                    type="text"
                    id="name"
                    placeholder="Enter equipment name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                    className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700 placeholder-slate-400"
                  />
                </div>

                <div className="space-y-2">
                  <label htmlFor="serialNumber" className="block text-sm font-semibold text-slate-700">
                    Serial Number *
                  </label>
                  <input
                    type="text"
                    id="serialNumber"
                    placeholder="Enter serial number"
                    value={serialNumber}
                    onChange={(e) => setSerialNumber(e.target.value)}
                    required
                    className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700 placeholder-slate-400"
                  />
                </div>

                <div className="space-y-2">
                  <label htmlFor="model" className="block text-sm font-semibold text-slate-700">
                    Model *
                  </label>
                  <input
                    type="text"
                    id="model"
                    placeholder="Enter model"
                    value={model}
                    onChange={(e) => setModel(e.target.value)}
                    required
                    className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700 placeholder-slate-400"
                  />
                </div>

                <div className="space-y-2">
                  <label htmlFor="manufacturer" className="block text-sm font-semibold text-slate-700">
                    Manufacturer *
                  </label>
                  <input
                    type="text"
                    id="manufacturer"
                    placeholder="Enter manufacturer"
                    value={manufacturer}
                    onChange={(e) => setManufacturer(e.target.value)}
                    required
                    className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700 placeholder-slate-400"
                  />
                </div>
              </div>

              {/* Right Column */}
              <div className="space-y-6">
                <div className="space-y-2">
                  <label htmlFor="department" className="block text-sm font-semibold text-slate-700">
                    Department
                  </label>
                  <input
                    type="text"
                    id="department"
                    placeholder="Enter department"
                    value={department}
                    onChange={(e) => setDepartment(e.target.value)}
                    className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700 placeholder-slate-400"
                  />
                </div>

                <div className="space-y-2">
                  <label htmlFor="location" className="block text-sm font-semibold text-slate-700">
                    Location
                  </label>
                  <input
                    type="text"
                    id="location"
                    placeholder="Enter location"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700 placeholder-slate-400"
                  />
                </div>

                <div className="space-y-2">
                  <label htmlFor="category" className="block text-sm font-semibold text-slate-700">
                    Category *
                  </label>
                  <input
                    type="text"
                    id="category"
                    placeholder="Enter category"
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                    required
                    className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700 placeholder-slate-400"
                  />
                </div>
              </div>
            </div>

            {/* Date Fields */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div className="space-y-2">
                <label htmlFor="installationDate" className="block text-sm font-semibold text-slate-700">
                  Installation Date *
                </label>
                <div className="relative">
                  <input
                    type="date"
                    id="installationDate"
                    value={installationDate}
                    onChange={(e) => setInstallationDate(e.target.value)}
                    required
                    className="w-full px-4 py-3 pl-10 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700"
                  />
                  <svg 
                    className="absolute left-3 top-3.5 h-5 w-5 text-slate-400" 
                    fill="none" 
                    stroke="currentColor" 
                    viewBox="0 0 24 24"
                  >
                    <path 
                      strokeLinecap="round" 
                      strokeLinejoin="round" 
                      strokeWidth={2} 
                      d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" 
                    />
                  </svg>
                </div>
              </div>

              <div className="space-y-2">
                <label htmlFor="warrantyExpiry" className="block text-sm font-semibold text-slate-700">
                  Warranty Expiry
                </label>
                <div className="relative">
                  <input
                    type="date"
                    id="warrantyExpiry"
                    value={warrantyExpiry}
                    onChange={(e) => setWarrantyExpiry(e.target.value)}
                    className="w-full px-4 py-3 pl-10 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700"
                  />
                  <svg 
                    className="absolute left-3 top-3.5 h-5 w-5 text-slate-400" 
                    fill="none" 
                    stroke="currentColor" 
                    viewBox="0 0 24 24"
                  >
                    <path 
                      strokeLinecap="round" 
                      strokeLinejoin="round" 
                      strokeWidth={2} 
                      d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" 
                    />
                  </svg>
                </div>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="flex flex-col sm:flex-row gap-4 pt-4">
              <button
                type="submit"
                className="flex-1 px-12 py-4 bg-gradient-to-r from-blue-600 to-indigo-600 text-white font-semibold rounded-xl hover:from-blue-700 hover:to-indigo-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-all duration-200 transform hover:scale-105 shadow-lg"
              >
                Add Equipment
              </button>
              
              <button
                type="button"
                onClick={handlePrint}
                className="flex-1 px-12 py-4 bg-gradient-to-r from-gray-600 to-gray-700 text-white font-semibold rounded-xl hover:from-gray-700 hover:to-gray-800 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2 transition-all duration-200 transform hover:scale-105 shadow-lg"
              >
                <svg className="w-5 h-5 inline-block mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 17h2a2 2 0 002-2v-4a2 2 0 00-2-2H5a2 2 0 00-2 2v4a2 2 0 002 2h2m2 4h6a2 2 0 002-2v-4a2 2 0 00-2-2H9a2 2 0 00-2 2v4a2 2 0 002 2zm8-12V5a2 2 0 00-2-2H9a2 2 0 00-2 2v4h10z" />
                </svg>
                Print Equipment
              </button>
            </div>
          </form>
        </div>

        {/* Decorative Elements */}
        <div className="absolute top-20 -left-10 w-72 h-72 bg-blue-200 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-pulse"></div>
        <div className="absolute top-40 -right-10 w-72 h-72 bg-indigo-200 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-pulse" style={{animationDelay: '2s'}}></div>
      </div>
    </div>
  );
};

export default AddEquipment;
