// frontend/src/components/maintenance/Maintenance.js
import React, { useEffect, useMemo, useState } from 'react';
import io from 'socket.io-client';
import api from '../../utils/api';
import { Plus, Calendar, CheckCircle } from 'lucide-react';

const socket = io('http://localhost:5000', { transports: ['websocket'] });

export default function Maintenance() {
  const user = JSON.parse(localStorage.getItem('user'));

  const [preventive, setPreventive] = useState([]);
  const [history, setHistory] = useState([]);
  const [equipments, setEquipments] = useState([]);
  const [techs, setTechs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState('');

  // schedule modal
  const [pmOpen, setPmOpen] = useState(false);
  const [pmForm, setPmForm] = useState({
    title: '', description: '',
    pmType: '', pmInterval: '', priority: 'Low',
    assignedTo: '', equipment: '', dueDate: ''
  });

  const load = async () => {
    try {
      setErr(''); setLoading(true);
      const [tAll, eqRes] = await Promise.all([
        api.get('/tasks?includePreventive=true'),
        api.get('/equipment')
      ]);
      const tasks = tAll.data.tasks || [];
      setPreventive(tasks.filter(t => t.isPreventive === true));
      setHistory(tasks.filter(t => t.status === 'Completed' && !t.isPreventive));
      setEquipments(eqRes.data.equipment || []);
      if (user?.role === 'Engineer') {
        const u = await api.get('/users/preapproved/technicians?approved=any');
        setTechs(u.data.technicians || []);
      }
    } catch (e) {
      setErr(e.response?.data?.message || 'Failed to load maintenance');
    } finally { setLoading(false); }
  };

  useEffect(()=>{ load(); }, []);
  useEffect(()=>{
    const r = ()=>load();
    ['task:created','task:updated','task:workUpdated','task:certified','task:deleted'].forEach(evt => socket.on(evt, r));
    return ()=>['task:created','task:updated','task:workUpdated','task:certified','task:deleted'].forEach(evt => socket.off(evt, r));
  },[]);

  const createPM = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        title: pmForm.title || pmForm.pmType || 'Preventive Task',
        description: pmForm.description,
        taskType: 'Preventive',
        priority: pmForm.priority,
        assignedTo: pmForm.assignedTo || undefined,
        equipment: pmForm.equipment,
        dueDate: pmForm.dueDate,
        isPreventive: true,
        pmType: pmForm.pmType,
        pmInterval: pmForm.pmInterval
      };
      await api.post('/tasks', payload);
      setPmOpen(false);
      setPmForm({ title:'', description:'', pmType:'', pmInterval:'', priority:'Low', assignedTo:'', equipment:'', dueDate:'' });
    } catch (e2) {
      alert(e2.response?.data?.message || 'Failed to schedule preventive task');
    }
  };

  if (loading) {
    return <div className="p-8"><div className="bg-white rounded-2xl p-12 border shadow text-center">Loading…</div></div>;
  }
  if (err) {
    return <div className="p-8"><div className="bg-white rounded-2xl p-12 border shadow text-center text-red-600">{err}</div></div>;
  }

  return (
    <div className="p-8 space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold text-slate-800">Maintenance Management</h1>
        {user?.role==='Engineer' && (
          <button onClick={()=>setPmOpen(true)} className="inline-flex items-center gap-2 px-5 py-3 bg-amber-600 hover:bg-amber-700 text-white rounded-xl">
            <Plus className="w-5 h-5"/> Schedule Preventive
          </button>
        )}
      </div>

      <div className="grid md:grid-cols-2 gap-6">
        {/* Preventive Panel */}
        <div className="bg-white rounded-2xl border shadow p-6">
          <div className="flex items-center gap-2 mb-4">
            <Calendar className="w-5 h-5 text-amber-600"/><h2 className="text-lg font-semibold">Preventive Maintenance</h2>
          </div>
          {preventive.length===0 ? (
            <div className="text-slate-500">No preventive maintenance tasks.</div>
          ) : (
            <div className="space-y-3">
              {preventive.map(p=>(
                <div key={p._id} className="border rounded-xl p-4">
                  <div className="font-semibold">{p.title}</div>
                  <div className="text-sm text-slate-600">Type: {p.pmType} • Interval: {p.pmInterval} • Priority: {p.priority}</div>
                  <div className="text-xs text-slate-500 mt-1">Equipment: {p.equipment?.name || 'N/A'}</div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* History */}
        <div className="bg-white rounded-2xl border shadow p-6">
          <div className="flex items-center gap-2 mb-4">
            <CheckCircle className="w-5 h-5 text-green-600"/><h2 className="text-lg font-semibold">Maintenance History</h2>
          </div>
          {history.length===0 ? (
            <div className="text-slate-500">No maintenance history yet.</div>
          ) : (
            <div className="space-y-3">
              {history.map(t=>(
                <div key={t._id} className="border rounded-xl p-4">
                  <div className="flex justify-between">
                    <div>
                      <div className="font-semibold">{t.title}</div>
                      <div className="text-xs text-slate-500">Equipment: {t.equipment?.name || 'N/A'} {t.assignedTo?.name && <>• Technician: {t.assignedTo.name}</>}</div>
                    </div>
                    <div className="text-xs px-2 py-1 rounded bg-green-100 border border-green-200 text-green-800">Completed</div>
                  </div>
                  {(t.faultDescription||t.comments||(t.spareParts?.length)) && (
                    <div className="grid md:grid-cols-3 gap-3 mt-3">
                      {t.faultDescription && <div className="bg-slate-50 p-3 rounded border"><div className="text-xs text-slate-500 uppercase">Fault</div><div className="text-sm">{t.faultDescription}</div></div>}
                      {t.comments && <div className="bg-slate-50 p-3 rounded border"><div className="text-xs text-slate-500 uppercase">Repair</div><div className="text-sm whitespace-pre-wrap">{t.comments}</div></div>}
                      {(t.spareParts?.length>0) && <div className="bg-slate-50 p-3 rounded border"><div className="text-xs text-slate-500 uppercase">Spare Parts</div><ul className="text-sm list-disc list-inside">{t.spareParts.map((s,i)=><li key={i}><b>{s.name}</b>{typeof s.quantity==='number' && ` × ${s.quantity}`}{s.description && ` — ${s.description}`}</li>)}</ul></div>}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Schedule Preventive Modal */}
      {pmOpen && user?.role==='Engineer' && (
        <div className="fixed inset-0 bg-black/40 z-50 flex items-center justify-center">
          <div className="bg-white rounded-2xl w-full max-w-2xl p-6 max-h-[85vh] overflow-y-auto">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold">Schedule Preventive Task</h3>
              <button onClick={()=>setPmOpen(false)} className="text-slate-500 hover:text-slate-700">✕</button>
            </div>
            <form onSubmit={createPM} className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-1">Title (optional)</label>
                <input className="w-full border rounded-lg px-3 py-2" value={pmForm.title} onChange={e=>setPmForm(f=>({...f,title:e.target.value}))}/>
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Description</label>
                <textarea className="w-full border rounded-lg px-3 py-2" rows={3} value={pmForm.description} onChange={e=>setPmForm(f=>({...f,description:e.target.value}))}/>
              </div>
              <div className="grid md:grid-cols-3 gap-3">
                <div>
                  <label className="block text-sm font-medium mb-1">PM Type *</label>
                  <select required className="w-full border rounded-lg px-3 py-2" value={pmForm.pmType} onChange={e=>setPmForm(f=>({...f,pmType:e.target.value}))}>
                    <option value="">Select</option>
                    <option>Cleaning</option><option>Lubrication</option><option>Adjustment</option><option>Calibrations</option><option>Safety Testing</option><option>Filter Replacement</option>
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-1">Priority</label>
                  <select className="w-full border rounded-lg px-3 py-2" value={pmForm.priority} onChange={e=>setPmForm(f=>({...f,priority:e.target.value}))}>
                    <option>Low</option><option>Medium</option><option>High</option><option>Urgent</option>
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-1">Periodic Interval *</label>
                  <select required className="w-full border rounded-lg px-3 py-2" value={pmForm.pmInterval} onChange={e=>setPmForm(f=>({...f,pmInterval:e.target.value}))}>
                    <option value="">Select interval</option>
                    <option>Weekly</option><option>Monthly</option><option>Quarterly</option><option>Biannually</option><option>Annually</option>
                  </select>
                </div>
              </div>
              <div className="grid md:grid-cols-2 gap-3">
                <div>
                  <label className="block text-sm font-medium mb-1">Assign To (optional)</label>
                  <select className="w-full border rounded-lg px-3 py-2" value={pmForm.assignedTo} onChange={e=>setPmForm(f=>({...f,assignedTo:e.target.value}))}>
                    <option value="">Unassigned (show in Preventive)</option>
                    {techs.map(t=> <option key={t._id} value={t._id}>{t.name}</option>)}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-1">Equipment *</label>
                  <select required className="w-full border rounded-lg px-3 py-2" value={pmForm.equipment} onChange={e=>setPmForm(f=>({...f,equipment:e.target.value}))}>
                    <option value="">Select Equipment</option>
                    {equipments.map(e => <option key={e._id} value={e._id}>{e.name} — {e.serialNumber}</option>)}
                  </select>
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Due Date *</label>
                <input required type="date" className="w-full border rounded-lg px-3 py-2" value={pmForm.dueDate} onChange={e=>setPmForm(f=>({...f,dueDate:e.target.value}))}/>
              </div>

              <div className="flex justify-end gap-3 pt-2">
                <button type="button" onClick={()=>setPmOpen(false)} className="px-4 py-2 border rounded-lg">Cancel</button>
                <button type="submit" className="px-6 py-2 bg-amber-600 hover:bg-amber-700 text-white rounded-lg">Save</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
