// frontend/src/components/tasks/Tasks.js
import React, { useEffect, useMemo, useState } from 'react';
import io from 'socket.io-client';
import api from '../../utils/api';
import {
  ClipboardCheck,
  CheckCircle,
  ArrowRight,
  Clock,
  X,
  Wrench,
  Plus,
  AlertCircle,
  Check
} from 'lucide-react';

const SOCKET_URL = 'http://localhost:5000'; // match your backend origin

function Tasks() {
  const user = JSON.parse(localStorage.getItem('user'));
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [search, setSearch] = useState('');

  // Work modal
  const [workOpen, setWorkOpen] = useState(false);
  const [workTask, setWorkTask] = useState(null);
  const [workForm, setWorkForm] = useState({
    faultDescription: '',
    repairDetails: '',
    sparePartsUsed: [{ name: '', quantity: 1, description: '' }]
  });

  const fetchTasks = async () => {
    try {
      setLoading(true);
      setError('');
      // your backend excludes preventive by default; keep explicit for clarity
      const res = await api.get('/tasks', { params: { includePreventive: 'false' } });
      setTasks(Array.isArray(res.data.tasks) ? res.data.tasks : []);
    } catch (e) {
      setError(e.response?.data?.message || 'Failed to load tasks');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  // Socket live refresh
  useEffect(() => {
    const s = io(SOCKET_URL, { transports: ['websocket'] });
    const refresh = () => fetchTasks();
    s.on('task:created', refresh);
    s.on('task:updated', refresh);
    s.on('task:workUpdated', refresh);
    s.on('task:certified', refresh);
    s.on('task:deleted', refresh);
    return () => s.disconnect();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const filtered = useMemo(() => {
    if (!search) return tasks;
    const q = search.toLowerCase();
    return tasks.filter(t =>
      (t.title || '').toLowerCase().includes(q) ||
      (t.description || '').toLowerCase().includes(q) ||
      (t.assignedTo?.name || '').toLowerCase().includes(q) ||
      (t.equipment?.name || '').toLowerCase().includes(q)
    );
  }, [tasks, search]);

  const getStatusColor = (status) => {
    switch (status) {
      case 'Completed': return 'bg-green-100 text-green-800 border border-green-200';
      case 'In Progress': return 'bg-blue-100 text-blue-800 border border-blue-200';
      case 'Pending': return 'bg-yellow-100 text-yellow-800 border border-yellow-200';
      case 'Cancelled': return 'bg-red-100 text-red-800 border border-red-200';
      default: return 'bg-gray-100 text-gray-800 border border-gray-200';
    }
  };
  const getStatusIcon = (status) => {
    switch (status) {
      case 'Completed': return <CheckCircle className="w-4 h-4" />;
      case 'In Progress': return <ArrowRight className="w-4 h-4" />;
      case 'Pending': return <Clock className="w-4 h-4" />;
      case 'Cancelled': return <X className="w-4 h-4" />;
      default: return <Clock className="w-4 h-4" />;
    }
  };

  // TECH/ENGINEER: update status (tech sets needsCertification; equipment unchanged server-side)
  const updateStatus = async (taskId, status) => {
    // optimistic UI
    setTasks(prev => prev.map(t =>
      t._id === taskId
        ? {
            ...t,
            status,
            // tech change → show awaiting cert immediately
            needsCertification: user?.role === 'Technician' ? true : t.needsCertification
          }
        : t
    ));
    try {
      await api.put(`/tasks/${taskId}/status`, { status });
      setSuccess('Status updated');
      setTimeout(() => setSuccess(''), 1200);
    } catch (e) {
      setError(e.response?.data?.message || 'Failed to update status');
      fetchTasks(); // revert to server truth
    }
  };

  // TECH: open work modal
  const openWork = (task) => {
    setWorkTask(task);
    setWorkForm({
      faultDescription: task.faultDescription || '',
      // backend stores technician repair notes in `comments`; we still send 'repairDetails'
      repairDetails: task.comments || task.repairDetails || '',
      sparePartsUsed: task.spareParts?.length ? task.spareParts : [{ name: '', quantity: 1, description: '' }]
    });
    setWorkOpen(true);
  };

  // TECH: save work → flags for certification
  const saveWork = async (e) => {
    e.preventDefault();
    if (!workTask) return;
    try {
      await api.put(`/tasks/${workTask._id}/work`, {
        faultDescription: workForm.faultDescription,
        repairDetails: workForm.repairDetails, // backend maps this to `comments`
        sparePartsUsed: workForm.sparePartsUsed.filter(p => p.name?.trim())
      });
      // optimistic set flag
      setTasks(prev => prev.map(t => t._id === workTask._id ? { ...t, needsCertification: true } : t));
      setWorkOpen(false);
      setWorkTask(null);
      setSuccess('Work saved (awaiting certification)');
      setTimeout(() => setSuccess(''), 1500);
    } catch (e2) {
      setError(e2.response?.data?.message || 'Failed to save work');
    }
  };

  // ENGINEER/Admin: certify (IMPORTANT: PUT to /certify to match your routes)
  const certify = async (taskId) => {
    try {
      await api.put(`/tasks/${taskId}/certify`);
      setSuccess('Task certified & equipment updated');
      setTimeout(() => setSuccess(''), 1500);
      fetchTasks();
    } catch (e) {
      setError(e.response?.data?.message || 'Failed to certify');
    }
  };

  // UI
  if (loading) {
    return (
      <div className="min-h-screen bg-slate-50 py-8 px-4">
        <div className="max-w-7xl mx-auto">
          <div className="bg-white rounded-2xl shadow-lg border border-slate-200 p-12 text-center">
            <div className="inline-block h-12 w-12 animate-spin rounded-full border-4 border-solid border-blue-600 border-r-transparent"></div>
            <p className="mt-4 text-xl text-slate-600">Loading tasks...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-8 px-4">
      <div className="max-w-7xl mx-auto">

        {/* Header */}
        <div className="mb-8 flex items-center gap-3">
          <div className="bg-gradient-to-r from-blue-600 to-green-500 p-2 rounded-lg text-white">
            <ClipboardCheck className="w-6 h-6" />
          </div>
          <h1 className="text-3xl font-bold text-slate-800">Task Management</h1>
        </div>

        {/* Search */}
        <div className="bg-white rounded-2xl border border-slate-200 p-4 mb-6">
          <div className="relative">
            <input
              className="w-full pl-10 pr-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Search tasks by description, title, equipment, or technician..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <svg className="w-5 h-5 absolute left-3 top-3.5 text-slate-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
            </svg>
          </div>
        </div>

        {/* Alerts */}
        {success && (
          <div className="mb-4 px-4 py-2 bg-green-100 text-green-700 rounded">{success}</div>
        )}
        {error && (
          <div className="mb-4 px-4 py-2 bg-red-100 text-red-700 rounded flex items-center gap-2">
            <AlertCircle className="w-5 h-5" />
            {error}
          </div>
        )}

        {/* Count */}
        <div className="mb-6 text-sm text-slate-600 bg-white rounded-xl p-4 border border-slate-200">
          <span className="font-medium">{filtered.length}</span> task{filtered.length !== 1 ? 's' : ''} found
        </div>

        {/* Grid */}
        {filtered.length === 0 ? (
          <div className="bg-white rounded-2xl shadow-lg border border-slate-200 p-12 text-center">
            <svg className="mx-auto h-16 w-16 text-slate-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9.172 16.172a4 4 0 005.656 0M12 12v.01M7 16v.01M17 16v.01M21 12v.01" />
            </svg>
            <h3 className="mt-4 text-xl font-semibold text-slate-600">No tasks found</h3>
            <p className="mt-2 text-slate-400">Try adjusting your search.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filtered.map(t => (
              <div key={t._id} className="bg-white rounded-2xl shadow-lg border border-slate-200 overflow-hidden hover:shadow-xl transition-all">
                {/* Status header */}
                <div className={`flex items-center gap-2 px-4 py-2 ${getStatusColor(t.status)}`}>
                  {getStatusIcon(t.status)}
                  <span className="text-sm font-semibold">{t.status}</span>
                  {t.needsCertification && (
                    <span className="ml-auto text-[11px] px-2 py-0.5 rounded bg-indigo-100 text-indigo-700 border border-indigo-200 whitespace-nowrap">
                      Awaiting Certification
                    </span>
                  )}
                </div>

                <div className="p-5 space-y-2">
                  <div className="flex items-start justify-between gap-3">
                    <h3 className="text-lg font-semibold text-slate-800">{t.title}</h3>
                    <div className="shrink-0">
                      <span className="inline-flex items-center text-xs px-2 py-1 rounded bg-slate-100 border border-slate-200">{t.priority || 'Medium'}</span>
                    </div>
                  </div>
                  <p className="text-sm text-slate-600">{t.description}</p>

                  <div className="text-xs text-slate-500 grid grid-cols-2 gap-2 pt-2">
                    <div>Type: <span className="font-medium text-slate-700">{t.taskType || 'N/A'}</span></div>
                    {t.equipment?.name && (<div>Equipment: <span className="font-medium text-slate-700">{t.equipment.name}</span></div>)}
                    {t.dueDate && (<div>Due: <span className="font-medium text-slate-700">{new Date(t.dueDate).toLocaleDateString()}</span></div>)}
                    {t.assignedTo?.name && (<div>Assigned to: <span className="font-medium text-slate-700">{t.assignedTo.name}</span></div>)}
                  </div>

                  {/* Status dropdown */}
                  <div className="pt-3">
                    <select
                      value={t.status}
                      onChange={(e) => updateStatus(t._id, e.target.value)}
                      className="w-full py-2 px-3 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 text-slate-700"
                    >
                      <option value="Pending">Pending</option>
                      <option value="In Progress">In Progress</option>
                      <option value="Completed">Completed</option>
                      <option value="Cancelled">Cancelled</option>
                    </select>
                  </div>

                  <div className="flex items-center gap-2 pt-3">
                    {/* Technician: Add Work visible for ALL assigned tasks */}
                    {user?.role === 'Technician' && String(t.assignedTo?._id || t.assignedTo) === String(user._id) && (
                      <button
                        onClick={() => openWork(t)}
                        className="inline-flex items-center gap-2 px-3 py-2 rounded-lg border border-slate-300 hover:bg-slate-50 text-slate-700"
                        title="Add work details"
                      >
                        <Wrench className="w-4 h-4" />
                        Add Work
                      </button>
                    )}

                    {/* Engineer/Admin: Certify button when needed */}
                    {['Engineer', 'Admin'].includes(user?.role) && t.needsCertification && (
                      <button
                        onClick={() => certify(t._id)}
                        className="inline-flex items-center gap-2 px-3 py-2 rounded-lg bg-green-600 hover:bg-green-700 text-white"
                        title="Certify task and update equipment status"
                      >
                        <Check className="w-4 h-4" />
                        Certify
                      </button>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Work Modal */}
      {workOpen && (
        <div className="fixed inset-0 bg-black/30 flex items-center justify-center z-50">
          <div className="bg-white rounded-xl shadow-xl max-w-2xl w-full p-6">
            <div className="flex items-center justify-between mb-3">
              <h3 className="text-xl font-semibold">Add Work • {workTask?.title}</h3>
              <button onClick={() => setWorkOpen(false)} className="text-slate-500 hover:text-slate-700">
                <X className="w-5 h-5" />
              </button>
            </div>

            <form onSubmit={saveWork} className="space-y-4">
              <div>
                <label className="block mb-1 font-medium">Fault Description</label>
                <textarea
                  className="w-full border border-slate-300 rounded-lg px-3 py-2"
                  rows={3}
                  value={workForm.faultDescription}
                  onChange={(e) => setWorkForm(prev => ({ ...prev, faultDescription: e.target.value }))}
                />
              </div>

              <div>
                <label className="block mb-1 font-medium">Repair Details</label>
                <textarea
                  className="w-full border border-slate-300 rounded-lg px-3 py-2"
                  rows={4}
                  value={workForm.repairDetails}
                  onChange={(e) => setWorkForm(prev => ({ ...prev, repairDetails: e.target.value }))}
                />
              </div>

              <div>
                <label className="block mb-2 font-medium">Spare Parts Used/Required</label>
                <div className="space-y-2">
                  {workForm.sparePartsUsed.map((p, idx) => (
                    <div key={idx} className="grid grid-cols-12 gap-2">
                      <input
                        className="col-span-5 border border-slate-300 rounded-lg px-3 py-2"
                        placeholder="Part name"
                        value={p.name}
                        onChange={(e) => {
                          const v = e.target.value;
                          setWorkForm(prev => {
                            const arr = [...prev.sparePartsUsed];
                            arr[idx] = { ...arr[idx], name: v };
                            return { ...prev, sparePartsUsed: arr };
                          });
                        }}
                      />
                      <input
                        type="number"
                        min="1"
                        className="col-span-2 border border-slate-300 rounded-lg px-3 py-2"
                        placeholder="Qty"
                        value={p.quantity}
                        onChange={(e) => {
                          const v = Math.max(1, Number(e.target.value || 1));
                          setWorkForm(prev => {
                            const arr = [...prev.sparePartsUsed];
                            arr[idx] = { ...arr[idx], quantity: v };
                            return { ...prev, sparePartsUsed: arr };
                          });
                        }}
                      />
                      <input
                        className="col-span-5 border border-slate-300 rounded-lg px-3 py-2"
                        placeholder="Description"
                        value={p.description}
                        onChange={(e) => {
                          const v = e.target.value;
                          setWorkForm(prev => {
                            const arr = [...prev.sparePartsUsed];
                            arr[idx] = { ...arr[idx], description: v };
                            return { ...prev, sparePartsUsed: arr };
                          });
                        }}
                      />
                    </div>
                  ))}
                </div>
                <button
                  type="button"
                  onClick={() => setWorkForm(prev => ({ ...prev, sparePartsUsed: [...prev.sparePartsUsed, { name: '', quantity: 1, description: '' }] }))}
                  className="mt-2 inline-flex items-center gap-1 text-blue-600 hover:text-blue-700"
                >
                  <Plus className="w-4 h-4" /> Add Part
                </button>
              </div>

              <div className="flex justify-end gap-3 pt-2">
                <button type="button" onClick={() => setWorkOpen(false)} className="px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-50">
                  Cancel
                </button>
                <button type="submit" className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg">
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Tasks;
