// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

// Auth
import Login from './components/auth/Login';
import Register from './components/auth/Register';

// Layout
import Navbar from './components/layout/Navbar';
import Sidebar from './components/layout/Sidebar';

// Routing
import PrivateRoute from './components/routing/PrivateRoute';

// Pages
import Dashboard from './pages/Dashboard';
import Equipment from './pages/Equipment';
import Tasks from './pages/Tasks';
import Reports from './pages/Reports';
import Settings from './pages/Settings';
import NotFound from './pages/NotFound';

// Axios base config
axios.defaults.baseURL = 'http://localhost:5000/api';
axios.defaults.withCredentials = true;

// Axios interceptors
axios.interceptors.request.use(
  request => {
    const token = localStorage.getItem('token');
    if (token) request.headers['Authorization'] = `Bearer ${token}`;
    return request;
  },
  error => Promise.reject(error)
);

axios.interceptors.response.use(
  response => response,
  error => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

function App() {
  const [user, setUser] = useState(null);
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [loading, setLoading] = useState(true);
  const [redirectPath, setRedirectPath] = useState('/dashboard');

  const location = useLocation();
  const navigate = useNavigate();

  // Check auth on load
  useEffect(() => {
    const initializeAuth = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          const res = await axios.get('/auth/me', {
            headers: { Authorization: `Bearer ${token}` }
          });
          if (res.data?.user) {
            setUser(res.data.user);
          } else {
            localStorage.removeItem('token');
          }
        } catch {
          localStorage.removeItem('token');
        }
      }
      setLoading(false);
    };

    initializeAuth();
  }, []);

  // Handle login
  const login = (userData) => {
    if (userData?.token && userData?.user) {
      localStorage.setItem('token', userData.token);
      setUser(userData.user);
      axios.defaults.headers.common['Authorization'] = `Bearer ${userData.token}`;
      navigate(redirectPath || '/dashboard', { replace: true });
    }
  };

  // Handle logout
  const logout = () => {
    setUser(null);
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    navigate('/login', { replace: true });
  };

  // Store the page the user tried to visit before login
  useEffect(() => {
    if (!user && location.pathname !== '/login' && location.pathname !== '/register') {
      setRedirectPath(location.pathname);
    }
  }, [location, user]);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="text-center">
          <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-teal-600 border-r-transparent"></div>
          <p className="mt-4 text-gray-600">Loading application...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-gray-50">
      {user && <Sidebar currentPage={currentPage} />}
      <div className={user ? "flex-1 flex flex-col overflow-hidden" : "w-full"}>
        {user && <Navbar user={user} logout={logout} />}
        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50">
          <div className="container mx-auto px-6 py-8">
            <Routes>
              {/* Public */}
              <Route
                path="/login"
                element={!user ? <Login login={login} /> : <Navigate to={redirectPath || '/dashboard'} replace />}
              />
              <Route
                path="/register"
                element={!user ? <Register login={login} /> : <Navigate to={redirectPath || '/dashboard'} replace />}
              />

              {/* Default */}
              <Route path="/" element={<Navigate to={user ? "/dashboard" : "/login"} replace />} />

              {/* Private */}
              <Route path="/dashboard" element={<PrivateRoute user={user}><Dashboard user={user} setCurrentPage={setCurrentPage} /></PrivateRoute>} />
              <Route path="/tasks" element={<PrivateRoute user={user}><Tasks user={user} setCurrentPage={setCurrentPage} /></PrivateRoute>} />
              <Route path="/equipment" element={<PrivateRoute user={user}><Equipment user={user} setCurrentPage={setCurrentPage} /></PrivateRoute>} />
              <Route path="/reports" element={<PrivateRoute user={user}><Reports user={user} setCurrentPage={setCurrentPage} /></PrivateRoute>} />
              <Route path="/settings" element={<PrivateRoute user={user}><Settings user={user} setCurrentPage={setCurrentPage} /></PrivateRoute>} />

              {/* Not found */}
              <Route path="*" element={<NotFound />} />
            </Routes>
          </div>
        </main>
      </div>
    </div>
  );
}

export default function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}
