// frontend/src/components/tasks/Tasks.js
import React, { useEffect, useMemo, useState } from 'react';
import io from 'socket.io-client';
import api from '../../utils/api';
import { ClipboardCheck, Users, CheckCircle, Clock, ArrowRight, X, Search, AlertCircle, Plus, Wrench } from 'lucide-react';

const socket = io('http://localhost:5000', { transports: ['websocket'] });

export default function Tasks() {
  const user = JSON.parse(localStorage.getItem('user'));
  const [tasks, setTasks] = useState([]);
  const [technicians, setTechnicians] = useState([]);
  const [equipments, setEquipments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedTechnicianId, setSelectedTechnicianId] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 9;

  // Assign modal
  const [showAssign, setShowAssign] = useState(false);
  const [assignData, setAssignData] = useState({
    title: '', description: '',
    taskType: '', priority: 'Low',
    assignedTo: '', equipment: '', dueDate: ''
  });

  // Add Work modal
  const [workOpen, setWorkOpen] = useState(false);
  const [workTask, setWorkTask] = useState(null);
  const [workForm, setWorkForm] = useState({
    faultDescription: '', repairDetails: '', sparePartsUsed: [{ name: '', quantity: 1, description: '' }]
  });

  /* ----------- load base data ----------- */
  const fetchTasks = async () => {
    try {
      setLoading(true);
      setError('');
      const res = await api.get('/tasks?includePreventive=false'); // server will scope tech to own tasks
      setTasks(res.data.tasks || []);
    } catch (e) {
      setError(e.response?.data?.message || 'Failed to load tasks');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchTasks(); }, []);

  useEffect(() => {
    const refetch = () => fetchTasks();
    ['task:created','task:updated','task:workUpdated','task:deleted','task:certified'].forEach(evt => socket.on(evt, refetch));
    return () => ['task:created','task:updated','task:workUpdated','task:deleted','task:certified'].forEach(evt => socket.off(evt, refetch));
  }, []);

  useEffect(() => {
    if (user?.role === 'Engineer') {
      api.get('/users/preapproved/technicians?approved=any').then(r => {
        if (r.data.success) setTechnicians(r.data.technicians);
      }).catch(()=>{});
      api.get('/equipment').then(r => {
        if (r.data.success) setEquipments(r.data.equipment || []);
      }).catch(()=>{});
    }
  }, [user?.role]);

  /* ----------- filtering (client) ----------- */
  const filtered = useMemo(() => {
    let out = tasks;
    if (user?.role === 'Engineer' && selectedTechnicianId) {
      out = out.filter(t => t.assignedTo?._id === selectedTechnicianId);
    }
    if (searchTerm) {
      const q = searchTerm.toLowerCase();
      out = out.filter(t =>
        (t.title||'').toLowerCase().includes(q) ||
        (t.description||'').toLowerCase().includes(q) ||
        (t.assignedTo?.name||'').toLowerCase().includes(q) ||
        (t.equipment?.name||'').toLowerCase().includes(q)
      );
    }
    return out;
  }, [tasks, selectedTechnicianId, searchTerm, user?.role]);

  const start = (currentPage - 1) * itemsPerPage;
  const pageTasks = filtered.slice(start, start + itemsPerPage);
  const totalPages = Math.ceil(filtered.length / itemsPerPage);
  useEffect(() => setCurrentPage(1), [searchTerm, selectedTechnicianId]);

  /* ----------- helpers ----------- */
  const statusColor = s =>
    s==='Completed' ? 'bg-green-100 text-green-800 border border-green-200'
    : s==='In Progress' ? 'bg-blue-100 text-blue-800 border border-blue-200'
    : s==='Pending' ? 'bg-yellow-100 text-yellow-800 border border-yellow-200'
    : s==='Cancelled' ? 'bg-red-100 text-red-800 border border-red-200'
    : 'bg-gray-100 text-gray-800 border border-gray-200';

  const statusIcon = s =>
    s==='Completed' ? <CheckCircle className="w-4 h-4"/> :
    s==='In Progress' ? <ArrowRight className="w-4 h-4"/> :
    s==='Pending' ? <Clock className="w-4 h-4"/> : <X className="w-4 h-4"/>;

  /* ----------- actions ----------- */
  const updateStatus = async (taskId, status) => {
    try {
      await api.put(`/tasks/${taskId}/status`, { status });
      setSuccess('Status updated');
      setTimeout(()=>setSuccess(''),1500);
    } catch (e) {
      setError(e.response?.data?.message || 'Failed to update status');
    }
  };

  const certify = async (taskId) => {
    try {
      await api.put(`/tasks/${taskId}/certify`);
      setSuccess('Task certified and inventory updated');
      setTimeout(()=>setSuccess(''),1500);
    } catch (e) {
      setError(e.response?.data?.message || 'Certification failed');
    }
  };

  const openWork = (t) => {
    setWorkTask(t);
    setWorkForm({
      faultDescription: t.faultDescription || '',
      repairDetails: t.comments || '',
      sparePartsUsed: (t.spareParts?.length ? t.spareParts : [{ name: '', quantity: 1, description: '' }])
    });
    setWorkOpen(true);
  };

  const saveWork = async (e) => {
    e.preventDefault();
    if (!workTask) return;
    try {
      await api.put(`/tasks/${workTask._id}/work`, {
        faultDescription: workForm.faultDescription,
        repairDetails: workForm.repairDetails,
        sparePartsUsed: workForm.sparePartsUsed.filter(p=>p.name?.trim())
      });
      setWorkOpen(false);
      setWorkTask(null);
      setSuccess('Work saved (awaiting certification)');
      setTimeout(()=>setSuccess(''),1500);
    } catch (e) {
      setError(e.response?.data?.message || 'Failed to save work');
    }
  };

  const addSpare = () => setWorkForm(f => ({...f, sparePartsUsed:[...f.sparePartsUsed,{name:'',quantity:1,description:''}]}));
  const setSpare = (i, key, val) => setWorkForm(f => {
    const next = [...f.sparePartsUsed]; next[i] = {...next[i],[key]: key==='quantity'?Number(val):val}; return {...f, sparePartsUsed: next};
  });
  const removeSpare = (i) => setWorkForm(f => ({...f, sparePartsUsed: f.sparePartsUsed.filter((_,idx)=>idx!==i)}));

  const createTask = async (e) => {
    e.preventDefault();
    try {
      const payload = { ...assignData };
      if (!payload.assignedTo) delete payload.assignedTo;
      const res = await api.post('/tasks', payload);
      if (res.data.success) {
        setShowAssign(false);
        setAssignData({ title:'', description:'', taskType:'', priority:'Low', assignedTo:'', equipment:'', dueDate:'' });
        setSuccess('Task assigned');
        setTimeout(()=>setSuccess(''),1500);
      }
    } catch (e2) {
      setError(e2.response?.data?.message || 'Failed to assign task');
    }
  };

  /* ----------- UI ----------- */
  if (loading) {
    return (
      <div className="min-h-screen p-8">
        <div className="bg-white rounded-2xl p-12 border shadow text-center">
          <div className="inline-block h-10 w-10 animate-spin rounded-full border-4 border-solid border-blue-600 border-r-transparent"></div>
          <div className="mt-3 text-slate-600">Loading tasks</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-8 px-4">
      <div className="max-w-7xl mx-auto">
        <div className="mb-8 flex flex-col md:flex-row md:justify-between md:items-center gap-4">
          <div>
            <h1 className="text-3xl font-bold text-slate-800 flex items-center gap-3 mb-1">
              <div className="bg-gradient-to-r from-blue-600 to-green-500 p-2 rounded-lg text-white">
                <ClipboardCheck className="w-6 h-6" />
              </div>
              Task Management
            </h1>
            <p className="text-slate-600">Manage and track maintenance tasks efficiently</p>
          </div>

          {user?.role === 'Engineer' && (
            <button
              onClick={() => setShowAssign(true)}
              className="inline-flex items-center gap-2 px-5 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-xl"
            >
              <Plus className="w-5 h-5" />
              Assign Task
            </button>
          )}
        </div>

        {/* search & filter */}
        <div className="flex flex-col lg:flex-row gap-4 mb-6">
          <div className="relative flex-1">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none"><Search className="w-5 h-5 text-slate-400"/></div>
            <input
              value={searchTerm}
              onChange={e=>setSearchTerm(e.target.value)}
              placeholder="Search tasks by description, title, equipment, or technician"
              className="w-full pl-10 pr-4 py-3 border border-slate-300 rounded-xl focus:ring-2 focus:ring-blue-500"
            />
          </div>
          {user?.role === 'Engineer' && (
            <div className="relative w-64">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none"><Users className="w-5 h-5 text-slate-400"/></div>
              <select
                value={selectedTechnicianId}
                onChange={e=>setSelectedTechnicianId(e.target.value)}
                className="pl-10 pr-8 py-3 border border-slate-300 rounded-xl bg-white"
              >
                <option value="">All Technicians</option>
                {technicians.map(t=> <option key={t._id} value={t._id}>{t.name}</option>)}
              </select>
            </div>
          )}
        </div>

        {success && <div className="mb-4 px-4 py-2 rounded bg-green-100 text-green-700">{success}</div>}
        {error && <div className="mb-4 px-4 py-2 rounded bg-red-100 text-red-700 flex items-center gap-2"><AlertCircle className="w-5 h-5"/>{error}</div>}

        <div className="mb-4 text-sm text-slate-600 bg-white rounded-xl p-4 border flex justify-between">
          <div><span className="font-medium">{filtered.length}</span> task{filtered.length!==1?'s':''} found</div>
          {totalPages>1 && <div className="text-sm text-slate-500">Page {currentPage} of {totalPages}</div>}
        </div>

        {filtered.length===0 ? (
          <div className="bg-white rounded-2xl p-12 border shadow text-center text-slate-500">No tasks found</div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {pageTasks.map(t=>(
              <div key={t._id} className={`bg-white rounded-2xl border shadow hover:shadow-lg transition overflow-hidden ${user?.role==='Technician' && String(t.assignedTo?._id)===String(user._id) ? 'ring-1 ring-blue-300' : ''}`}>
                <div className={`flex items-center gap-2 px-4 py-2 ${statusColor(t.status)}`}>
                  {statusIcon(t.status)} <span className="text-sm font-semibold">{t.status}</span>
                  {t.needsCertification && <span className="ml-auto text-[11px] px-2 py-0.5 rounded bg-indigo-100 text-indigo-700 border border-indigo-200">Awaiting Certification</span>}
                </div>
                <div className="p-5 space-y-2">
                  <div className="text-lg font-semibold text-slate-800">{t.title}</div>
                  <div className="text-sm text-slate-600">{t.description}</div>
                  <div className="text-xs text-slate-500">Type: <b>{t.taskType||'N/A'}</b>  Priority: <b>{t.priority}</b></div>
                  <div className="text-xs text-slate-600">Equipment: <b>{t.equipment?.name||'N/A'}</b></div>

                  <div className="mt-3 flex items-center gap-2">
                    {/* status dropdown (tech on own tasks, engineer on all) */}
                    {(user?.role==='Engineer' || (user?.role==='Technician' && String(t.assignedTo?._id)===String(user._id))) && (
                      <select value={t.status} onChange={e=>updateStatus(t._id, e.target.value)} className="flex-1 py-2 px-3 border rounded-lg">
                        <option>Pending</option>
                        <option>In Progress</option>
                        <option>Completed</option>
                        <option>Cancelled</option>
                      </select>
                    )}

                    {/* Add Work */}
                    {(user?.role==='Engineer' || (user?.role==='Technician' && String(t.assignedTo?._id)===String(user._id))) && (
                      <button onClick={()=>openWork(t)} className="px-3 py-2 bg-slate-800 text-white rounded-lg flex items-center gap-1"><Wrench className="w-4 h-4"/>Add Work</button>
                    )}

                    {/* Certify (engineer only, when flagged) */}
                    {user?.role==='Engineer' && t.needsCertification && (
                      <button onClick={()=>certify(t._id)} className="px-3 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg">Certify</button>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* pagination */}
        {totalPages>1 && (
          <div className="mt-8 flex justify-center gap-2">
            <button disabled={currentPage===1} onClick={()=>setCurrentPage(p=>Math.max(1,p-1))} className="px-3 py-2 border rounded-lg disabled:opacity-50">Prev</button>
            {Array.from({length: totalPages}, (_,i)=>i+1).map(n=>(
              <button key={n} onClick={()=>setCurrentPage(n)} className={`px-3 py-2 border rounded-lg ${currentPage===n?'bg-blue-600 text-white':''}`}>{n}</button>
            ))}
            <button disabled={currentPage===totalPages} onClick={()=>setCurrentPage(p=>Math.min(totalPages,p+1))} className="px-3 py-2 border rounded-lg disabled:opacity-50">Next</button>
          </div>
        )}
      </div>

      {/* Assign Task Modal */}
      {showAssign && user?.role==='Engineer' && (
        <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl w-full max-w-2xl p-6 max-h-[85vh] overflow-y-auto">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold">Assign New Task</h3>
              <button onClick={()=>setShowAssign(false)} className="text-slate-500 hover:text-slate-700"><X className="w-5 h-5"/></button>
            </div>

            <form onSubmit={createTask} className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-1">Title *</label>
                <input className="w-full border rounded-lg px-3 py-2" value={assignData.title} onChange={e=>setAssignData(d=>({...d,title:e.target.value}))} required />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Description</label>
                <textarea className="w-full border rounded-lg px-3 py-2" rows={3} value={assignData.description} onChange={e=>setAssignData(d=>({...d,description:e.target.value}))}/>
              </div>
              <div className="grid md:grid-cols-3 gap-3">
                <div>
                  <label className="block text-sm font-medium mb-1">Task Type *</label>
                  <select className="w-full border rounded-lg px-3 py-2" value={assignData.taskType} onChange={e=>setAssignData(d=>({...d,taskType:e.target.value}))} required>
                    <option value="">Select</option>
                    <option>Repair</option><option>Calibrate</option><option>Assess</option><option>Inspect</option><option>Install</option>
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-1">Priority</label>
                  <select className="w-full border rounded-lg px-3 py-2" value={assignData.priority} onChange={e=>setAssignData(d=>({...d,priority:e.target.value}))}>
                    <option>Low</option><option>Medium</option><option>High</option><option>Urgent</option>
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-1">Due Date</label>
                  <input type="date" className="w-full border rounded-lg px-3 py-2" value={assignData.dueDate} onChange={e=>setAssignData(d=>({...d,dueDate:e.target.value}))}/>
                </div>
              </div>
              <div className="grid md:grid-cols-2 gap-3">
                <div>
                  <label className="block text-sm font-medium mb-1">Assign To (Technician)</label>
                  <select className="w-full border rounded-lg px-3 py-2" value={assignData.assignedTo} onChange={e=>setAssignData(d=>({...d,assignedTo:e.target.value}))}>
                    <option value="">Unassigned</option>
                    {technicians.map(t=> <option key={t._id} value={t._id}>{t.name}</option>)}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-1">Equipment *</label>
                  <select required className="w-full border rounded-lg px-3 py-2" value={assignData.equipment} onChange={e=>setAssignData(d=>({...d,equipment:e.target.value}))}>
                    <option value="">Select Equipment</option>
                    {equipments.map(e => <option key={e._id} value={e._id}>{e.name}  {e.serialNumber}</option>)}
                  </select>
                </div>
              </div>

              <div className="flex justify-end gap-3 pt-2">
                <button type="button" onClick={()=>setShowAssign(false)} className="px-4 py-2 border rounded-lg">Cancel</button>
                <button type="submit" className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg">Assign</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Add Work Modal */}
      {workOpen && workTask && (
        <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl w-full max-w-2xl p-6 max-h-[85vh] overflow-y-auto">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold">Add Work  {workTask.title}</h3>
              <button onClick={()=>{setWorkOpen(false);setWorkTask(null);}} className="text-slate-500 hover:text-slate-700"><X className="w-5 h-5"/></button>
            </div>

            <form onSubmit={saveWork} className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-1">Fault Description</label>
                <textarea className="w-full border rounded-lg px-3 py-2" rows={3} value={workForm.faultDescription} onChange={e=>setWorkForm(f=>({...f,faultDescription:e.target.value}))}/>
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Repair Details</label>
                <textarea className="w-full border rounded-lg px-3 py-2" rows={3} value={workForm.repairDetails} onChange={e=>setWorkForm(f=>({...f,repairDetails:e.target.value}))}/>
              </div>
              <div>
                <label className="block text-sm font-medium mb-2">Spare Parts Used/Required</label>
                <div className="space-y-3">
                  {workForm.sparePartsUsed.map((p,i)=>(
                    <div key={i} className="grid md:grid-cols-6 gap-3">
                      <input className="md:col-span-2 border rounded-lg px-3 py-2" placeholder="Name" value={p.name} onChange={e=>setSpare(i,'name',e.target.value)}/>
                      <input className="md:col-span-1 border rounded-lg px-3 py-2" type="number" min="1" value={p.quantity} onChange={e=>setSpare(i,'quantity',e.target.value)}/>
                      <input className="md:col-span-2 border rounded-lg px-3 py-2" placeholder="Description" value={p.description} onChange={e=>setSpare(i,'description',e.target.value)}/>
                      <button type="button" onClick={()=>removeSpare(i)} className="md:col-span-1 px-3 py-2 border border-red-200 rounded-lg text-red-600 hover:bg-red-50">Remove</button>
                    </div>
                  ))}
                </div>
                <button type="button" onClick={addSpare} className="mt-3 px-3 py-2 border rounded-lg">+ Add Spare</button>
              </div>

              <div className="flex justify-end gap-3 pt-2">
                <button type="button" onClick={()=>{setWorkOpen(false);setWorkTask(null);}} className="px-4 py-2 border rounded-lg">Cancel</button>
                <button type="submit" className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg">Save</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
