// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import axios from 'axios';

// Components
import Sidebar from './components/layout/Sidebar';
import Navbar from './components/layout/Navbar';
import PrivateRoute from './components/routing/PrivateRoute';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import Dashboard from './components/pages/Dashboard';
import Tasks from './components/pages/Tasks';
import Equipment from './components/pages/Equipment';
import Reports from './components/pages/Reports';
import Settings from './components/pages/Settings';
import NotFound from './components/pages/NotFound';

// Axios defaults
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
const token = localStorage.getItem('token');
if (token) {
  axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
}

function App() {
  const [user, setUser] = useState(null);
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [loading, setLoading] = useState(true);

  const location = useLocation();

  useEffect(() => {
    const initializeAuth = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          const res = await axios.get('/auth/me', { headers: { Authorization: `Bearer ${token}` } });
          if (res.data?.user) {
            setUser(res.data.user);
          } else {
            localStorage.removeItem('token');
            delete axios.defaults.headers.common['Authorization'];
          }
        } catch {
          localStorage.removeItem('token');
          delete axios.defaults.headers.common['Authorization'];
        }
      }
      setLoading(false);
    };
    initializeAuth();
  }, []);

  const login = (userData) => {
    if (userData?.token && userData?.user) {
      localStorage.setItem('token', userData.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${userData.token}`;
      setUser(userData.user);
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setUser(null);
    window.location.href = '/login';
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="text-center">
          <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-teal-600 border-r-transparent"></div>
          <p className="mt-4 text-gray-600">Loading application...</p>
        </div>
      </div>
    );
  }

  const redirectPath = location.state?.from || '/dashboard';

  return (
    <div className="flex h-screen bg-gray-50">
      {user && <Sidebar currentPage={currentPage} />}
      <div className={user ? "flex-1 flex flex-col overflow-hidden" : "w-full"}>
        {user && <Navbar user={user} logout={logout} />}
        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50">
          <div className="container mx-auto px-6 py-8">
            <Routes>
              {/* Auth Routes */}
              <Route path="/login" element={!user ? <Login login={login} /> : <Navigate to={redirectPath} replace />} />
              <Route path="/register" element={!user ? <Register login={login} /> : <Navigate to={redirectPath} replace />} />

              {/* Default redirect */}
              <Route path="/" element={user ? <Navigate to="/dashboard" replace /> : <Navigate to="/login" replace />} />

              {/* Protected Routes */}
              <Route
                path="/dashboard"
                element={
                  <PrivateRoute user={user}>
                    <Dashboard user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                }
              />
              <Route
                path="/tasks"
                element={
                  <PrivateRoute user={user}>
                    <Tasks user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                }
              />
              <Route
                path="/equipment"
                element={
                  <PrivateRoute user={user}>
                    <Equipment user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                }
              />
              <Route
                path="/reports"
                element={
                  <PrivateRoute user={user}>
                    <Reports user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                }
              />
              <Route
                path="/settings"
                element={
                  <PrivateRoute user={user}>
                    <Settings user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                }
              />

              {/* 404 */}
              <Route path="*" element={<NotFound />} />
            </Routes>
          </div>
        </main>
      </div>
    </div>
  );
}

// Wrap App in Router to keep layout & routes
export default function WrappedApp() {
  return (
    <Router>
      <App />
    </Router>
  );
}
