// frontend/src/components/equipment/Equipment.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Equipment = ({ user }) => {
  const [equipment, setEquipment] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEquipment = async () => {
      try {
        const res = await axios.get('/equipment');
        setEquipment(res.data.data); // Assuming the backend returns { success: true, data: [] }
        setLoading(false);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch equipment');
        setLoading(false);
      }
    };

    fetchEquipment();
  }, []);

  return (
    <div>
      <h1>Equipment Inventory</h1>
      <button onClick={() => console.log('Add Equipment')}>+ Add Equipment</button>
      {loading && <p>Loading...</p>}
      {error && <p>Error: {error}</p>}
      {!loading && !error && equipment.length === 0 && <p>No equipment found.</p>}
      {!loading && !error && equipment.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Brand/Model</th>
              <th>ID/SERIAL</th>
              {/* Add other columns as needed */}
            </tr>
          </thead>
          <tbody>
            {equipment.map(eq => (
              <tr key={eq.id}>
                <td>{eq.name}</td>
                <td>{`${eq.brand} ${eq.model}`}</td>
                <td>{eq.serialNumber}</td>
                {/* Add other rows as needed */}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Equipment;
