// frontend/src/components/dashboard/Dashboard.js
import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import socket from '../../utils/socket'; // <-- real-time updates

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const user = JSON.parse(localStorage.getItem('user'));

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      setError('');
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          Authorization: `Bearer ${token}`
        }
      };

      const response = await axios.get('http://localhost:5000/api/dashboard', config);
      setDashboardData(response.data.dashboard);
    } catch (err) {
      setError('Failed to fetch dashboard data');
      console.error('Error fetching dashboard:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDashboardData();
  }, []);

  // ðŸ”” Real-time: refetch on any equipment/task change
  useEffect(() => {
    const refetch = () => fetchDashboardData();

    socket.on('equipment:updated', refetch);
    socket.on('equipment:deleted', refetch);
    socket.on('task:created', refetch);
    socket.on('task:updated', refetch);
    socket.on('task:completed', refetch);
    socket.on('task:deleted', refetch);

    return () => {
      socket.off('equipment:updated', refetch);
      socket.off('equipment:deleted', refetch);
      socket.off('task:created', refetch);
      socket.off('task:updated', refetch);
      socket.off('task:completed', refetch);
      socket.off('task:deleted', refetch);
    };
  }, []);

  // Helpers
  const totalEquipment = dashboardData?.equipment?.total || 0;
  const pct = (count) => {
    if (!totalEquipment) return 0;
    return Math.min(100, Math.round((count / totalEquipment) * 100));
  };

  // Build dynamic bars list (keeps your 3 statuses visual)
  const bars = useMemo(() => {
    const eq = dashboardData?.equipment || {};
    return [
      {
        status: 'Serviceable',
        count: eq.serviceable || 0,
        percentage: pct(eq.serviceable || 0),
        color: 'bg-green-500'
      },
      {
        status: 'Under Maintenance',
        count: eq.maintenance || 0,
        percentage: pct(eq.maintenance || 0),
        color: 'bg-yellow-500'
      },
      {
        status: 'Unserviceable',
        count: eq.unserviceable || 0,
        percentage: pct(eq.unserviceable || 0),
        color: 'bg-red-500'
      }
    ];
  }, [dashboardData, totalEquipment]);

  // Card component for consistent styling
  const StatCard = ({ title, value, icon, color, trend, trendValue }) => (
    <div className="bg-white rounded-2xl shadow-lg p-6 border border-slate-200 hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1">
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm font-medium text-slate-600 mb-1">{title}</p>
          <h3 className={`text-3xl font-bold ${color}`}>{value}</h3>
        </div>
        <div className={`p-3 rounded-xl bg-gradient-to-br ${color.replace('text-', 'from-')}50 ${color.replace('text-', 'to-')}100`}>
          {icon}
        </div>
      </div>
      {trend && (
        <div className="mt-4 pt-4 border-t border-slate-200">
          <div className="flex items-center">
            <svg
              className={`w-4 h-4 mr-1 ${trend === 'up' ? 'text-green-500' : 'text-red-500'}`}
              fill="currentColor"
              viewBox="0 0 20 20"
            >
              <path
                fillRule="evenodd"
                d={
                  trend === 'up'
                    ? 'M5.293 7.707a1 1 0 010-1.414l4-4a1 1 0 011.414 0l4 4a1 1 0 01-1.414 1.414L11 5.414V17a1 1 0 11-2 0V5.414L6.707 7.707a1 1 0 01-1.414 0z'
                    : 'M14.707 12.293a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 111.414-1.414L9 14.586V3a1 1 0 012 0v11.586l2.293-2.293a1 1 0 011.414 0z'
                }
                clipRule="evenodd"
              />
            </svg>
            <span className={`text-sm font-medium ${trend === 'up' ? 'text-green-600' : 'text-red-600'}`}>
              {trendValue}% from last month
            </span>
          </div>
        </div>
      )}
    </div>
  );

  // Quick action buttons
  const QuickAction = ({ icon, label, onClick, color }) => (
    <button
      onClick={onClick}
      className={`flex flex-col items-center p-4 rounded-xl bg-white shadow-md hover:shadow-lg transition-all duration-200 transform hover:scale-105 border ${color} hover:bg-opacity-10`}
    >
      {icon}
      <span className="mt-2 text-sm font-medium text-slate-700">{label}</span>
    </button>
  );

  // Recent activity from API (role-aware via backend)
  const recentTasks = dashboardData?.recentTasks || [];
  const fmtDate = (d) => {
    const dt = new Date(d);
    return Number.isNaN(dt.getTime()) ? 'â€”' : dt.toLocaleString();
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-12 px-4">
        <div className="max-w-7xl mx-auto">
          {/* Header */}
          <div className="text-center mb-12">
            <h1 className="text-4xl font-bold text-slate-800 mb-4">Dashboard</h1>
            <p className="text-slate-600 text-lg">Loading your Computerized Maintenance overview...</p>
          </div>

          {/* Loading Skeleton */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="bg-white rounded-2xl shadow-lg p-6 border border-slate-200 animate-pulse">
                <div className="flex items-center justify-between">
                  <div>
                    <div className="h-4 bg-slate-200 rounded w-24 mb-3"></div>
                    <div className="h-8 bg-slate-200 rounded w-16"></div>
                  </div>
                  <div className="h-12 w-12 bg-slate-200 rounded-xl"></div>
                </div>
              </div>
            ))}
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            <div className="lg:col-span-2">
              <div className="bg-white rounded-2xl shadow-lg p-8 border border-slate-200 animate-pulse">
                <div className="h-6 bg-slate-200 rounded w-48 mb-6"></div>
                <div className="space-y-4">
                  {[...Array(5)].map((_, i) => (
                    <div key={i} className="h-4 bg-slate-200 rounded"></div>
                  ))}
                </div>
              </div>
            </div>
            <div className="space-y-6">
              {[...Array(3)].map((_, i) => (
                <div key={i} className="bg-white rounded-2xl shadow-lg p-6 border border-slate-200 animate-pulse">
                  <div className="h-4 bg-slate-200 rounded w-32 mb-4"></div>
                  <div className="h-16 bg-slate-200 rounded"></div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-12 px-4">
        <div className="max-w-7xl mx-auto">
          <div className="bg-red-50 border border-red-200 rounded-2xl p-8 text-center">
            <svg className="w-16 h-16 text-red-400 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <h3 className="text-lg font-medium text-red-800 mb-2">Error Loading Dashboard</h3>
            <p className="text-red-700">{error}</p>
            <button
              onClick={() => window.location.reload()}
              className="mt-4 px-6 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors duration-200"
            >
              Retry
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-12 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="text-center mb-12">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-blue-600 to-green-500 rounded-full mb-4">
            <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0h10a2 2 0 002-2v-6a2 2 0 00-2-2H9a2 2 0 00-2 2v6a2 2 0 002 2z" />
            </svg>
          </div>
          <h1 className="text-4xl font-bold text-slate-800 mb-4">
            Welcome back {user?.name?.split(' ')[0]}!
          </h1>
          <p className="text-slate-600 text-lg">Your Computerized Maintenance Management overview</p>
        </div>

        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatCard
            title="Total Equipment"
            value={dashboardData?.equipment?.total || 0}
            color="text-blue-600"
            trend="up"
            trendValue="12"
            icon={
              <svg className="w-6 h-6 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2-2v2m8 0V6a2 2 0 012 2v6a2 2 0 01-2 2H8a2 2 0 01-2-2V8a2 2 0 012-2V6" />
              </svg>
            }
          />

          <StatCard
            title="Serviceable"
            value={dashboardData?.equipment?.serviceable || 0}
            color="text-green-600"
            trend="up"
            trendValue="8"
            icon={
              <svg className="w-6 h-6 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            }
          />

          <StatCard
            title="Under Maintenance"
            value={dashboardData?.equipment?.maintenance || 0}
            color="text-yellow-600"
            trend="down"
            trendValue="3"
            icon={
              <svg className="w-6 h-6 text-yellow-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z" />
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
              </svg>
            }
          />

          <StatCard
            title="Unserviceable"
            value={dashboardData?.equipment?.unserviceable || 0}
            color="text-red-600"
            trend="up"
            trendValue="2"
            icon={
              <svg className="w-6 h-6 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            }
          />
        </div>

        {/* Quick Actions */}
        <div className="mb-8">
          <h2 className="text-xl font-semibold text-slate-800 mb-4">Quick Actions</h2>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <QuickAction
              icon={
                <svg className="w-6 h-6 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                </svg>
              }
              label="Add Equipment"
              onClick={() => (window.location.href = '/add-equipment')}
              color="border-blue-100"
            />
            <QuickAction
              icon={
                <svg className="w-6 h-6 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                </svg>
              }
              label="View Inventory"
              onClick={() => (window.location.href = '/equipment')}
              color="border-green-100"
            />
            <QuickAction
              icon={
                <svg className="w-6 h-6 text-purple-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
              }
              label="Generate Report"
              onClick={() => alert('Report generation started')}
              color="border-purple-100"
            />
            <QuickAction
              icon={
                <svg className="w-6 h-6 text-orange-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-5 5v-5zM9 7H4l5-5v5zm6 10V7a2 2 0 00-2-2H7a2 2 0 00-2 2v10a2 2 0 002 2h6a2 2 0 002-2z" />
                </svg>
              }
              label="Maintenance Tasks"
              onClick={() => (window.location.href = '/tasks')}
              color="border-orange-100"
            />
          </div>
        </div>

        {/* Main Content Area */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Equipment Status Overview */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-2xl shadow-lg p-8 border border-slate-200">
              <h2 className="text-xl font-semibold text-slate-800 mb-6">Equipment Status Overview</h2>

              <div className="space-y-4">
                {bars.map((item) => (
                  <div key={item.status} className="flex items-center">
                    <div className="w-40 text-sm font-medium text-slate-700">{item.status}</div>
                    <div className="flex-1 mx-4">
                      <div className="h-2 bg-slate-200 rounded-full">
                        <div
                          className={`h-2 rounded-full ${item.color} transition-all duration-500 ease-out`}
                          style={{ width: `${item.percentage}%` }}
                        ></div>
                      </div>
                    </div>
                    <div className="w-16 text-sm text-slate-600 text-right">{item.count}</div>
                  </div>
                ))}
              </div>

              {/* Recent Activity (live) */}
              <div className="mt-8 pt-6 border-t border-slate-200">
                <h3 className="text-lg font-medium text-slate-800 mb-4">Recent Activity</h3>

                {recentTasks.length === 0 ? (
                  <div className="text-slate-500">No recent activity</div>
                ) : (
                  <div className="space-y-3">
                    {recentTasks.map((t) => (
                      <div key={t._id} className="flex items-start p-3 bg-slate-50 rounded-lg">
                        <div className="w-8 h-8 rounded-full flex items-center justify-center bg-slate-200">
                          <svg className="w-4 h-4 text-slate-700" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                          </svg>
                        </div>
                        <div className="ml-3">
                          <p className="text-sm font-medium text-slate-800">
                            {t.title}{' '}
                            <span className="text-slate-500">({t.status}{t.taskType ? ` â€¢ ${t.taskType}` : ''})</span>
                          </p>
                          <p className="text-xs text-slate-500">
                            {t.equipment ? `Equipment: ${t.equipment.name}${t.equipment.serialNumber ? ` â€¢ S/N: ${t.equipment.serialNumber}` : ''}` : 'No equipment'}
                            {t.assignedTo?.name ? ` â€¢ Tech: ${t.assignedTo.name}` : ''}
                            {` â€¢ ${fmtDate(t.updatedAt || t.createdAt)}`}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* User Stats and Information */}
          <div className="space-y-6">
            {/* User Profile Card */}
            <div className="bg-white rounded-2xl shadow-lg p-6 border border-slate-200">
              <div className="text-center">
                <div className="w-16 h-16 bg-gradient-to-r from-blue-600 to-green-500 rounded-full mx-auto mb-4 flex items-center justify-center text-white text-xl font-bold">
                  {user?.name?.charAt(0) || 'U'}
                </div>
                <h3 className="text-lg font-semibold text-slate-800">{user?.name}</h3>
                <p className="text-slate-600">{user?.role}</p>
                <div className="mt-4 pt-4 border-t border-slate-200">
                  <div className="flex justify-between text-sm">
                    <span className="text-slate-600">Role:</span>
                    <span className="font-medium">{user?.role}</span>
                  </div>
                  <div className="flex justify-between text-sm mt-2">
                    <span className="text-slate-600">Department:</span>
                    <span className="font-medium">BMED</span>
                  </div>
                  <div className="flex justify-between text-sm mt-2">
                    <span className="text-slate-600">Last Login:</span>
                    <span className="font-medium">Today</span>
                  </div>
                </div>
              </div>
            </div>

            {/* Tasks Summary */}
            <div className="bg-white rounded-2xl shadow-lg p-6 border border-slate-200">
              <h3 className="text-lg font-semibold text-slate-800 mb-4">Tasks Summary</h3>
              <div className="space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-slate-600">Total Tasks</span>
                  <span className="font-semibold text-blue-600">{dashboardData?.tasks?.total || 0}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-slate-600">Pending</span>
                  <span className="font-semibold text-yellow-600">{dashboardData?.tasks?.pending || 0}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-slate-600">In Progress</span>
                  <span className="font-semibold text-blue-600">{dashboardData?.tasks?.inProgress || 0}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-slate-600">Completed</span>
                  <span className="font-semibold text-green-600">{dashboardData?.tasks?.completed || 0}</span>
                </div>
              </div>
            </div>

            {/* System Status */}
            <div className="bg-white rounded-2xl shadow-lg p-6 border border-slate-200">
              <h3 className="text-lg font-semibold text-slate-800 mb-4">System Status</h3>
              <div className="space-y-3">
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-green-500 rounded-full mr-3"></div>
                  <span className="text-sm text-slate-700">Database Connected</span>
                </div>
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-green-500 rounded-full mr-3"></div>
                  <span className="text-sm text-slate-700">API Service Running</span>
                </div>
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-green-500 rounded-full mr-3"></div>
                  <span className="text-sm text-slate-700">Backup System Active</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Decorative Elements */}
        <div className="absolute top-20 -left-10 w-50 h-50 bg-blue-200 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-pulse"></div>
        <div
          className="absolute top-40 -right-10 w-50 h-50 bg-green-200 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-pulse"
          style={{ animationDelay: '2s' }}
        ></div>
      </div>
    </div>
  );
};

export default Dashboard;
