import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import AuthContext from '../../context/auth/authContext';

const Tasks = () => {
  const { user } = useContext(AuthContext);

  const [tasks, setTasks] = useState([]);
  const [technicians, setTechnicians] = useState([]);
  const [equipment, setEquipment] = useState([]);
  const [equipmentSearch, setEquipmentSearch] = useState('');
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    assignedTo: '',
    equipmentId: ''
  });

  // Fetch tasks
  useEffect(() => {
    fetchTasks();
    if (user && (user.role === 'Admin' || user.role === 'Engineer')) {
      fetchTechnicians();
    }
  }, [user]);

  const fetchTasks = async () => {
    try {
      const res = await axios.get('/api/tasks', { withCredentials: true });
      setTasks(res.data.tasks || []);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  const fetchTechnicians = async () => {
    try {
      const res = await axios.get('/api/staff/preapproved', { withCredentials: true });
      setTechnicians(res.data.technicians || []);
    } catch (err) {
      console.error('Error fetching technicians:', err);
    }
  };

  const searchEquipment = async (searchTerm) => {
    try {
      const res = await axios.get(`/api/equipment?search=${encodeURIComponent(searchTerm)}`, { withCredentials: true });
      setEquipment(res.data.equipment || []);
    } catch (err) {
      console.error('Error fetching equipment:', err);
    }
  };

  // Handle equipment search typing
  useEffect(() => {
    if (equipmentSearch.trim().length > 1) {
      searchEquipment(equipmentSearch);
    } else {
      setEquipment([]);
    }
  }, [equipmentSearch]);

  const onChange = e => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    if (!(user.role === 'Admin' || user.role === 'Engineer')) {
      alert('You are not authorized to assign tasks.');
      return;
    }

    try {
      await axios.post('/api/tasks', formData, { withCredentials: true });
      setFormData({ title: '', description: '', assignedTo: '', equipmentId: '' });
      setEquipmentSearch('');
      fetchTasks();
    } catch (err) {
      console.error('Error creating task:', err);
    }
  };

  return (
    <div className="tasks-page">
      <h1>Task Management</h1>

      {(user.role === 'Admin' || user.role === 'Engineer') && (
        <form onSubmit={onSubmit} className="task-form">
          <div>
            <label>Title</label>
            <input type="text" name="title" value={formData.title} onChange={onChange} required />
          </div>
          <div>
            <label>Description</label>
            <textarea name="description" value={formData.description} onChange={onChange} required />
          </div>
          <div>
            <label>Assign to Technician</label>
            <select name="assignedTo" value={formData.assignedTo} onChange={onChange} required>
              <option value="">-- Select Technician --</option>
              {technicians.map(tech => (
                <option key={tech._id} value={tech._id}>
                  {tech.name} ({tech.serviceNumber})
                </option>
              ))}
            </select>
          </div>
          <div>
            <label>Equipment</label>
            <input
              type="text"
              placeholder="Search equipment by name, model, or serial..."
              value={equipmentSearch}
              onChange={(e) => setEquipmentSearch(e.target.value)}
            />
            {equipment.length > 0 && (
              <select
                name="equipmentId"
                value={formData.equipmentId}
                onChange={onChange}
                required
              >
                <option value="">-- Select Equipment --</option>
                {equipment.map(eq => (
                  <option key={eq._id} value={eq._id}>
                    {eq.name} - {eq.model} ({eq.serialNumber})
                  </option>
                ))}
              </select>
            )}
          </div>
          <button type="submit">Assign Task</button>
        </form>
      )}

      <h2>Existing Tasks</h2>
      <ul className="task-list">
        {tasks.map(task => (
          <li key={task._id}>
            <strong>{task.title}</strong> - {task.description}
            {task.assignedTo && (
              <span> | Assigned to: {task.assignedTo.name}</span>
            )}
            {task.equipment && (
              <span> | Equipment: {task.equipment.name}</span>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Tasks;
