// frontend/src/components/tasks/Tasks.js
import React, { useEffect, useMemo, useState } from 'react';
import io from 'socket.io-client';
import api from '../../utils/api';
import {
  ClipboardCheck, Users, CheckCircle, Clock, ArrowRight, X, Search, AlertCircle, Plus, Wrench
} from 'lucide-react';

const socket = io('http://localhost:5000', { transports: ['websocket'] });

function Tasks({ user }) {
  const [tasks, setTasks] = useState([]);
  const [technicians, setTechnicians] = useState([]);
  const [equipments, setEquipments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [successMsg, setSuccessMsg] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedTechnicianId, setSelectedTechnicianId] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [updatingTaskId, setUpdatingTaskId] = useState(null);

  // Add Work modal state
  const [workModal, setWorkModal] = useState({ open: false, task: null });
  const [workForm, setWorkForm] = useState({
    faultDescription: '',
    repairDetails: '',
    sparePartsUsed: [{ name: '', quantity: 1, description: '' }]
  });

  // Assign Task modal
  const [showAssignModal, setShowAssignModal] = useState(false);
  const [newTaskData, setNewTaskData] = useState({
    title: '',
    description: '',
    taskType: '',
    priority: 'Low',
    assignedTo: '',
    equipment: '',
    dueDate: '',
  });

  const itemsPerPage = 8;

  // Fetch technicians for Engineer dropdown
  useEffect(() => {
    if (user.role === 'Engineer') {
      api.get('/users/preapproved/technicians?approved=any')
        .then(res => {
          if (res.data.success) setTechnicians(res.data.technicians);
        })
        .catch(() => setTechnicians([]));
    }
  }, [user.role]);

  // Fetch equipments for assignment (only if Engineer)
  useEffect(() => {
    if (user.role === 'Engineer') {
      api.get('/equipment')
        .then(res => {
          if (res.data.success) setEquipments(res.data.equipment || []);
        })
        .catch(() => setEquipments([]));
    }
  }, [user.role]);

  const fetchTasks = async () => {
    try {
      setLoading(true);
      setError('');
      // exclude preventive from Tasks board
      const res = await api.get('/tasks?includePreventive=false');
      setTasks(res.data.tasks || []);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to fetch tasks');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchTasks(); }, []);

  // Real-time: refresh on socket updates
  useEffect(() => {
    const onAnyUpdate = () => fetchTasks();
    socket.on('task:created', onAnyUpdate);
    socket.on('task:updated', onAnyUpdate);
    socket.on('task:workUpdated', onAnyUpdate);
    socket.on('task:deleted', onAnyUpdate);
    return () => {
      socket.off('task:created', onAnyUpdate);
      socket.off('task:updated', onAnyUpdate);
      socket.off('task:workUpdated', onAnyUpdate);
      socket.off('task:deleted', onAnyUpdate);
    };
  }, []);

  // Filter tasks
  const filteredTasks = useMemo(() => {
    let filtered = tasks;

    if (user.role === 'Engineer' && selectedTechnicianId) {
      filtered = filtered.filter(t => t.assignedTo?._id === selectedTechnicianId);
    }
    if (user.role === 'Technician') {
      filtered = filtered.filter(t => t.assignedTo?._id === user._id);
    }
    if (searchTerm) {
      const q = searchTerm.toLowerCase();
      filtered = filtered.filter(t =>
        (t.title || '').toLowerCase().includes(q) ||
        (t.description || '').toLowerCase().includes(q) ||
        (t.assignedTo?.name || '').toLowerCase().includes(q) ||
        (t.equipment?.name || '').toLowerCase().includes(q)
      );
    }
    return filtered;
  }, [tasks, selectedTechnicianId, user, searchTerm]);

  const currentTasks = useMemo(() => {
    const start = (currentPage - 1) * itemsPerPage;
    return filteredTasks.slice(start, start + itemsPerPage);
  }, [filteredTasks, currentPage]);
  const totalPages = Math.ceil(filteredTasks.length / itemsPerPage);

  useEffect(() => setCurrentPage(1), [searchTerm, selectedTechnicianId]);

  // Status style helpers
  const getStatusColor = (status) => {
    switch (status) {
      case 'Completed': return 'bg-green-100 text-green-800 border border-green-200';
      case 'In Progress': return 'bg-blue-100 text-blue-800 border border-blue-200';
      case 'Pending': return 'bg-yellow-100 text-yellow-800 border border-yellow-200';
      case 'Cancelled': return 'bg-red-100 text-red-800 border border-red-200';
      default: return 'bg-gray-100 text-gray-800 border border-gray-200';
    }
  };
  const getStatusIcon = (status) => {
    switch (status) {
      case 'Completed': return <CheckCircle className="w-4 h-4" />;
      case 'In Progress': return <ArrowRight className="w-4 h-4" />;
      case 'Pending': return <Clock className="w-4 h-4" />;
      case 'Cancelled': return <X className="w-4 h-4" />;
      default: return <Clock className="w-4 h-4" />;
    }
  };

  // ── Status change (Tech or Engineer)
  const handleStatusChange = async (taskId, newStatus) => {
    try {
      setUpdatingTaskId(taskId);
      await api.put(`/tasks/${taskId}/status`, { status: newStatus });
      setSuccessMsg('Task status updated (awaiting certification if from tech).');
      setTimeout(() => setSuccessMsg(''), 2500);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update task status');
    } finally {
      setUpdatingTaskId(null);
    }
  };

  // ── Add Work modal open
  const openWorkModal = (task) => {
    setWorkForm({
      faultDescription: task.faultDescription || '',
      repairDetails: task.comments || '',
      sparePartsUsed: (task.spareParts && task.spareParts.length)
        ? task.spareParts.map(s => ({ name: s.name || '', quantity: s.quantity || 1, description: s.description || '' }))
        : [{ name: '', quantity: 1, description: '' }]
    });
    setWorkModal({ open: true, task });
  };

  // ── Add Work submit
  const submitWork = async (e) => {
    e.preventDefault();
    if (!workModal.task) return;
    try {
      const payload = {
        faultDescription: workForm.faultDescription,
        repairDetails: workForm.repairDetails,
        sparePartsUsed: workForm.sparePartsUsed.filter(p => p.name?.trim())
      };
      await api.put(`/tasks/${workModal.task._id}/work`, payload);
      setWorkModal({ open: false, task: null });
      setSuccessMsg('Work details saved. Awaiting engineer certification.');
      setTimeout(() => setSuccessMsg(''), 2500);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to save work details');
    }
  };

  const addSpare = () => setWorkForm(f => ({ ...f, sparePartsUsed: [...f.sparePartsUsed, { name: '', quantity: 1, description: '' }] }));
  const updateSpare = (i, field, val) => {
    setWorkForm(f => {
      const next = [...f.sparePartsUsed];
      next[i] = { ...next[i], [field]: field === 'quantity' ? Number(val) : val };
      return { ...f, sparePartsUsed: next };
    });
  };
  const removeSpare = (i) => setWorkForm(f => ({ ...f, sparePartsUsed: f.sparePartsUsed.filter((_, idx) => idx !== i) }));

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-8 px-4">
        <div className="max-w-7xl mx-auto">
          <div className="bg-white rounded-2xl shadow-lg border border-slate-200 p-12 text-center">
            <div className="inline-block h-12 w-12 animate-spin rounded-full border-4 border-solid border-blue-600 border-r-transparent"></div>
            <p className="mt-4 text-xl text-slate-600">Loading tasks...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-8 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8 flex flex-col md:flex-row md:justify-between md:items-center gap-4">
          <div>
            <h1 className="text-3xl font-bold text-slate-800 flex items-center gap-3 mb-1">
              <div className="bg-gradient-to-r from-blue-600 to-green-500 p-2 rounded-lg text-white">
                <ClipboardCheck className="w-6 h-6" />
              </div>
              Task Management
            </h1>
            <p className="text-slate-600 mb-4">Manage and track maintenance tasks efficiently</p>
          </div>

          {/* Assign Task button for Engineers */}
          {user.role === 'Engineer' && (
            <button
              onClick={() => setShowAssignModal(true)}
              className="inline-flex items-center gap-2 px-5 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-xl transition"
              title="Assign New Task"
            >
              <Plus className="w-5 h-5" />
              Assign Task
            </button>
          )}
        </div>

        {/* Search & Technician Filter */}
        <div className="flex flex-col lg:flex-row gap-4 mb-6">
          {/* Search */}
          <div className="relative flex-1">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <Search className="w-5 h-5 text-slate-400" />
            </div>
            <input
              type="text"
              placeholder="Search tasks by description, title, equipment, or technician..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700 placeholder-slate-400"
            />
          </div>

          {/* Technician filter for Engineer */}
          {user.role === 'Engineer' && (
            <div className="relative w-60">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <Users className="w-5 h-5 text-slate-400" />
              </div>
              <select
                value={selectedTechnicianId}
                onChange={(e) => setSelectedTechnicianId(e.target.value)}
                className="pl-10 pr-8 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700 bg-white"
              >
                <option value="">All Technicians</option>
                {technicians.map(tech => (
                  <option key={tech._id} value={tech._id}>
                    {tech.name}
                  </option>
                ))}
              </select>
            </div>
          )}
        </div>

        {/* Success/Error Messages */}
        {successMsg && (<div className="mb-4 px-4 py-2 bg-green-100 text-green-700 rounded">{successMsg}</div>)}
        {error && (
          <div className="mb-4 px-4 py-2 bg-red-100 text-red-700 rounded flex items-center gap-2">
            <AlertCircle className="w-5 h-5" />
            {error}
          </div>
        )}

        {/* Task count */}
        <div className="mb-6 text-sm text-slate-600 bg-white rounded-xl p-4 border border-slate-200 flex justify-between items-center">
          <div>
            <span className="font-medium">{filteredTasks.length}</span> task{filteredTasks.length !== 1 ? 's' : ''} found
            {searchTerm && <span> matching "<span className="font-medium">{searchTerm}</span>"</span>}
            {user.role === 'Engineer' && selectedTechnicianId && (
              <span> for <span className="font-medium">{technicians.find(t => t._id === selectedTechnicianId)?.name}</span></span>
            )}
          </div>
          {totalPages > 1 && (<div className="text-sm text-slate-500">Page {currentPage} of {totalPages}</div>)}
        </div>

        {/* No tasks */}
        {filteredTasks.length === 0 && (
          <div className="bg-white rounded-2xl shadow-lg border border-slate-200 p-12 text-center">
            <svg className="mx-auto h-16 w-16 text-slate-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <h3 className="mt-4 text-xl font-semibold text-slate-600">No tasks found</h3>
            <p className="mt-2 text-slate-400">Try adjusting your search or filters.</p>
          </div>
        )}

        {/* Tasks Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {currentTasks.map(task => (
            <div
              key={task._id}
              className={`bg-white rounded-2xl shadow-lg border border-slate-200 overflow-hidden hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1
                ${user.role === 'Technician' && task.assignedTo?._id === user._id ? 'border-blue-500 ring-1 ring-blue-300' : ''}`}
            >
              {/* Status Label */}
              <div className={`flex items-center gap-2 px-4 py-2 ${getStatusColor(task.status)}`}>
                {getStatusIcon(task.status)}
                <span className="text-sm font-semibold">{task.status}</span>
                {task.needsCertification && (
                  <span className="ml-auto text-[11px] px-2 py-0.5 rounded bg-indigo-100 text-indigo-700 border border-indigo-200">
                    Awaiting Certification
                  </span>
                )}
              </div>

              <div className="p-5">
                <h3 className="text-lg font-semibold text-slate-800 mb-1 truncate">{task.title}</h3>
                <p className="text-sm text-slate-600 mb-2 line-clamp-2">{task.description}</p>

                <div className="text-xs text-slate-400 mb-3 flex flex-wrap gap-2">
                  <div>Priority: {task.priority || 'N/A'}</div>
                  <div>Type: {task.taskType || 'N/A'}</div>
                </div>

                <div className="text-xs text-slate-600 mb-3">
                  Equipment: <span className="font-medium">{task.equipment?.name || 'N/A'}</span>
                </div>

                {/* Status Update dropdown */}
                {(user.role === 'Engineer' || (user.role === 'Technician' && task.assignedTo?._id === user._id)) && (
                  <div className="flex items-center gap-2">
                    <select
                      disabled={updatingTaskId === task._id}
                      value={task.status}
                      onChange={e => handleStatusChange(task._id, e.target.value)}
                      className="w-full py-2 px-3 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent text-slate-700"
                    >
                      <option value="Pending">Pending</option>
                      <option value="In Progress">In Progress</option>
                      <option value="Completed">Completed</option>
                      <option value="Cancelled">Cancelled</option>
                    </select>

                    {/* Add Work (tech on their assigned tasks; engineers can also open to view/edit if desired) */}
                    {(user.role === 'Engineer' || (user.role === 'Technician' && task.assignedTo?._id === user._id)) && (
                      <button
                        onClick={() => openWorkModal(task)}
                        className="inline-flex items-center gap-1 px-3 py-2 bg-slate-800 hover:bg-slate-900 text-white rounded-lg"
                        title="Add Work"
                      >
                        <Wrench className="w-4 h-4" />
                        Add Work
                      </button>
                    )}
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="mt-8 flex justify-center gap-3 text-slate-700">
            <button onClick={() => setCurrentPage(p => Math.max(p - 1, 1))} disabled={currentPage === 1}
                    className="px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100 disabled:opacity-50">Prev</button>
            {Array.from({ length: totalPages }, (_, i) => i + 1).map(n => (
              <button key={n} onClick={() => setCurrentPage(n)}
                      className={`px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100 ${currentPage === n ? 'bg-blue-600 text-white' : ''}`}>
                {n}
              </button>
            ))}
            <button onClick={() => setCurrentPage(p => Math.min(p + 1, totalPages))} disabled={currentPage === totalPages}
                    className="px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100 disabled:opacity-50">Next</button>
          </div>
        )}
      </div>

      {/* Add Work Modal */}
      {workModal.open && workModal.task && (
        <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-2xl p-6 max-h-[85vh] overflow-y-auto">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold">Add Work — {workModal.task.title}</h3>
              <button onClick={() => setWorkModal({ open: false, task: null })} className="text-slate-500 hover:text-slate-700">
                <X className="w-5 h-5" />
              </button>
            </div>

            <form onSubmit={submitWork} className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-1">Fault Description</label>
                <textarea
                  className="w-full border border-slate-300 rounded-lg px-3 py-2"
                  rows={3}
                  value={workForm.faultDescription}
                  onChange={(e) => setWorkForm(f => ({ ...f, faultDescription: e.target.value }))}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Repair Details</label>
                <textarea
                  className="w-full border border-slate-300 rounded-lg px-3 py-2"
                  rows={3}
                  value={workForm.repairDetails}
                  onChange={(e) => setWorkForm(f => ({ ...f, repairDetails: e.target.value }))}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">Spare Parts Used/Required</label>
                <div className="space-y-3">
                  {workForm.sparePartsUsed.map((p, i) => (
                    <div key={i} className="grid grid-cols-1 md:grid-cols-6 gap-3">
                      <input
                        type="text"
                        placeholder="Name"
                        value={p.name}
                        onChange={(e) => updateSpare(i, 'name', e.target.value)}
                        className="md:col-span-2 border border-slate-300 rounded-lg px-3 py-2"
                      />
                      <input
                        type="number"
                        min="1"
                        value={p.quantity}
                        onChange={(e) => updateSpare(i, 'quantity', e.target.value)}
                        className="md:col-span-1 border border-slate-300 rounded-lg px-3 py-2"
                      />
                      <input
                        type="text"
                        placeholder="Description"
                        value={p.description}
                        onChange={(e) => updateSpare(i, 'description', e.target.value)}
                        className="md:col-span-2 border border-slate-300 rounded-lg px-3 py-2"
                      />
                      <button type="button" onClick={() => removeSpare(i)}
                              className="md:col-span-1 px-3 py-2 border border-red-200 text-red-600 rounded-lg hover:bg-red-50">
                        Remove
                      </button>
                    </div>
                  ))}
                </div>
                <button type="button" onClick={addSpare}
                        className="mt-3 px-3 py-2 border border-slate-300 rounded-lg hover:bg-slate-50">
                  + Add Spare
                </button>
              </div>

              <div className="flex justify-end gap-3 pt-2">
                <button type="button" onClick={() => setWorkModal({ open: false, task: null })}
                        className="px-4 py-2 border border-slate-300 rounded-lg hover:bg-slate-50">Cancel</button>
                <button type="submit" className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg">
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* (Assign Task modal unchanged; omitted here for brevity if you already have it) */}
    </div>
  );
}

export default Tasks;
