import React, { useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const Login = ({ login }) => {
  const location = useLocation();
  const from = location.state?.from?.pathname || '/dashboard';

  // Keep your original fields
  const [username, setUsername] = useState('');
  const [pin, setPin] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Match your existing API payload
      const res = await axios.post('/auth/login', { username, pin });
      if (res.data) {
        login(res.data, from); // Pass "from" to App.js
      }
    } catch (err) {
      console.error(err);
      setError('Login failed. Please check your credentials.');
    }
  };

  return (
    <div className="max-w-md mx-auto bg-white shadow-md rounded-lg p-6 mt-20">
      <h2 className="text-2xl font-bold mb-6 text-center">Login</h2>
      {error && (
        <div className="bg-red-100 text-red-700 p-2 mb-4 rounded">
          {error}
        </div>
      )}
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block mb-2 font-medium">Username</label>
          <input
            type="text"
            className="w-full p-2 border rounded"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="mb-6">
          <label className="block mb-2 font-medium">PIN</label>
          <input
            type="password"
            className="w-full p-2 border rounded"
            value={pin}
            onChange={(e) => setPin(e.target.value)}
            required
          />
        </div>
        <button
          type="submit"
          className="w-full bg-teal-600 text-white py-2 px-4 rounded hover:bg-teal-700"
        >
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
