// frontend/src/components/equipment/Equipment.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Equipment = ({ user }) => {
  const [equipment, setEquipment] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchEquipment = async () => {
      try {
        const res = await axios.get('/equipment');
        setEquipment(res.data.data); // Assuming backend returns { success: true, data: [] }
        setLoading(false);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch equipment');
        setLoading(false);
      }
    };

    fetchEquipment();
  }, []);

  const handleSearch = e => {
    setSearchTerm(e.target.value);
  };

  const filteredEquipment = equipment.filter(eq =>
    eq.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleAddEquipment = () => {
    window.location.href = '/add-equipment';
  };

  return (
    <div>
      <h1>Equipment Inventory</h1>
      <input
        type="text"
        placeholder="Search by name, brand, model..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <button onClick={handleAddEquipment}>+ Add Equipment</button>
      {loading && <p>Loading...</p>}
      {error && <p>Error: {error}</p>}
      {!loading && !error && filteredEquipment.length === 0 && <p>No equipment found.</p>}
      {filteredEquipment.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Brand/Model</th>
              <th>ID/SERIAL</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {filteredEquipment.map(eq => (
              <tr key={eq.id}>
                <td>{eq.name}</td>
                <td>{`${eq.brand} ${eq.model}`}</td>
                <td>{eq.serialNumber}</td>
                <td>{eq.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Equipment;
