// frontend/src/components/tasks/Tasks.js
import React, { useState, useEffect, useMemo } from 'react';
import api from '../../utils/api';
import socket from '../../utils/socket';
import {
  ClipboardCheck,
  Users,
  CheckCircle,
  Clock,
  ArrowRight,
  X,
  Search,
  AlertCircle,
  Plus,
  Wrench
} from 'lucide-react';

function Tasks({ user }) {
  const [tasks, setTasks] = useState([]);
  const [technicians, setTechnicians] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [successMsg, setSuccessMsg] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedTechnicianId, setSelectedTechnicianId] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [updatingTaskId, setUpdatingTaskId] = useState(null);

  // Assign Task modal states
  const [showAssignModal, setShowAssignModal] = useState(false);
  const [newTaskData, setNewTaskData] = useState({
    title: '',
    description: '',
    taskType: '',
    priority: 'Low',
    assignedTo: '',
    equipment: '',
    dueDate: '',
  });
  const [equipments, setEquipments] = useState([]);

  // Technician work-details modal
  const [showWorkModal, setShowWorkModal] = useState(false);
  const [activeTask, setActiveTask] = useState(null);
  const [workData, setWorkData] = useState({
    faultDescription: '',
    comments: '',
    spareParts: [{ name: '', quantity: 1, description: '' }],
    status: 'In Progress'
  });

  const itemsPerPage = 8;

  useEffect(() => {
    if (user.role === 'Engineer') {
      api.get('/users/preapproved/technicians')
        .then(res => {
          if (res.data.success) setTechnicians(res.data.technicians);
        })
        .catch(() => setTechnicians([]));
    }
  }, [user.role]);

  useEffect(() => {
    if (user.role === 'Engineer') {
      api.get('/equipment')
        .then(res => {
          if (res.data.success) setEquipments(res.data.equipment || []);
        })
        .catch(() => setEquipments([]));
    }
  }, [user.role]);

  const fetchTasks = async () => {
    try {
      setLoading(true);
      setError('');
      const res = await api.get('/tasks');
      setTasks(res.data.tasks || []);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to fetch tasks');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchTasks(); }, []);

  // Real-time refresh
  useEffect(() => {
    const refetch = () => fetchTasks();
    socket.on('task:created', refetch);
    socket.on('task:updated', refetch);
    socket.on('task:completed', refetch);
    socket.on('task:deleted', refetch);
    return () => {
      socket.off('task:created', refetch);
      socket.off('task:updated', refetch);
      socket.off('task:completed', refetch);
      socket.off('task:deleted', refetch);
    };
  }, []);

  const filteredTasks = useMemo(() => {
    let filtered = tasks;
    if (user.role === 'Engineer' && selectedTechnicianId) {
      filtered = filtered.filter(t => t.assignedTo?._id === selectedTechnicianId);
    }
    if (user.role === 'Technician') {
      filtered = filtered.filter(t => t.assignedTo?._id === user._id);
    }
    if (searchTerm) {
      const q = searchTerm.toLowerCase();
      filtered = filtered.filter(t =>
        (t.description || '').toLowerCase().includes(q) ||
        (t.title || '').toLowerCase().includes(q) ||
        (t.assignedTo?.name || '').toLowerCase().includes(q)
      );
    }
    return filtered;
  }, [tasks, selectedTechnicianId, user, searchTerm]);

  const currentTasks = useMemo(() => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    return filteredTasks.slice(startIndex, startIndex + itemsPerPage);
  }, [filteredTasks, currentPage]);

  useEffect(() => { setCurrentPage(1); }, [searchTerm, selectedTechnicianId]);

  const handleStatusChange = async (taskId, newStatus) => {
    try {
      // If technician moving to In Progress / Completed => open details modal
      const targetTask = tasks.find(t => t._id === taskId);
      const techOwn = user.role === 'Technician' && targetTask?.assignedTo?._id === user._id;

      if (techOwn && (newStatus === 'In Progress' || newStatus === 'Completed')) {
        setActiveTask(targetTask);
        setWorkData({
          faultDescription: targetTask.faultDescription || '',
          comments: targetTask.comments || '',
          spareParts: targetTask.spareParts?.length ? targetTask.spareParts : [{ name: '', quantity: 1, description: '' }],
          status: newStatus
        });
        setShowWorkModal(true);
        return;
      }

      setUpdatingTaskId(taskId);
      await api.put(`/tasks/${taskId}/status`, { status: newStatus });
      await fetchTasks();
      setSuccessMsg('Task status updated successfully');
      setTimeout(() => setSuccessMsg(''), 3000);
    } catch {
      setError('Failed to update task status');
    } finally {
      setUpdatingTaskId(null);
    }
  };

  // Submit work details (technician)
  const submitWorkDetails = async (e) => {
    e.preventDefault();
    if (!activeTask) return;

    try {
      setUpdatingTaskId(activeTask._id);
      await api.put(`/tasks/${activeTask._id}`, {
        faultDescription: workData.faultDescription,
        comments: workData.comments,
        spareParts: workData.spareParts,
        status: workData.status // may be In Progress or Completed
      });

      setShowWorkModal(false);
      setActiveTask(null);
      await fetchTasks();
      setSuccessMsg('Work details saved');
      setTimeout(() => setSuccessMsg(''), 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to save work details');
    } finally {
      setUpdatingTaskId(null);
    }
  };

  // Assign task handlers
  const handleNewTaskChange = (e) => {
    const { name, value } = e.target;
    setNewTaskData(prev => ({ ...prev, [name]: value }));
  };

  const handleAssignTask = async (e) => {
    e.preventDefault();
    setError('');
    setSuccessMsg('');
    try {
      if (!newTaskData.title || !newTaskData.taskType || !newTaskData.equipment) {
        setError('Please fill in all required fields');
        return;
      }
      await api.post('/tasks', newTaskData);
      setSuccessMsg('Task assigned successfully!');
      setShowAssignModal(false);
      setNewTaskData({
        title: '',
        description: '',
        taskType: '',
        priority: 'Low',
        assignedTo: '',
        equipment: '',
        dueDate: '',
      });
      await fetchTasks();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to assign task');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Completed': return 'bg-green-100 text-green-800 border border-green-200';
      case 'In Progress': return 'bg-blue-100 text-blue-800 border border-blue-200';
      case 'Pending': return 'bg-yellow-100 text-yellow-800 border border-yellow-200';
      case 'Cancelled': return 'bg-red-100 text-red-800 border border-red-200';
      default: return 'bg-gray-100 text-gray-800 border border-gray-200';
    }
  };
  const getStatusIcon = (s) => {
    switch (s) {
      case 'Completed': return <CheckCircle className="w-4 h-4" />;
      case 'In Progress': return <ArrowRight className="w-4 h-4" />;
      case 'Pending': return <Clock className="w-4 h-4" />;
      case 'Cancelled': return <X className="w-4 h-4" />;
      default: return <Clock className="w-4 h-4" />;
    }
  };

  const totalPages = Math.ceil(filteredTasks.length / itemsPerPage);

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-8 px-4">
        <div className="max-w-7xl mx-auto">
          <div className="bg-white rounded-2xl shadow-lg border border-slate-200 p-12 text-center">
            <div className="inline-block h-12 w-12 animate-spin rounded-full border-4 border-solid border-blue-600 border-r-transparent"></div>
            <p className="mt-4 text-xl text-slate-600">Loading tasks...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-8 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8 flex flex-col md:flex-row md:justify-between md:items-center gap-4">
          <div>
            <h1 className="text-3xl font-bold text-slate-800 flex items-center gap-3 mb-1">
              <div className="bg-gradient-to-r from-blue-600 to-green-500 p-2 rounded-lg text-white">
                <ClipboardCheck className="w-6 h-6" />
              </div>
              Task Management
            </h1>
            <p className="text-slate-600 mb-4">Manage and track maintenance tasks efficiently</p>
          </div>
          {user.role === 'Engineer' && (
            <button
              onClick={() => setShowAssignModal(true)}
              className="inline-flex items-center gap-2 px-5 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-xl transition"
              title="Assign New Task"
            >
              <Plus className="w-5 h-5" />
              Assign Task
            </button>
          )}
        </div>

        {/* Search & filter */}
        <div className="flex flex-col lg:flex-row gap-4 mb-6">
          <div className="relative flex-1">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <Search className="w-5 h-5 text-slate-400" />
            </div>
            <input
              type="text"
              placeholder="Search tasks by description, title, or technician..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700 placeholder-slate-400"
            />
          </div>

          {user.role === 'Engineer' && (
            <div className="relative w-60">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <Users className="w-5 h-5 text-slate-400" />
              </div>
              <select
                value={selectedTechnicianId}
                onChange={(e) => setSelectedTechnicianId(e.target.value)}
                className="pl-10 pr-8 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700 bg-white"
              >
                <option value="">All Technicians</option>
                {technicians.map(tech => (
                  <option key={tech._id} value={tech._id}>{tech.name}</option>
                ))}
              </select>
            </div>
          )}
        </div>

        {/* Messages */}
        {successMsg && <div className="mb-4 px-4 py-2 bg-green-100 text-green-700 rounded">{successMsg}</div>}
        {error && (
          <div className="mb-4 px-4 py-2 bg-red-100 text-red-700 rounded flex items-center gap-2">
            <AlertCircle className="w-5 h-5" />
            {error}
          </div>
        )}

        {/* Summary */}
        <div className="mb-6 text-sm text-slate-600 bg-white rounded-xl p-4 border border-slate-200 flex justify-between items-center">
          <div>
            <span className="font-medium">{filteredTasks.length}</span> task{filteredTasks.length !== 1 ? 's' : ''} found
            {searchTerm && <span> matching "<span className="font-medium">{searchTerm}</span>"</span>}
            {user.role === 'Engineer' && selectedTechnicianId && (
              <span> for <span className="font-medium">{technicians.find(t => t._id === selectedTechnicianId)?.name}</span></span>
            )}
          </div>
          {totalPages > 1 && <div className="text-sm text-slate-500">Page {currentPage} of {totalPages}</div>}
        </div>

        {/* Tasks grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {currentTasks.map(task => (
            <div
              key={task._id}
              className={`bg-white rounded-2xl shadow-lg border border-slate-200 overflow-hidden hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1
                ${user.role === 'Technician' && task.assignedTo?._id === user._id ? 'border-blue-500 ring-1 ring-blue-300' : ''}`}
            >
              <div className={`flex items-center gap-2 px-4 py-2 ${getStatusColor(task.status)}`}>
                {getStatusIcon(task.status)}
                <span className="text-sm font-semibold">{task.status}</span>
              </div>

              <div className="p-5">
                <h3 className="text-lg font-semibold text-slate-800 mb-1 truncate">{task.title}</h3>
                <p className="text-sm text-slate-600 mb-2 truncate">{task.description}</p>

                <div className="text-xs text-slate-400 mb-3 flex flex-wrap gap-2">
                  <div>Priority: {task.priority || 'N/A'}</div>
                  <div>Type: {task.taskType || 'N/A'}</div>
                </div>

                <div className="text-xs text-slate-600 mb-3">
                  Assigned to: <span className="font-medium">{task.assignedTo?.name || 'Unassigned'}</span>
                </div>

                {(user.role === 'Admin' || user.role === 'Engineer' || (user.role === 'Technician' && task.assignedTo?._id === user._id)) && (
                  <>
                    <select
                      disabled={updatingTaskId === task._id}
                      value={task.status}
                      onChange={e => handleStatusChange(task._id, e.target.value)}
                      className="w-full py-2 px-3 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent text-slate-700"
                    >
                      <option value="Pending">Pending</option>
                      <option value="In Progress">In Progress</option>
                      <option value="Completed">Completed</option>
                      <option value="Cancelled">Cancelled</option>
                    </select>

                    {/* Quick open modal for technicians */}
                    {user.role === 'Technician' && task.assignedTo?._id === user._id && (
                      <button
                        onClick={() => { setActiveTask(task); setWorkData({
                          faultDescription: task.faultDescription || '',
                          comments: task.comments || '',
                          spareParts: task.spareParts?.length ? task.spareParts : [{ name: '', quantity: 1, description: '' }],
                          status: task.status
                        }); setShowWorkModal(true); }}
                        className="mt-3 inline-flex items-center gap-2 px-3 py-2 rounded-lg border border-slate-300 hover:bg-slate-50 text-slate-700"
                      >
                        <Wrench className="w-4 h-4" /> Update Work Details
                      </button>
                    )}
                  </>
                )}
              </div>
            </div>
          ))}
        </div>

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="mt-8 flex justify-center gap-3 text-slate-700">
            <button onClick={() => setCurrentPage(p => Math.max(p - 1, 1))} disabled={currentPage === 1}
              className="px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100 disabled:opacity-50">Prev</button>
            {[...Array(totalPages)].map((_, i) => (
              <button key={i} onClick={() => setCurrentPage(i + 1)}
                className={`px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100 ${currentPage === i + 1 ? 'bg-blue-600 text-white' : ''}`}>
                {i + 1}
              </button>
            ))}
            <button onClick={() => setCurrentPage(p => Math.min(p + 1, totalPages))} disabled={currentPage === totalPages}
              className="px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100 disabled:opacity-50">Next</button>
          </div>
        )}
      </div>

      {/* Assign Task Modal (unchanged layout, validations kept) */}
      {showAssignModal && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex justify-center items-center z-50">
          <div className="bg-white rounded-xl shadow-xl max-w-lg w-full p-6 relative">
            <h2 className="text-xl font-semibold mb-4">Assign New Task</h2>
            <form onSubmit={handleAssignTask} className="space-y-4">
              <div>
                <label className="block mb-1 font-medium">Title *</label>
                <input name="title" value={newTaskData.title} onChange={handleNewTaskChange}
                  type="text" required className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"/>
              </div>
              <div>
                <label className="block mb-1 font-medium">Description</label>
                <textarea name="description" value={newTaskData.description} onChange={handleNewTaskChange}
                  rows={3} className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"/>
              </div>
              <div>
                <label className="block mb-1 font-medium">Task Type *</label>
                <select name="taskType" required value={newTaskData.taskType} onChange={handleNewTaskChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500">
                  <option value="">Select Task Type</option>
                  <option>Repair</option>
                  <option>Install</option>
                  <option>Inspect</option>
                  <option>Assess</option>
                  <option>Calibrate</option>
                </select>
              </div>
              <div>
                <label className="block mb-1 font-medium">Priority</label>
                <select name="priority" value={newTaskData.priority} onChange={handleNewTaskChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500">
                  <option>Low</option><option>Medium</option><option>High</option><option>Urgent</option>
                </select>
              </div>
              <div>
                <label className="block mb-1 font-medium">Assign To (optional)</label>
                <select name="assignedTo" value={newTaskData.assignedTo} onChange={handleNewTaskChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500">
                  <option value="">Unassigned</option>
                  {technicians.map(tech => <option key={tech._id} value={tech._id}>{tech.name}</option>)}
                </select>
              </div>
              <div>
                <label className="block mb-1 font-medium">Equipment *</label>
                <select name="equipment" required value={newTaskData.equipment} onChange={handleNewTaskChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500">
                  <option value="">Select Equipment</option>
                  {equipments.map(eq => (
                    <option key={eq._id} value={eq._id}>{eq.name} - {eq.serialNumber || 'No S/N'}</option>
                  ))}
                </select>
              </div>
              <div>
                <label className="block mb-1 font-medium">Due Date</label>
                <input name="dueDate" type="date" value={newTaskData.dueDate} onChange={handleNewTaskChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"/>
              </div>
              <div className="flex justify-end gap-3 pt-4">
                <button type="button" onClick={() => setShowAssignModal(false)}
                  className="px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100">Cancel</button>
                <button type="submit" className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg">Assign</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* TECH: Work details modal */}
      {showWorkModal && activeTask && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex justify-center items-center z-50">
          <div className="bg-white rounded-xl shadow-xl max-w-2xl w-full p-6 relative">
            <h2 className="text-xl font-semibold mb-4">Update Work Details</h2>
            <form onSubmit={submitWorkDetails} className="space-y-4">
              <div>
                <label className="block mb-1 font-medium">Fault Description</label>
                <textarea
                  value={workData.faultDescription}
                  onChange={(e) => setWorkData(prev => ({ ...prev, faultDescription: e.target.value }))}
                  rows={3}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block mb-1 font-medium">Repair Details</label>
                <textarea
                  value={workData.comments}
                  onChange={(e) => setWorkData(prev => ({ ...prev, comments: e.target.value }))}
                  rows={4}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              {/* Spare parts dynamic list */}
              <div>
                <div className="flex items-center justify-between mb-2">
                  <label className="font-medium">Spare Parts Used / Required</label>
                  <button
                    type="button"
                    onClick={() => setWorkData(prev => ({ ...prev, spareParts: [...prev.spareParts, { name: '', quantity: 1, description: '' }] }))}
                    className="text-blue-600 hover:underline"
                  >
                    + Add Part
                  </button>
                </div>
                <div className="space-y-3">
                  {workData.spareParts.map((p, idx) => (
                    <div key={idx} className="grid grid-cols-12 gap-3">
                      <input
                        className="col-span-4 border border-slate-300 rounded-lg px-3 py-2"
                        placeholder="Name"
                        value={p.name}
                        onChange={(e) => {
                          const v = e.target.value;
                          setWorkData(prev => {
                            const arr = [...prev.spareParts];
                            arr[idx] = { ...arr[idx], name: v };
                            return { ...prev, spareParts: arr };
                          });
                        }}
                      />
                      <input
                        type="number"
                        min="1"
                        className="col-span-2 border border-slate-300 rounded-lg px-3 py-2"
                        placeholder="Qty"
                        value={p.quantity}
                        onChange={(e) => {
                          const v = Number(e.target.value) || 1;
                          setWorkData(prev => {
                            const arr = [...prev.spareParts];
                            arr[idx] = { ...arr[idx], quantity: v };
                            return { ...prev, spareParts: arr };
                          });
                        }}
                      />
                      <input
                        className="col-span-5 border border-slate-300 rounded-lg px-3 py-2"
                        placeholder="Description"
                        value={p.description}
                        onChange={(e) => {
                          const v = e.target.value;
                          setWorkData(prev => {
                            const arr = [...prev.spareParts];
                            arr[idx] = { ...arr[idx], description: v };
                            return { ...prev, spareParts: arr };
                          });
                        }}
                      />
                      <button
                        type="button"
                        onClick={() => setWorkData(prev => ({ ...prev, spareParts: prev.spareParts.filter((_, i) => i !== idx) }))}
                        className="col-span-1 text-red-600 hover:underline"
                        title="Remove"
                      >
                        Remove
                      </button>
                    </div>
                  ))}
                </div>
              </div>

              {/* Desired status (stick with In Progress / Completed for techs) */}
              <div>
                <label className="block mb-1 font-medium">Set Status</label>
                <select
                  value={workData.status}
                  onChange={(e) => setWorkData(prev => ({ ...prev, status: e.target.value }))}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2"
                >
                  <option value="In Progress">In Progress</option>
                  <option value="Completed">Completed</option>
                </select>
              </div>

              <div className="flex justify-end gap-3 pt-4">
                <button type="button" onClick={() => setShowWorkModal(false)}
                  className="px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100">Cancel</button>
                <button type="submit" className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg"
                  disabled={updatingTaskId === activeTask._id}>
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Tasks;
