import React from 'react';
import { Navigate, Outlet, useLocation } from 'react-router-dom';

const PrivateRoute = ({ user, role, children }) => {
  const location = useLocation();

  // If not authenticated, redirect to /login and include the original location
  if (!user) {
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  // If role is required and user doesn't match, send to dashboard
  if (role && user.role !== role) {
    return <Navigate to="/dashboard" replace />;
  }

  // Otherwise render the children or outlet
  return children || <Outlet />;
};

export default PrivateRoute;
