// frontend/src/components/maintenance/Maintenance.js
import React, { useEffect, useState } from 'react';
import api from '../../utils/api';
import socket from '../../utils/socket';

const Maintenance = () => {
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchHistory = async () => {
    try {
      setLoading(true);
      // Pull all tasks then filter; if you have a dedicated /tasks?status=Completed use it instead
      const res = await api.get('/tasks');
      const items = Array.isArray(res.data.tasks) ? res.data.tasks : [];
      const completed = items
        .filter(t => t.status === 'Completed')
        .map(t => ({
          id: t._id,
          title: t.title,
          equipmentName: t.equipment?.name || '—',
          technician: t.assignedTo?.name || '—',
          completedDate: t.completedDate || t.updatedAt || t.createdAt,
          faultDescription: t.faultDescription || '',
          repairDetails: t.comments || '',
          spareParts: Array.isArray(t.spareParts) ? t.spareParts : []
        }));
      setHistory(completed);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchHistory(); }, []);

  // live updates after task completion
  useEffect(() => {
    const refetch = () => fetchHistory();
    socket.on('task:completed', refetch);
    socket.on('task:updated', refetch);
    return () => {
      socket.off('task:completed', refetch);
      socket.off('task:updated', refetch);
    };
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-8">
        <div className="max-w-7xl mx-auto text-center">
          <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-blue-600 border-r-transparent" />
          <p className="mt-3 text-slate-600">Loading maintenance history…</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-8">
      <div className="max-w-7xl mx-auto">
        <div className="mb-8 flex items-center justify-between">
          <h1 className="text-3xl font-bold text-slate-800">Maintenance Management</h1>
          <div className="flex gap-3">
            <button
              onClick={() => (window.location.href = '/schedule-preventive')}
              className="px-5 py-2 rounded-lg bg-amber-600 hover:bg-amber-700 text-white"
            >
              Schedule Preventive
            </button>
            <button
              onClick={() => (window.location.href = '/report-issue')}
              className="px-5 py-2 rounded-lg bg-red-600 hover:bg-red-700 text-white"
            >
              Report Issue
            </button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div className="bg-white rounded-2xl shadow-lg border border-slate-200 p-6">
            <h2 className="text-xl font-semibold text-slate-800 mb-4">Preventive Maintenance</h2>
            <p className="text-slate-500">Get started by scheduling a new preventive maintenance task.</p>
          </div>

          <div className="bg-white rounded-2xl shadow-lg border border-slate-200 p-6">
            <h2 className="text-xl font-semibold text-slate-800 mb-4">Maintenance History</h2>

            {history.length === 0 ? (
              <div className="text-slate-500">No maintenance history yet.</div>
            ) : (
              <div className="space-y-4">
                {history.map((h) => (
                  <div key={h.id} className="p-4 rounded-xl border border-slate-200 bg-slate-50">
                    <div className="flex items-center justify-between">
                      <div className="text-lg font-semibold text-slate-800">{h.title}</div>
                      <div className="text-xs text-slate-500">{new Date(h.completedDate).toLocaleString()}</div>
                    </div>
                    <div className="text-sm text-slate-600 mt-1">
                      Equipment: <span className="font-medium">{h.equipmentName}</span> •
                      Technician: <span className="font-medium"> {h.technician}</span>
                    </div>

                    {h.faultDescription && (
                      <div className="mt-3">
                        <div className="text-xs uppercase text-slate-400">Fault Description</div>
                        <div className="text-sm text-slate-700">{h.faultDescription}</div>
                      </div>
                    )}

                    {h.repairDetails && (
                      <div className="mt-3">
                        <div className="text-xs uppercase text-slate-400">Repair Details</div>
                        <div className="text-sm text-slate-700 whitespace-pre-wrap">{h.repairDetails}</div>
                      </div>
                    )}

                    {h.spareParts.length > 0 && (
                      <div className="mt-3">
                        <div className="text-xs uppercase text-slate-400 mb-1">Spare Parts Used / Required</div>
                        <ul className="list-disc ml-5 text-sm text-slate-700">
                          {h.spareParts.map((p, idx) => (
                            <li key={idx}>
                              <span className="font-medium">{p.name}</span> — qty {p.quantity}{p.description ? ` (${p.description})` : ''}
                            </li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Maintenance;
