// src/index.js

// --- Set theme ASAP to minimize flash between light/dark ---
// (If you also add an inline script in public/index.html this can be even snappier.)
(() => {
  try {
    const stored = localStorage.getItem('theme');
    const prefersDark = window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches;
    if (stored === 'dark' || (!stored && prefersDark)) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  } catch {
    // no-op
  }
})();

// --- Mobile viewport height fix (iOS/Android browser chrome changes) ---
function setAppVh() {
  const vh = window.innerHeight * 0.01;
  document.documentElement.style.setProperty('--app-vh', `${vh}px`);
}
setAppVh();
window.addEventListener('resize', setAppVh);
window.addEventListener('orientationchange', setAppVh);

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import './index.css'; // Tailwind + your globals

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

// Optional: clean up listeners on HMR (harmless in production)
if (import.meta && import.meta.hot) {
  import.meta.hot.dispose(() => {
    window.removeEventListener('resize', setAppVh);
    window.removeEventListener('orientationchange', setAppVh);
  });
}
