import React, { useState, useEffect, useMemo } from 'react';
import api from '../../utils/api';
import {
  ClipboardCheck, Users, CheckCircle, Clock, ArrowRight, X, Search, AlertCircle, Plus, Wrench
} from 'lucide-react';

function Tasks({ user }) {
  const [tasks, setTasks] = useState([]);
  const [technicians, setTechnicians] = useState([]);
  const [equipments, setEquipments] = useState([]);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [successMsg, setSuccessMsg] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedTechnicianId, setSelectedTechnicianId] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [updatingTaskId, setUpdatingTaskId] = useState(null);

  // Assign Task modal
  const [showAssignModal, setShowAssignModal] = useState(false);
  const [newTaskData, setNewTaskData] = useState({
    title: '',
    description: '',
    taskType: '',
    priority: 'Low',
    assignedTo: '',
    equipment: '',
    dueDate: '',
  });

  // Work Details modal (Technician)
  const [workModalOpen, setWorkModalOpen] = useState(false);
  const [workTaskId, setWorkTaskId] = useState(null);
  const [workForm, setWorkForm] = useState({
    faultDescription: '',
    comments: '', // repair details
    sparePartsText: '' // parse to array
  });

  const itemsPerPage = 8;

  // ✅ Load ALL technicians from your route (approved=any)
  useEffect(() => {
    const loadTechs = async () => {
      if (user.role !== 'Engineer' && user.role !== 'Admin') return;
      try {
        const res = await api.get('/users/preapproved/technicians', {
          params: { approved: 'any' } // <— key change
        });
        setTechnicians(res?.data?.technicians || []);
      } catch {
        setTechnicians([]);
      }
    };
    loadTechs();
  }, [user.role]);

  // Fetch equipments (for assigning)
  useEffect(() => {
    if (user.role === 'Engineer' || user.role === 'Admin') {
      api.get('/equipment')
        .then(res => setEquipments(res.data.equipment || []))
        .catch(() => setEquipments([]));
    }
  }, [user.role]);

  // Fetch tasks
  const fetchTasks = async () => {
    try {
      setLoading(true);
      setError('');
      const res = await api.get('/tasks');
      setTasks(res.data.tasks || []);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to fetch tasks');
    } finally {
      setLoading(false);
    }
  };
  useEffect(() => { fetchTasks(); }, []);

  // Filter tasks
  const filteredTasks = useMemo(() => {
    let filtered = tasks;

    if ((user.role === 'Engineer' || user.role === 'Admin') && selectedTechnicianId) {
      filtered = filtered.filter(t => t.assignedTo?._id === selectedTechnicianId);
    }

    if (user.role === 'Technician') {
      filtered = filtered.filter(t => t.assignedTo?._id === user._id);
    }

    if (searchTerm) {
      const s = searchTerm.toLowerCase();
      filtered = filtered.filter(t =>
        (t.description || '').toLowerCase().includes(s) ||
        (t.title || '').toLowerCase().includes(s) ||
        (t.assignedTo?.name || '').toLowerCase().includes(s)
      );
    }
    return filtered;
  }, [tasks, selectedTechnicianId, user, searchTerm]);

  // Pagination
  const currentTasks = useMemo(() => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    return filteredTasks.slice(startIndex, startIndex + itemsPerPage);
  }, [filteredTasks, currentPage]);
  useEffect(() => setCurrentPage(1), [searchTerm, selectedTechnicianId]);

  // Status helpers
  const getStatusColor = (status) => {
    switch (status) {
      case 'Completed': return 'bg-green-100 text-green-800 border border-green-200';
      case 'In Progress': return 'bg-blue-100 text-blue-800 border border-blue-200';
      case 'Pending': return 'bg-yellow-100 text-yellow-800 border border-yellow-200';
      case 'Cancelled': return 'bg-red-100 text-red-800 border border-red-200';
      default: return 'bg-gray-100 text-gray-800 border border-gray-200';
    }
  };
  const getStatusIcon = (status) => {
    switch (status) {
      case 'Completed': return <CheckCircle className="w-4 h-4" />;
      case 'In Progress': return <ArrowRight className="w-4 h-4" />;
      case 'Pending': return <Clock className="w-4 h-4" />;
      case 'Cancelled': return <X className="w-4 h-4" />;
      default: return <Clock className="w-4 h-4" />;
    }
  };

  // Update status
  const handleStatusChange = async (taskId, newStatus) => {
    try {
      setUpdatingTaskId(taskId);
      await api.put(`/tasks/${taskId}/status`, { status: newStatus });
      await fetchTasks();
      setSuccessMsg('Task status updated successfully');
      setTimeout(() => setSuccessMsg(''), 2500);
    } catch {
      setError('Failed to update task status');
    } finally {
      setUpdatingTaskId(null);
    }
  };

  // Assign modal handlers
  const handleNewTaskChange = (e) => {
    const { name, value } = e.target;
    setNewTaskData(prev => ({ ...prev, [name]: value }));
  };
  const handleAssignTask = async (e) => {
    e.preventDefault();
    setError('');
    setSuccessMsg('');
    try {
      if (!newTaskData.title || !newTaskData.equipment || !newTaskData.taskType) {
        setError('Please fill in all required fields');
        return;
      }
      await api.post('/tasks', newTaskData);
      setSuccessMsg('Task assigned successfully!');
      setShowAssignModal(false);
      setNewTaskData({
        title: '',
        description: '',
        taskType: '',
        priority: 'Low',
        assignedTo: '',
        equipment: '',
        dueDate: '',
      });
      await fetchTasks();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to assign task');
    }
  };

  // Work Details modal handlers (Technician)
  const openWorkModal = (task) => {
    setWorkTaskId(task._id);
    setWorkForm({
      faultDescription: task.faultDescription || '',
      comments: task.comments || '',
      sparePartsText: (task.spareParts || [])
        .map(p => `${p.name || ''}:${p.quantity || 1}`).join('; ')
    });
    setWorkModalOpen(true);
  };
  const closeWorkModal = () => {
    setWorkModalOpen(false);
    setWorkTaskId(null);
  };
  const submitWorkDetails = async (e) => {
    e.preventDefault();
    try {
      // Parse "name:qty; name:qty"
      const parts = (workForm.sparePartsText || '')
        .split(';')
        .map(s => s.trim())
        .filter(Boolean)
        .map(s => {
          const [name, qty] = s.split(':').map(z => z.trim());
          return { name, quantity: Number(qty || 1) };
        });

      await api.put(`/tasks/${workTaskId}`, {
        faultDescription: workForm.faultDescription,
        comments: workForm.comments, // repair details
        spareParts: parts
      });
      closeWorkModal();
      await fetchTasks();
      setSuccessMsg('Work details saved');
      setTimeout(() => setSuccessMsg(''), 2000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to save work details');
    }
  };

  const totalPages = Math.ceil(filteredTasks.length / itemsPerPage);

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-8 px-4">
        <div className="max-w-7xl mx-auto">
          <div className="bg-white rounded-2xl shadow-lg border border-slate-200 p-12 text-center">
            <div className="inline-block h-12 w-12 animate-spin rounded-full border-4 border-solid border-blue-600 border-r-transparent"></div>
            <p className="mt-4 text-xl text-slate-600">Loading tasks...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-8 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8 flex flex-col md:flex-row md:justify-between md:items-center gap-4">
          <div>
            <h1 className="text-3xl font-bold text-slate-800 flex items-center gap-3 mb-1">
              <div className="bg-gradient-to-r from-blue-600 to-green-500 p-2 rounded-lg text-white">
                <ClipboardCheck className="w-6 h-6" />
              </div>
              Task Management
            </h1>
            <p className="text-slate-600 mb-4">Manage and track maintenance tasks efficiently</p>
          </div>

          {(user.role === 'Engineer' || user.role === 'Admin') && (
            <button
              onClick={() => setShowAssignModal(true)}
              className="inline-flex items-center gap-2 px-5 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-xl transition"
              title="Assign New Task"
            >
              <Plus className="w-5 h-5" />
              Assign Task
            </button>
          )}
        </div>

        {/* Search & Filter */}
        <div className="flex flex-col lg:flex-row gap-4 mb-6">
          <div className="relative flex-1">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <Search className="w-5 h-5 text-slate-400" />
            </div>
            <input
              type="text"
              placeholder="Search tasks by description, title, or technician..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700 placeholder-slate-400"
            />
          </div>

          {(user.role === 'Engineer' || user.role === 'Admin') && (
            <div className="relative w-60">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <Users className="w-5 h-5 text-slate-400" />
              </div>
              <select
                value={selectedTechnicianId}
                onChange={(e) => setSelectedTechnicianId(e.target.value)}
                className="pl-10 pr-8 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-slate-700 bg-white"
              >
                <option value="">All Technicians</option>
                {technicians.map(tech => (
                  <option key={tech._id} value={tech._id}>
                    {tech.name}
                  </option>
                ))}
              </select>
            </div>
          )}
        </div>

        {/* Messages */}
        {successMsg && <div className="mb-4 px-4 py-2 bg-green-100 text-green-700 rounded">{successMsg}</div>}
        {error && (
          <div className="mb-4 px-4 py-2 bg-red-100 text-red-700 rounded flex items-center gap-2">
            <AlertCircle className="w-5 h-5" />
            {error}
          </div>
        )}

        {/* Info */}
        <div className="mb-6 text-sm text-slate-600 bg-white rounded-xl p-4 border border-slate-200 flex justify-between items-center">
          <div>
            <span className="font-medium">{filteredTasks.length}</span> task{filteredTasks.length !== 1 ? 's' : ''} found
            {searchTerm && <span> matching "<span className="font-medium">{searchTerm}</span>"</span>}
            {(user.role === 'Engineer' || user.role === 'Admin') && selectedTechnicianId && (
              <span> for <span className="font-medium">{technicians.find(t => t._id === selectedTechnicianId)?.name}</span></span>
            )}
          </div>
          {Math.ceil(filteredTasks.length / itemsPerPage) > 1 && (
            <div className="text-sm text-slate-500">
              Page {currentPage} of {Math.ceil(filteredTasks.length / itemsPerPage)}
            </div>
          )}
        </div>

        {/* Grid */}
        {filteredTasks.length === 0 ? (
          <div className="bg-white rounded-2xl shadow-lg border border-slate-200 p-12 text-center">
            <svg className="mx-auto h-16 w-16 text-slate-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <h3 className="mt-4 text-xl font-semibold text-slate-600">No tasks found</h3>
            <p className="mt-2 text-slate-400">Try adjusting your search or filters.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {currentTasks.map(task => (
              <div
                key={task._id}
                className={`bg-white rounded-2xl shadow-lg border border-slate-200 overflow-hidden hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1
                  ${user.role === 'Technician' && task.assignedTo?._id === user._id ? 'border-blue-500 ring-1 ring-blue-300' : ''}
                `}
              >
                {/* Status */}
                <div className={`flex items-center gap-2 px-4 py-2 ${getStatusColor(task.status)}`}>
                  {getStatusIcon(task.status)}
                  <span className="text-sm font-semibold">{task.status}</span>
                </div>

                <div className="p-5 space-y-3">
                  <h3 className="text-lg font-semibold text-slate-800">{task.title}</h3>
                  <p className="text-sm text-slate-600">{task.description}</p>

                  <div className="text-xs text-slate-400 flex flex-wrap gap-2">
                    <div>Priority: {task.priority || 'N/A'}</div>
                    <div>Type: {task.taskType || 'N/A'}</div>
                  </div>

                  <div className="text-xs text-slate-600">
                    Assigned to: <span className="font-medium">{task.assignedTo?.name || 'Unassigned'}</span>
                  </div>

                  {/* Work details view */}
                  {(task.faultDescription || task.comments || (task.spareParts && task.spareParts.length > 0)) && (
                    <div className="mt-2 p-3 rounded-lg bg-slate-50 border border-slate-200">
                      <div className="flex items-center gap-2 mb-2 text-slate-700 font-medium">
                        <Wrench className="w-4 h-4" /> Work Details
                      </div>
                      {task.faultDescription && (
                        <div className="text-sm text-slate-700">
                          <span className="font-semibold">Fault:</span> {task.faultDescription}
                        </div>
                      )}
                      {task.comments && (
                        <div className="text-sm text-slate-700">
                          <span className="font-semibold">Repair Details:</span> {task.comments}
                        </div>
                      )}
                      {task.spareParts && task.spareParts.length > 0 && (
                        <div className="text-sm text-slate-700">
                          <span className="font-semibold">Spare Parts:</span>{' '}
                          {task.spareParts.map((p, idx) => (
                            <span key={idx} className="mr-2">{p.name}({p.quantity})</span>
                          ))}
                        </div>
                      )}
                    </div>
                  )}

                  {/* Status select + Work button */}
                  <div className="mt-2 flex items-center gap-3">
                    {(user.role === 'Admin' || user.role === 'Engineer' || (user.role === 'Technician' && task.assignedTo?._id === user._id)) && (
                      <select
                        disabled={updatingTaskId === task._id}
                        value={task.status}
                        onChange={(e) => handleStatusChange(task._id, e.target.value)}
                        className="flex-1 py-2 px-3 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent text-slate-700"
                      >
                        <option value="Pending">Pending</option>
                        <option value="In Progress">In Progress</option>
                        <option value="Completed">Completed</option>
                        <option value="Cancelled">Cancelled</option>
                      </select>
                    )}

                    {(user.role === 'Technician' && task.assignedTo?._id === user._id) && (
                      <button
                        onClick={() => openWorkModal(task)}
                        className="px-3 py-2 rounded-lg border border-slate-300 hover:bg-slate-100 text-slate-700"
                      >
                        Add Work
                      </button>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="mt-8 flex justify-center gap-3 text-slate-700">
            <button
              onClick={() => setCurrentPage(p => Math.max(p - 1, 1))}
              disabled={currentPage === 1}
              className="px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100 disabled:opacity-50"
            >
              Prev
            </button>
            {[...Array(totalPages)].map((_, i) => (
              <button
                key={i}
                onClick={() => setCurrentPage(i + 1)}
                className={`px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100 ${currentPage === i + 1 ? 'bg-blue-600 text-white' : ''}`}
              >
                {i + 1}
              </button>
            ))}
            <button
              onClick={() => setCurrentPage(p => Math.min(p + 1, totalPages))}
              disabled={currentPage === totalPages}
              className="px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100 disabled:opacity-50"
            >
              Next
            </button>
          </div>
        )}
      </div>

      {/* Assign Task Modal (scroll-safe) */}
      {showAssignModal && (
        <div className="fixed inset-0 bg-black/30 flex items-center justify-center z-50 overflow-auto">
          <div className="bg-white rounded-xl shadow-xl max-w-lg w-full p-6 relative my-10">
            <h2 className="text-xl font-semibold mb-4">Assign New Task</h2>
            <form onSubmit={handleAssignTask} className="space-y-4 max-h-[80vh] overflow-y-auto pr-2">
              <div>
                <label className="block mb-1 font-medium">Title *</label>
                <input
                  name="title" value={newTaskData.title} onChange={handleNewTaskChange}
                  type="text" required
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div>
                <label className="block mb-1 font-medium">Description</label>
                <textarea
                  name="description" value={newTaskData.description} onChange={handleNewTaskChange} rows={3}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div>
                <label className="block mb-1 font-medium">Task Type *</label>
                <select
                  name="taskType" required value={newTaskData.taskType} onChange={handleNewTaskChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">Select Task Type</option>
                  <option>Repair</option>
                  <option>Install</option>
                  <option>Inspect</option>
                  <option>Assess</option>
                  <option>Calibrate</option>
                </select>
              </div>

              <div>
                <label className="block mb-1 font-medium">Priority</label>
                <select
                  name="priority" value={newTaskData.priority} onChange={handleNewTaskChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option>Low</option>
                  <option>Medium</option>
                  <option>High</option>
                  <option>Urgent</option>
                </select>
              </div>

              <div>
                <label className="block mb-1 font-medium">Assign To (optional)</label>
                <select
                  name="assignedTo" value={newTaskData.assignedTo} onChange={handleNewTaskChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">Unassigned</option>
                  {technicians.map(tech => (
                    <option key={tech._id} value={tech._id}>{tech.name}</option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block mb-1 font-medium">Equipment *</label>
                <select
                  name="equipment" required value={newTaskData.equipment} onChange={handleNewTaskChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">Select Equipment</option>
                  {equipments.map(eq => (
                    <option key={eq._id} value={eq._id}>
                      {eq.name} - {eq.serialNumber || 'No S/N'}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block mb-1 font-medium">Due Date</label>
                <input
                  name="dueDate" type="date" value={newTaskData.dueDate} onChange={handleNewTaskChange}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div className="flex justify-end gap-3 pt-2">
                <button type="button" onClick={() => setShowAssignModal(false)}
                        className="px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100">Cancel</button>
                <button type="submit" className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg">
                  Assign
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Work Details Modal */}
      {workModalOpen && (
        <div className="fixed inset-0 bg-black/30 flex items-center justify-center z-50 overflow-auto">
          <div className="bg-white rounded-xl shadow-xl max-w-lg w-full p-6 relative my-10">
            <h2 className="text-xl font-semibold mb-4">Add Work Details</h2>
            <form onSubmit={submitWorkDetails} className="space-y-4 max-h-[75vh] overflow-y-auto pr-2">
              <div>
                <label className="block mb-1 font-medium">Fault Description</label>
                <textarea
                  rows={3}
                  value={workForm.faultDescription}
                  onChange={(e) => setWorkForm(prev => ({ ...prev, faultDescription: e.target.value }))}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block mb-1 font-medium">Repair Details</label>
                <textarea
                  rows={4}
                  value={workForm.comments}
                  onChange={(e) => setWorkForm(prev => ({ ...prev, comments: e.target.value }))}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block mb-1 font-medium">Spare Parts Used/Required</label>
                <textarea
                  rows={3}
                  placeholder="e.g. Pressure sensor:1; O-ring:2"
                  value={workForm.sparePartsText}
                  onChange={(e) => setWorkForm(prev => ({ ...prev, sparePartsText: e.target.value }))}
                  className="w-full border border-slate-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <p className="text-xs text-slate-500 mt-1">Format: <code>name:qty; name:qty</code></p>
              </div>

              <div className="flex justify-end gap-3 pt-2">
                <button type="button" onClick={closeWorkModal}
                        className="px-4 py-2 rounded-lg border border-slate-300 hover:bg-slate-100">Cancel</button>
                <button type="submit" className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg">
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Tasks;
