// frontend/src/components/equipment/Equipment.js
import React, { useEffect, useMemo, useState } from 'react';
import { Search, Plus, Building2, CheckCircle2 } from 'lucide-react';
import api from '../../utils/api';

const STATUS_COLORS = {
  Serviceable:        { bg: 'bg-green-100', text: 'text-green-800', dot: 'bg-green-500' },
  'Under Maintenance':{ bg: 'bg-blue-100',  text: 'text-blue-800',  dot: 'bg-blue-500' },
  Unserviceable:      { bg: 'bg-amber-100', text: 'text-amber-800', dot: 'bg-amber-500' },
  Auctioned:          { bg: 'bg-purple-100',text: 'text-purple-800',dot: 'bg-purple-500' },
  Decommissioned:     { bg: 'bg-red-100',   text: 'text-red-800',   dot: 'bg-red-500' }
};
const defaultStatus = { bg: 'bg-slate-100', text: 'text-slate-800', dot: 'bg-slate-400' };

function Equipment({ user, setCurrentPage }) {
  const [list, setList] = useState([]);
  const [meta, setMeta] = useState({ departments: [], statuses: [] });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // filters
  const [search, setSearch] = useState('');
  const [department, setDepartment] = useState('');
  const [status, setStatus] = useState('');

  // pagination summary
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const limit = 50;

  useEffect(() => {
    setCurrentPage && setCurrentPage('equipment');
  }, [setCurrentPage]);

  // Load meta for dropdowns
  useEffect(() => {
    let cancelled = false;
    const run = async () => {
      try {
        const res = await api.get('/equipment/meta');
        if (!cancelled && res.data?.success) {
          const { departments = [], statuses = [] } = res.data.meta || {};
          setMeta({ departments, statuses });
        }
      } catch (_) { /* fail silent */ }
    };
    run();
    return () => { cancelled = true; };
  }, []);

  // Load equipment list with filters
  const fetchEquipment = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await api.get('/equipment', {
        params: {
          search: search || undefined,
          department: department || undefined,
          status: status || undefined,
          page,
          limit
        }
      });
      setList(res.data?.equipment || []);
      setTotal(res.data?.total || 0);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load equipment');
      setList([]);
      setTotal(0);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEquipment();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [search, department, status, page]);

  const rangeText = useMemo(() => {
    if (total === 0) return 'Showing 0 of 0 total';
    const start = (page - 1) * limit + 1;
    const end = Math.min(page * limit, total);
    return `Showing ${start}-${end} of ${total} total`;
  }, [total, page]);

  const statusStyles = (s) => STATUS_COLORS[s] || defaultStatus;

  return (
    <div className="min-h-screen bg-slate-50">
      {/* Header */}
      <div className="sticky top-0 z-10 bg-slate-50/80 backdrop-blur border-b border-slate-200">
        <div className="max-w-7xl mx-auto px-6 py-5 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="p-2 rounded-lg bg-gradient-to-r from-blue-600 to-green-500 text-white">
              <CheckCircle2 className="w-5 h-5" />
            </div>
            <div>
              <h1 className="text-2xl font-bold text-slate-800">Servix CMMS</h1>
              <p className="text-sm text-slate-500">Equipment Inventory</p>
            </div>
          </div>

          {user?.role === 'Engineer' && (
            <button
              onClick={() => (window.location.href = '/add-equipment')}
              className="inline-flex items-center gap-2 px-5 py-3 rounded-xl bg-gradient-to-r from-green-500 to-blue-600 text-white hover:opacity-95"
            >
              <Plus className="w-5 h-5" />
              Add Equipment
            </button>
          )}
        </div>
      </div>

      {/* Search + Filters */}
      <div className="max-w-7xl mx-auto px-6 py-4">
        <div className="flex flex-col md:flex-row gap-3">
          {/* Search */}
          <div className="relative flex-1">
            <div className="absolute inset-y-0 left-3 flex items-center pointer-events-none">
              <Search className="w-5 h-5 text-slate-400" />
            </div>
            <input
              type="text"
              value={search}
              onChange={(e) => { setPage(1); setSearch(e.target.value); }}
              placeholder="Search by name, serial, model, manufacturer..."
              className="w-full pl-10 pr-4 py-3 rounded-xl border border-slate-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* Department filter */}
          <div className="relative w-full md:w-64">
            <div className="absolute inset-y-0 left-3 flex items-center pointer-events-none">
              <Building2 className="w-5 h-5 text-slate-400" />
            </div>
            <select
              value={department}
              onChange={(e) => { setPage(1); setDepartment(e.target.value); }}
              className="w-full pl-10 pr-8 py-3 rounded-xl border border-slate-300 focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white"
            >
              <option value="">All Departments</option>
              {meta.departments.map((d) => (
                <option key={d} value={d}>{d}</option>
              ))}
            </select>
          </div>

          {/* Status filter */}
          <div className="relative w-full md:w-64">
            <div className="absolute inset-y-0 left-3 flex items-center pointer-events-none">
              <span className="inline-block w-5 h-5 rounded-full bg-slate-300" />
            </div>
            <select
              value={status}
              onChange={(e) => { setPage(1); setStatus(e.target.value); }}
              className="w-full pl-10 pr-8 py-3 rounded-xl border border-slate-300 focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white"
            >
              <option value="">All Statuses</option>
              {meta.statuses.map((s) => (
                <option key={s} value={s}>{s}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Summary */}
        <div className="mt-4 flex items-center justify-between text-slate-600 text-sm">
          <div><span className="font-medium">{total}</span> equipments found</div>
          <div>{rangeText}</div>
        </div>
      </div>

      {/* Content */}
      <div className="max-w-7xl mx-auto px-6 pb-10">
        {loading && (
          <div className="bg-white border border-slate-200 rounded-2xl p-12 text-center">
            <div className="inline-block h-10 w-10 animate-spin rounded-full border-4 border-solid border-blue-600 border-r-transparent"></div>
            <p className="mt-4 text-slate-600">Loading equipment...</p>
          </div>
        )}
        {error && !loading && (
          <div className="bg-white border border-red-200 text-red-700 rounded-xl p-4">{error}</div>
        )}

        {!loading && !error && (
          <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
            {list.map((eq) => {
              const s = statusStyles(eq.status);
              const manufacturer = eq.manufacturer || eq.manufacturerName || '—';
              const dept = eq.department || '—';
              const loc = eq.location || '—';
              const cat = eq.category || '—';
              const installed =
                eq.installedDate || eq.installationDate
                  ? new Date(eq.installedDate || eq.installationDate).toLocaleDateString()
                  : '';

              return (
                <div key={eq._id} className="bg-white rounded-2xl border border-slate-200 shadow-sm overflow-hidden">
                  {/* Header */}
                  <div className="flex items-center justify-between px-5 pt-5">
                    <h3 className="text-xl font-semibold text-slate-800 truncate">
                      {eq.name || 'Unnamed Equipment'}
                    </h3>
                    <span className={`inline-flex items-center gap-2 px-3 py-1 rounded-full text-xs font-medium ${s.bg} ${s.text}`}>
                      <span className={`w-2.5 h-2.5 rounded-full ${s.dot}`} />
                      {eq.status || 'Unknown'}
                    </span>
                  </div>

                  {/* Body */}
                  <div className="px-5 pb-5 mt-3">
                    <div className="grid grid-cols-2 gap-y-2 text-sm">
                      <div className="text-slate-500">Serial Number:</div>
                      <div className="text-slate-800 font-medium break-all">{eq.serialNumber || '—'}</div>

                      <div className="text-slate-500">Model:</div>
                      <div className="text-slate-800 font-medium">{eq.model || '—'}</div>

                      <div className="text-slate-500">Manufacturer:</div>
                      <div className="text-slate-800 font-medium">{manufacturer}</div>

                      <div className="text-slate-500">Department:</div>
                      <div className="text-slate-800 font-medium">{dept}</div>

                      <div className="text-slate-500">Location:</div>
                      <div className="text-slate-800 font-medium">{loc}</div>

                      <div className="text-slate-500">Category:</div>
                      <div className="text-slate-800 font-medium">{cat}</div>
                    </div>

                    {installed && (
                      <div className="mt-4 text-xs text-slate-500">
                        Installed: <span className="text-slate-700 font-medium">{installed}</span>
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        )}

        {!loading && !error && list.length === 0 && (
          <div className="bg-white border border-slate-200 rounded-2xl p-12 text-center text-slate-500">
            No equipment found. Try adjusting filters.
          </div>
        )}
      </div>
    </div>
  );
}

export default Equipment;
