// frontend/src/components/layout/Sidebar.js
import React from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faBox, faWrench, faTasks, faUser } from '@fortawesome/free-solid-svg-icons';

const Sidebar = ({ currentPage }) => {
  return (
    <div className="bg-gray-800 text-white p-4">
      <nav>
        <ul>
          <li>
            <Link
              to="/dashboard"
              className={`block py-2 px-4 hover:bg-gray-700 ${
                currentPage === 'dashboard' ? 'bg-gray-700' : ''
              }`}
            >
              <FontAwesomeIcon icon={faHome} /> Dashboard
            </Link>
          </li>
          <li>
            <Link
              to="/equipment"
              className={`block py-2 px-4 hover:bg-gray-700 ${
                currentPage === 'inventory' ? 'bg-gray-700' : ''
              }`}
            >
              <FontAwesomeIcon icon={faBox} /> Inventory
            </Link>
          </li>
          <li>
            <Link
              to="/maintenance"
              className={`block py-2 px-4 hover:bg-gray-700 ${
                currentPage === 'maintenance' ? 'bg-gray-700' : ''
              }`}
            >
              <FontAwesomeIcon icon={faWrench} /> Maintenance
            </Link>
          </li>
          <li>
            <Link
              to="/tasks"
              className={`block py-2 px-4 hover:bg-gray-700 ${
                currentPage === 'tasks' ? 'bg-gray-700' : ''
              }`}
            >
              <FontAwesomeIcon icon={faTasks} /> Tasks
            </Link>
          </li>
          <li>
            <Link
              to="/users"
              className={`block py-2 px-4 hover:bg-gray-700 ${
                currentPage === 'users' ? 'bg-gray-700' : ''
              }`}
            >
              <FontAwesomeIcon icon={faUser} /> Users
            </Link>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default Sidebar;
