// frontend/src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import axios from 'axios';

// Import components
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import Dashboard from './components/dashboard/Dashboard';
import Equipment from './components/equipment/Equipment';
import Maintenance from './components/maintenance/Maintenance';
import SchedulePreventive from './components/maintenance/SchedulePreventive';
import ReportIssue from './components/maintenance/ReportIssue';
import Tasks from './components/tasks/Tasks';
import Users from './components/users/Users';
import AddEquipment from './components/equipment/AddEquipment';
import AddTask from './components/tasks/AddTask';
import AddUser from './components/users/AddUser';
import Navbar from './components/layout/Navbar';
import Sidebar from './components/layout/Sidebar';
import PrivateRoute from './components/routing/PrivateRoute';

// Axios defaults
axios.defaults.baseURL = 'http://localhost:5000/api';
axios.defaults.withCredentials = true;

// Request interceptor
axios.interceptors.request.use(
  request => {
    console.log('Request URL:', request.url);
    const token = localStorage.getItem('token');
    if (token) {
      request.headers['Authorization'] = `Bearer ${token}`;
    }
    return request;
  },
  error => Promise.reject(error)
);

// Response interceptor
axios.interceptors.response.use(
  response => {
    console.log('Response:', response);
    return response;
  },
  error => {
    console.error('Axios Error:', error);
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Main App component
function App() {
  const [currentPage, setCurrentPage] = useState('dashboard');

  return (
    <AuthProvider>
      <Router>
        <div className="flex h-screen bg-gray-50">
          <AppContent currentPage={currentPage} setCurrentPage={setCurrentPage} />
        </div>
      </Router>
    </AuthProvider>
  );
}

// AppContent — uses AuthContext
function AppContent({ currentPage, setCurrentPage }) {
  const { user, isAuthenticated, loading, logout } = useAuth();

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="text-center">
          <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-teal-600 border-r-transparent"></div>
          <p className="mt-4 text-gray-600">Loading application...</p>
        </div>
      </div>
    );
  }

  return (
    <>
      {user && <Sidebar currentPage={currentPage} />}
      <div className={user ? "flex-1 flex flex-col overflow-hidden" : "w-full"}>
        {user && <Navbar user={user} logout={logout} />}
        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50">
          <div className="container mx-auto px-6 py-8">
            <Routes>
              {/* Public routes */}
              <Route 
                path="/login" 
                element={!isAuthenticated ? <Login /> : <Navigate to="/dashboard" replace />} 
              />
              <Route 
                path="/register" 
                element={!isAuthenticated ? <Register /> : <Navigate to="/dashboard" replace />} 
              />

              {/* Default route */}
              <Route 
                path="/" 
                element={isAuthenticated ? <Navigate to="/dashboard" replace /> : <Navigate to="/login" replace />} 
              />

              {/* Protected routes */}
              <Route 
                path="/dashboard" 
                element={
                  <PrivateRoute user={user}>
                    <Dashboard user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                } 
              />
              <Route 
                path="/equipment" 
                element={
                  <PrivateRoute user={user} roles={['admin', 'engineer']}>
                    <Equipment user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                } 
              />
              <Route 
                path="/add-equipment" 
                element={
                  <PrivateRoute user={user} roles={['admin', 'engineer']}>
                    <AddEquipment user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                } 
              />
              <Route 
                path="/maintenance" 
                element={
                  <PrivateRoute user={user} roles={['admin', 'engineer']}>
                    <Maintenance user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                } 
              />
              <Route 
                path="/schedule-preventive" 
                element={
                  <PrivateRoute user={user} roles={['admin', 'engineer']}>
                    <SchedulePreventive user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                } 
              />
              <Route 
                path="/report-issue" 
                element={
                  <PrivateRoute user={user}>
                    <ReportIssue user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                } 
              />
              <Route 
                path="/tasks" 
                element={
                  <PrivateRoute user={user}>
                    <Tasks user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                } 
              />
              <Route 
                path="/add-task" 
                element={
                  <PrivateRoute user={user} roles={['admin', 'engineer']}>
                    <AddTask user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                } 
              />
              <Route 
                path="/users" 
                element={
                  <PrivateRoute user={user} roles={['admin', 'engineer']}>
                    <Users user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                } 
              />
              <Route 
                path="/add-user" 
                element={
                  <PrivateRoute user={user} roles={['admin', 'engineer']}>
                    <AddUser user={user} setCurrentPage={setCurrentPage} />
                  </PrivateRoute>
                } 
              />

              {/* Fallback */}
              <Route 
                path="*" 
                element={<Navigate to={isAuthenticated ? "/dashboard" : "/login"} replace />} 
              />
            </Routes>
          </div>
        </main>
      </div>
    </>
  );
}

export default App;
